create table INSURANCE_COMPANY_TYPE_DTL
(
   ICTD_ID			NUMERIC(31)	not null,
   INS_CMPY_TYP_ID		NUMERIC(31)	not null,
   ICTD_NAICS_ID    		VARCHAR(15),
   ICTD_NCCI_ID    		VARCHAR(15),
   ICTD_TAX_ID    		VARCHAR(15),
   
   ICTD_CNTC_NM			VARCHAR(120),
   ICTD_CNTC_PHN_NO		VARCHAR(15),
   ICTD_CNTC_PHN_CNTRY_CD  VARCHAR(15),
   ICTD_CNTC_PHN_DIAL_CD   VARCHAR(15),
   ICTD_CNTC_PHN_EXT		VARCHAR(15),
   ICTD_CNTC_FAX_NO    		VARCHAR(15),
   ICTD_CNTC_FAX_CNTRY_CD   VARCHAR(15),
   ICTD_CNTC_FAX_DIAL_CD   VARCHAR(15),
   ICTD_CNTC_EMAIL_ADDR		VARCHAR(80), 		
   ICTD_URL			VARCHAR(80),	
   
   ICTD_PHY_STR1		VARCHAR(40),            
   ICTD_PHY_STR2		VARCHAR(40),
   ICTD_PHY_CITY		VARCHAR(80),
   STT_ID_PHY			NUMERIC(31)	not null,
   ICTD_PHY_POST_CD		VARCHAR(15),
   
   ICTD_PHS_STR1		VARCHAR(40),            
   ICTD_PHS_STR2		VARCHAR(40),
   ICTD_PHS_CITY		VARCHAR(80),
   STT_ID_PHS			NUMERIC(31) not null,
   ICTD_PHS_POST_CD		VARCHAR(15),
   ICTD_PHS_CALL_CNTR_PHN_NO	VARCHAR(15),
   ICTD_PHS_CERT_CNTR_PHN_NO    VARCHAR(15),
   
   ICTD_BILL_STR1 		VARCHAR(40),            
   ICTD_BILL_STR2 		VARCHAR(40),
   ICTD_BILL_CITY		VARCHAR(80),
   STT_ID_BILL			NUMERIC(31)	not null,
   ICTD_BILL_POST_CD		VARCHAR(15),
   ICTD_BILL_PHN_NO		VARCHAR(15),
   ICTD_BILL_FAX_NO		VARCHAR(15),	
   ICTD_BILL_EMAIL_ADDR		VARCHAR(80),	

   ICTD_CLM_STR1 		VARCHAR(40),            
   ICTD_CLM_STR2 		VARCHAR(40),
   ICTD_CLM_CITY		VARCHAR(80),
   STT_ID_CLM		NUMERIC(31)	not null,
   ICTD_CLM_POST_CD		VARCHAR(15),
   ICTD_CLM_PHN_NO		VARCHAR(15),
   ICTD_CLM_FAX_NO		VARCHAR(15),
   ICTD_CLM_EMAIL_ADDR		VARCHAR(80),
   ICTD_INS_CAR_WEB_SITE	VARCHAR(80),
   ICTD_CMS_CALL_CNTR_PHN_NO	VARCHAR(25),
   
   ICTD_SML_LOGO_IMG_NM   	VARCHAR(80),
   ICTD_LG_LOGO_IMG_NM   	VARCHAR(80),
   ICTD_CEO_NM			VARCHAR(80),
   ICTD_CEO_SNG_IMG_NM	 	VARCHAR(80),
   ICTD_UNDRWT_EXEC_NM	 	VARCHAR(80),
   ICTD_UNDRWT_EXEC_SNG_IMG_NM  VARCHAR(80),
   ICTD_CLM_EXEC_NM           	VARCHAR(80),
   ICTD_CLM_EXEC_IMG_NM	  	VARCHAR(80),
   ICTD_ASWG_UNT_SBMN_CD	VARCHAR(15),
   ICTD_DFLT_IND		CHAR(1),
   ICTD_CRA_ACCT_NO             VARCHAR(30),
   ICTD_CRA_TRNSMT_NO           VARCHAR(30),
   ICTD_ISO_CMPY_NO	        VARCHAR(15), 
   ICTD_ISO_TRNSMT_GRP	        VARCHAR(15), 
   ICTD_ISO_CLM_ADMN	        VARCHAR(15), 
   ICTD_ISO_CLM_SRCH_CMPY       VARCHAR(15), 
   ICTD_NCCI_GRP	        VARCHAR(15),
   ICTD_MDCR_RESP_RPT_ENTY       VARCHAR(25) , 
   ICTD_MDCR_SITE_ID_NO          VARCHAR(25) ,
   ICTD_WEB_DFLT_IND                CHAR(1),
   ICTD_VRF_ACCT_NO 		VARCHAR(15), 
   CMS_GRP_CD               VARCHAR(30),
   constraint XPK_INSCTD primary key (ICTD_ID)
);


COMMENT ON TABLE INSURANCE_COMPANY_TYPE_DTL IS 'This is a code table specifying contact information for insurance companies (I.e. carriers) in the system.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.INS_CMPY_TYP_ID IS 'This is a foreign key to the insurance_company_type table specifying the insurance company that this contact information is for.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_NAICS_ID IS 'This is the identifier provided by NAICS to the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_NCCI_ID IS 'This is the identifier provided by NCCI to the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_TAX_ID IS 'This is the Tax Identifier Number of the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CNTC_NM IS 'This is the primary contact name for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CNTC_PHN_NO IS 'This is the primary contact phone number for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CNTC_PHN_EXT IS 'This is the extenstion for the primary contact phone number for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CNTC_FAX_NO IS 'This is the primary contact fax number for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_PHY_STR1 IS 'This is line one of the physical address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_PHY_STR2 IS 'This is line two of the physical address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_PHY_CITY IS 'This is the city of the physical address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.STT_ID_PHY IS 'This is a foreign key to the state table specifying the state for the physical address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_PHY_POST_CD IS 'This is a foreign key to the postal_code table specifying the zip code for the physical address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_PHS_CALL_CNTR_PHN_NO IS 'This is the policy call center phone number for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_PHS_CERT_CNTR_PHN_NO IS 'This is the policy certificate center phone number for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_BILL_STR1 IS 'This is line one of the billing address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_BILL_STR2 IS 'This is line two of the billing address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_BILL_CITY IS 'This is the city of the billing address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.STT_ID_BILL IS 'This is a foreign key to the state table specifying the state for the billing address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_BILL_POST_CD IS 'This is a foreign key to the postal_code table specifying the zip code for the billing address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_BILL_PHN_NO IS 'This is the billing phone number for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CLM_STR1 IS 'This is line one of the claim address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CLM_STR2 IS 'This is line two of the claim address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CLM_CITY IS 'This is the city of the claim address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.STT_ID_CLM IS 'This is a foreign key to the state table specifying the state for the claim address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CLM_POST_CD IS 'This is a foreign key to the postal_code table specifying the zip code for the claim address for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CLM_PHN_NO IS 'This is the claim phone number for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CLM_FAX_NO IS 'This is the claim fax number for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CMS_CALL_CNTR_PHN_NO IS 'This is the claim call center phone number for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_MDCR_SITE_ID_NO IS 'Medicare Site ID';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_MDCR_RESP_RPT_ENTY IS 'Medicare Responsible Reporting Entity';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_VRF_ACCT_NO IS 'SSA Verification Account Number for the default insurance company';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.CMS_GRP_CD IS 'This is a foreign key to CMS Group Code Table specifying the group code associated with each insurance company. ';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CNTC_PHN_CNTRY_CD IS 'This is the primary contact phone country code for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CNTC_PHN_DIAL_CD IS 'This is the primary contact phone dial code for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CNTC_FAX_CNTRY_CD IS 'This is the primary contact fax country code for the insurance company.';
COMMENT ON COLUMN INSURANCE_COMPANY_TYPE_DTL.ICTD_CNTC_FAX_DIAL_CD IS 'This is the primary contact fax dial code for the insurance company.';

create table AGENCY_TYPE
(
   AGY_TYP_CD           VARCHAR(15)            not null,
   AGY_TYP_NM           VARCHAR(80)            not null,
   AGY_TYP_VOID_IND     CHAR(1)                not null default 'n',
   constraint XPK_AGYT primary key (AGY_TYP_CD)
);


COMMENT ON TABLE AGENCY_TYPE IS 'Code table containing all possible agency types (Independent, Farm Bureau, Allstate, etc?).';
COMMENT ON COLUMN AGENCY_TYPE.AGY_TYP_CD IS 'Internal agency type code.';
COMMENT ON COLUMN AGENCY_TYPE.AGY_TYP_NM IS 'External agency type description.';
COMMENT ON COLUMN AGENCY_TYPE.AGY_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table AGREEMENT
(
   AGRE_ID              NUMERIC(31)            not null,
   AGRE_TYP_CD          VARCHAR(15),
   AGRE_BTCH_JOB_FAIL_IND CHAR(1)                not null default 'n',
   constraint XPK_AGRE primary key (AGRE_ID)
);


COMMENT ON TABLE AGREEMENT IS 'Root table for the agreement structure.  Agreements are generally between the carrier and one-to-many customers (handled via participation).  Some common agreements are claims, policy, quote, and contract.';
COMMENT ON COLUMN AGREEMENT.AGRE_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN AGREEMENT.AGRE_TYP_CD IS 'Internal agreement type code from AGREEMENT_TYPE.  Identifies the type of agreement (claim, policy, quote, etc?).';
COMMENT ON COLUMN AGREEMENT.AGRE_BTCH_JOB_FAIL_IND IS 'Indicates a critical batch process failure while processing an agreement. Subsequent dependent batches of failed one can be instructed to not process agreements where a failure is indicated, until the problem has been correct and the indicator reset.';


create table AGREEMENT_LOB_JUR_XREF
(
   AGRE_LOB_JUR_XREF_ID NUMERIC(31)            not null,
   AGRE_TYP_CD          VARCHAR(15),
   LOB_TYP_CD           VARCHAR(15),
   JUR_TYP_CD           VARCHAR(15),
   AGRE_LOB_JUR_XREF_VOID_IND CHAR(1)                default 'n',
   constraint XPK_ALJX primary key (AGRE_LOB_JUR_XREF_ID)
);


COMMENT ON TABLE AGREEMENT_LOB_JUR_XREF IS 'Cross-reference table that specifies what jurisdications are valid for which agreement type / line of business (LOB) combinations.';
COMMENT ON COLUMN AGREEMENT_LOB_JUR_XREF.AGRE_LOB_JUR_XREF_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN AGREEMENT_LOB_JUR_XREF.AGRE_TYP_CD IS 'Internal agreement type code from AGREEMENT_TYPE.  The agreement type (claim, policy, quote, etc?) for which the jurisdiction is valid for the associated line of business.';
COMMENT ON COLUMN AGREEMENT_LOB_JUR_XREF.LOB_TYP_CD IS 'Internal line of business code from LINE_OF_BUSINESS_TYPE.  The line of business (Workers Compensation, Commercial Auto, etc?) for which the jurisdiction is valid for the associated agreement type.';
COMMENT ON COLUMN AGREEMENT_LOB_JUR_XREF.JUR_TYP_CD IS 'Internal jurisdiction code from JURISDICTION_TYPE.  The jurisdiction valid for the agreement type / line of business combination.';
COMMENT ON COLUMN AGREEMENT_LOB_JUR_XREF.AGRE_LOB_JUR_XREF_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table AGREEMENT_TYPE
(
   AGRE_TYP_CD          VARCHAR(15)            not null,
   AGRE_TYP_NM          VARCHAR(80)            not null,
   AGRE_TYP_VOID_IND    CHAR(1)                not null default 'n',
   constraint XPK_AGRET primary key (AGRE_TYP_CD)
);


COMMENT ON TABLE AGREEMENT_TYPE IS 'Code table containing all possible agreement types (Claim, Policy, Quote, etc?).';
COMMENT ON COLUMN AGREEMENT_TYPE.AGRE_TYP_CD IS 'Internal agreement type code.';
COMMENT ON COLUMN AGREEMENT_TYPE.AGRE_TYP_NM IS 'External agreement type description.';
COMMENT ON COLUMN AGREEMENT_TYPE.AGRE_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table APPLICATION_CONTEXT_TYPE
(
   APP_CNTX_TYP_CD      VARCHAR(15)            not null,
   APP_CNTX_TYP_NM      VARCHAR(80) NOT NULL,
   APP_CNTX_TYP_EXT_IND	CHAR(1)		not null, 
   APP_CNTX_TYP_VOID_IND CHAR(1)                not null default 'n',
   constraint XPK_APPC primary key (APP_CNTX_TYP_CD)
);


COMMENT ON TABLE APPLICATION_CONTEXT_TYPE IS 'This table defines all the main application context defines in the power comp system.';
COMMENT ON COLUMN APPLICATION_CONTEXT_TYPE.APP_CNTX_TYP_CD IS 'A unique code value to identify the specific type of application context.';
COMMENT ON COLUMN APPLICATION_CONTEXT_TYPE.APP_CNTX_TYP_NM IS 'A user friendly name to describe the application context type.';
COMMENT ON COLUMN APPLICATION_CONTEXT_TYPE.APP_CNTX_TYP_EXT_IND IS 'Column to distinguish between context types that are internal and the ones that are externally available to create folders etc.';
COMMENT ON COLUMN APPLICATION_CONTEXT_TYPE.APP_CNTX_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table APPLICATION_TYPE
(
   APP_TYP_CD           VARCHAR(15)            not null,
   APP_TYP_NM           VARCHAR(40) NOT NULL,
   APP_TYP_INSTL_IND    CHAR(1)                not null default 'n',
   APP_TYP_VOID_IND     CHAR(1)                not null default 'n',
   constraint XPK_APPT primary key (APP_TYP_CD)
);


COMMENT ON TABLE APPLICATION_TYPE IS 'This table defines the main application areas defines in the power comp system.';
COMMENT ON COLUMN APPLICATION_TYPE.APP_TYP_CD IS 'A unique code value to identify the specific type of application.';
COMMENT ON COLUMN APPLICATION_TYPE.APP_TYP_NM IS 'A user friendly name to describe the application type.';
COMMENT ON COLUMN APPLICATION_TYPE.APP_TYP_INSTL_IND IS 'Indicator that specifies whether this application type is installed.';
COMMENT ON COLUMN APPLICATION_TYPE.APP_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table BILLING_SCHEDULE_DETAIL
(
   BILL_SCH_DTL_ID      NUMERIC(31)            not null,
   PLCY_PRD_BILL_SCH_ID NUMERIC(31),
   BILL_SCH_DTL_NO      NUMERIC(31),
   BILL_SCH_DTL_BILL_DT TIMESTAMP,
   BILL_SCH_DTL_DRV_AMT NUMERIC(15,2),
   BILL_SCH_DTL_DRV_ADJ_AMT NUMERIC(15,2),
   BILL_SCH_DTL_ADV_BTCH_IND CHAR(1)  not null default 'n',
   AUDIT_USER_ID_CREA   NUMERIC(31),
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   VOID_IND CHAR(1)                not null default 'n',
   constraint XPK_BSD primary key (BILL_SCH_DTL_ID)
);


COMMENT ON TABLE BILLING_SCHEDULE_DETAIL IS 'This data table captures installment level details about a policys billing schedule.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.BILL_SCH_DTL_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.PLCY_PRD_BILL_SCH_ID IS 'This is a foreign key to the policy_period_billing_schedule table to tie the details of this record back to a specific policy.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.BILL_SCH_DTL_NO IS 'A sequential number to identify which installment number is being represented by this record.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.BILL_SCH_DTL_BILL_DT IS 'The bill date of this installment (I.e. the date that this installment was put onto the policys accounts receivable).';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.BILL_SCH_DTL_DRV_AMT IS 'The original amount of this installment.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.BILL_SCH_DTL_DRV_ADJ_AMT IS 'The sum of any amounts added to this installment after it was originally created.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.BILL_SCH_DTL_ADV_BTCH_IND IS 'An indicator identifying whether the installment is billed ahead of the scheduled bill date by the advance billing batch.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table BILLING_SCHEDULE_DETAIL_AMOUNT
(
   BILL_SCH_DTL_AMT_ID  NUMERIC(31)         not null,
   BILL_SCH_DTL_ID      NUMERIC(31),
   BILL_SCH_DTL_AMT_NO  NUMERIC(31),
   SCH_AMT_TYP_CD       VARCHAR(15),
   BILL_SCH_DTL_AMT_AMT NUMERIC(15,2),
   AUDIT_USER_ID_CREA   NUMERIC(31),
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   VOID_IND             CHAR(1)             not null default 'n',
   constraint XPK_BSDA primary key (BILL_SCH_DTL_AMT_ID)
);


COMMENT ON TABLE BILLING_SCHEDULE_DETAIL_AMOUNT IS 'This data table captures line item level details about a policys billing schedule.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL_AMOUNT.BILL_SCH_DTL_AMT_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL_AMOUNT.BILL_SCH_DTL_ID IS 'This is a foreign key to the billing_schedule_detail table to represent which installment these line item records are for.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL_AMOUNT.BILL_SCH_DTL_AMT_NO IS 'A sequential number to identify which line item is being represented by this record.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL_AMOUNT.SCH_AMT_TYP_CD IS 'The type of line item (I.e. premium, assessment, tax, etc..) which is being represented by this line item record.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL_AMOUNT.BILL_SCH_DTL_AMT_AMT IS 'The amount of this line item.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL_AMOUNT.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL_AMOUNT.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL_AMOUNT.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL_AMOUNT.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN BILLING_SCHEDULE_DETAIL_AMOUNT.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';



create table BUSINESS
(
   CUST_ID              NUMERIC(31)			not null,
   BUSN_YR_EST_DTM      NUMERIC(31),
   BUSN_CS_OPER_DTM     TIMESTAMP,
   constraint XPK_BUSN primary key (CUST_ID)
);


COMMENT ON TABLE BUSINESS IS 'Data table used to store business-specific customer information.';
COMMENT ON COLUMN BUSINESS.CUST_ID IS 'This is a foreign key to the customer table specifying which customer record this business record pertains to.';
COMMENT ON COLUMN BUSINESS.BUSN_YR_EST_DTM IS 'Year the business was established.';
COMMENT ON COLUMN BUSINESS.BUSN_CS_OPER_DTM IS 'Year the business ceased operations.';


create table BUSINESS_DATE
(
   BUSN_DT_ID           NUMERIC(31)            not null,
   BUSN_DT_DT           TIMESTAMP,
   constraint XPK_BD primary key (BUSN_DT_ID)
);


COMMENT ON TABLE BUSINESS_DATE IS 'Data table to store the current business date to be used in batch processing.';
COMMENT ON COLUMN BUSINESS_DATE.BUSN_DT_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN BUSINESS_DATE.BUSN_DT_DT IS 'Current business date which remains the same until all batch processing is complete.';

create table CAUSE_OF_LOSS_CATEGORY_TYPE
(
   CAUS_OF_LOSS_CTG_TYP_CD VARCHAR(15)            not null,
   CAUS_OF_LOSS_CTG_TYP_NM VARCHAR(80)            not null,
   VOID_IND                CHAR(1)                not null default 'n',
   constraint XPK_COLCT primary key (CAUS_OF_LOSS_CTG_TYP_CD)
);


COMMENT ON TABLE CAUSE_OF_LOSS_CATEGORY_TYPE IS 'This identifies the categories that can be associated to cause of loss codes.  Cause of loss codes will be mapped to these categories, and both are used in the on-line application in the notice of occurrence and claim maintenance areas.';
COMMENT ON COLUMN CAUSE_OF_LOSS_CATEGORY_TYPE.CAUS_OF_LOSS_CTG_TYP_CD IS 'Unique identifier of the cause of loss category.';
COMMENT ON COLUMN CAUSE_OF_LOSS_CATEGORY_TYPE.CAUS_OF_LOSS_CTG_TYP_NM IS 'A description of the cause of loss category.';
COMMENT ON COLUMN CAUSE_OF_LOSS_CATEGORY_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table CAUSE_OF_LOSS_TYPE
(
   CAUS_OF_LOSS_TYP_CD  VARCHAR(15)            not null,
   CAUS_OF_LOSS_CTG_TYP_CD VARCHAR(15),
   CAUS_OF_LOSS_TYP_NM  VARCHAR(80)            not null,
   CAUS_OF_LOSS_TYP_ACP_ELIG_IND CHAR(1) NOT NULL DEFAULT 'n',
   CAUS_OF_LOSS_TYP_PTNT_SRGT_IND CHAR(1) NOT NULL DEFAULT 'n',
   VOID_IND             CHAR(1)                not null default 'n',
   constraint XPK_COLT primary key (CAUS_OF_LOSS_TYP_CD)
);


COMMENT ON TABLE CAUSE_OF_LOSS_TYPE IS 'This table defines all of cause of loss (COL) codes and their categories that can be used in the on-line application in the notice of occurrence and claim maintenance areas. The application will filter the COL Codes based on the category selected.';
COMMENT ON COLUMN CAUSE_OF_LOSS_TYPE.CAUS_OF_LOSS_TYP_CD IS 'Unique identifier representing the cause of loss.';
COMMENT ON COLUMN CAUSE_OF_LOSS_TYPE.CAUS_OF_LOSS_CTG_TYP_CD IS 'The cause of loss category that the cause of loss description is mapped to.';
COMMENT ON COLUMN CAUSE_OF_LOSS_TYPE.CAUS_OF_LOSS_TYP_NM IS 'Narrative description of the cause of loss.';
COMMENT ON COLUMN CAUSE_OF_LOSS_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table CHECK_PREFERENCE_TYPE
(
   CHCK_PREF_TYP_CD     VARCHAR(15)            not null,
   CHCK_PREF_TYP_NM     VARCHAR(80)            not null,
   CHCK_PREF_TYP_VOID_IND CHAR(1)                not null default 'n',
   constraint XPK_CHCKPT primary key (CHCK_PREF_TYP_CD)
);


COMMENT ON TABLE CHECK_PREFERENCE_TYPE IS 'Obsolete.';
COMMENT ON COLUMN CHECK_PREFERENCE_TYPE.CHCK_PREF_TYP_CD IS 'Obsolete.';
COMMENT ON COLUMN CHECK_PREFERENCE_TYPE.CHCK_PREF_TYP_NM IS 'Obsolete.';
COMMENT ON COLUMN CHECK_PREFERENCE_TYPE.CHCK_PREF_TYP_VOID_IND IS 'Obsolete.';


