COMMENT ON COLUMN CLAIM_STATUS_TYPE.CLM_STS_TYP_CD IS 'A unique code value to identify the specific claim status type record.';
COMMENT ON COLUMN CLAIM_STATUS_TYPE.CLM_STS_TYP_NM IS 'A user-friendly name to describe claim status type.';
COMMENT ON COLUMN CLAIM_STATUS_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table CLAIM_STS_STT_ACTN_TRANS_XREF
(
   CSSATX_ID            NUMERIC(31)            not null,
   CLM_STS_CLM_STT_XREF_ID_FR NUMERIC(31),
