   CSSATX_SYS_IND       CHAR(1)                default 'n',
   constraint XPK_CSSATX primary key (CSSATX_ID)
);


COMMENT ON TABLE CLAIM_STS_STT_ACTN_TRANS_XREF IS 'This code table contains the change claim status actions that are allowed for claims.  Each value defines the movement from and to claim state and status combinations that are allowed for associated claim types.';
COMMENT ON COLUMN CLAIM_STS_STT_ACTN_TRANS_XREF.CSSATX_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN CLAIM_STS_STT_ACTN_TRANS_XREF.CLM_STS_CLM_STT_XREF_ID_FR IS 'This is a foreign key from claim_status_claim_state_exref defining the transition "FROM" claim status claim status combinations.';
COMMENT ON COLUMN CLAIM_STS_STT_ACTN_TRANS_XREF.CLM_STS_CLM_STT_XREF_ID_TO IS 'This is a foreign key from claim_status_claim_state_exref defining the transition "TO" claim status claim status combinations.';
COMMENT ON COLUMN CLAIM_STS_STT_ACTN_TRANS_XREF.CLM_STS_STT_ACTN_TYP_CD IS 'This is a foreign key from claim_status_state_action_type defining the claim status state action type for the claim status claim state transition.';
COMMENT ON COLUMN CLAIM_STS_STT_ACTN_TRANS_XREF.CSSATX_SYS_IND IS 'This indicator defined whether or not the claim status action is editable on-line.';


create table CLAIM_TRANSITION_REASON_TYPE
