COMMENT ON TABLE CLAIM_WAGE_SOURCE_DETAIL IS 'This data table contains the details for the claimants wages source.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_SRC_DTL_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_SRC_ID IS 'This is a foreign key from the claim wage source table defining the claim wage source record associated to this detail record.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_RPT_SRC_TYP_CD IS 'This is a foreign key from the claim wage report source table defining  method the claimants wage source is received.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_TYP_CD IS 'This is a foreign key from the claim_wage_type table  defining type of wages for this claim wage source detail.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_PAY_PRD_TYP_CD IS 'This is a foreign key from the claim_pay_period_type table defining the claimants wage source payment period frequency.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_SRC_DTL_AMT IS 'This is the amount the claimant was paid for a period of time.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_SRC_DTL_AW_AMT IS 'This is the claim wage source average wage amount.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_SRC_DTL_EFF_DT IS 'This date represents the date the claim wage source detail record is in effect.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_SRC_DTL_END_DT IS 'This date represents the date the claim wage source detail record is no longer in effect.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_SRC_DTL_PAY_PRD_UNT IS 'This field represents how many of the Pay Period selected are represented by the wages entered.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_SRC_DTL_HR_WK_NO IS 'This field represents the number of hours the claimant works in a week.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_SRC_DTL_DD_WK_NO IS 'This field represents the number of days the claimant works in a week.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_SRC_DTL_INSRD_DD_WK_NO IS 'This field represents the number of days the insured works in a week.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.CLM_WG_SRC_DTL_COMT IS 'This field stores the users comment on this claim wage source detail record.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN CLAIM_WAGE_SOURCE_DETAIL.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table CLAIM_WAGE_CALCULATION
