CREATE TABLE CUSTOMER
(
   CUST_ID              	NUMERIC(31)        not null,
   CUST_TYP_CD          	VARCHAR(15)        not null,
   CNTRY_ID				NUMERIC(31),
   CUST_NO				VARCHAR(30),        
   CUST_1099_RECV_IND   	CHAR(1)            not null default 'n',
   CUST_W9_RECV_IND     	CHAR(1)            not null default 'n',
   CUST_TAX_EXMT_IND    	CHAR(1)            default 'n',
   CUST_FRGN_CITZ_IND   	CHAR(1)            default 'n',
   CUST_TAX_ID_UNAVL_IND  	CHAR(1)            default 'n',
   CUST_TAX_ID_OVRRD_IND     CHAR(1)			   NOT NULL DEFAULT 'n',
   CNTIRT_CD            	VARCHAR(15),
   CRST_CD	   			VARCHAR(15),
   CUST_PWRTRK_SENT_IND         CHAR(1) DEFAULT 'n',
   CUST_OFAC_WTCH_LIST_IND      CHAR(1)			   NOT NULL DEFAULT 'n',
   CUST_OFAC_OVRRD_IND     		CHAR(1)			   NOT NULL DEFAULT 'n',
   CUST_OFAC_SRCH_DT      		TIMESTAMP,
   CUST_SGNTR_FILE_NM           VARCHAR(80),
   AUDIT_USER_ID_CREA   	NUMERIC(31),
   AUDIT_USER_CREA_DTM  	TIMESTAMP           not null,
   AUDIT_USER_ID_UPDT   	NUMERIC(31),
   AUDIT_USER_UPDT_DTM  	TIMESTAMP,
   VOID_IND        		CHAR(1)             not null default 'n',
   constraint XPK_CUST primary key (CUST_ID)
);


COMMENT ON TABLE CUSTOMER IS 'Customers are the persons and businesses that are interacted with on an ongoing basis.';
COMMENT ON COLUMN CUSTOMER.CUST_ID IS 'This is a primary key to the customer table used to uniquely identify each record.';
COMMENT ON COLUMN CUSTOMER.CUST_TYP_CD IS 'This is a foreign key to the customer_type table specifying the customer type associated with this record. Indicates whether the customer is a person or a business.';
COMMENT ON COLUMN CUSTOMER.CUST_NO IS 'External customer reference number.  Set by system or user.';
COMMENT ON COLUMN CUSTOMER.CUST_1099_RECV_IND IS 'Indicator identifying whether or not the customer is a 1099 receiver.';
COMMENT ON COLUMN CUSTOMER.CUST_W9_RECV_IND IS 'Indicator identifying whether or not the customer is a W9 reciever.';
COMMENT ON COLUMN CUSTOMER.CUST_TAX_EXMT_IND IS 'Indicator identifying whether or not the customer is tax exempt.';
COMMENT ON COLUMN CUSTOMER.CUST_FRGN_CITZ_IND IS 'Indicator identifying whether or not the customer is a foreign citizen.';
COMMENT ON COLUMN CUSTOMER.CUST_TAX_ID_UNAVL_IND IS 'Indicator identifying whether or not no tax identifier is available for the customer.';
COMMENT ON COLUMN CUSTOMER.CNTIRT_CD IS 'This is a foreign key to the customer_no_tax_id_reason_type table specifying the reason tax id was given.';
COMMENT ON COLUMN CUSTOMER.CRST_CD IS 'This is a foreign key to the customer_record_source_type table specifying where the record came from. (PowerComp, legacy system, etc?).';
COMMENT ON COLUMN CUSTOMER.CUST_SGNTR_FILE_NM IS 'The name of the file that contains the customer signature';
COMMENT ON COLUMN CUSTOMER.CUST_OFAC_WTCH_LIST_IND IS 'Indicator identifying whether or not the customer is under OFAC watchlist';
COMMENT ON COLUMN CUSTOMER.CUST_OFAC_OVRRD_IND IS 'Indicator identifying whether or not the customer to be excluded for OFAC Search';
COMMENT ON COLUMN CUSTOMER.CUST_OFAC_SRCH_DT IS 'Date and time the customer was searched for OFAC Watchlist';
COMMENT ON COLUMN CUSTOMER.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CUSTOMER.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CUSTOMER.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CUSTOMER.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN CUSTOMER.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN CUSTOMER.CNTRY_ID IS 'The ID for the country of the customer';
COMMENT ON COLUMN CUSTOMER.CUST_PWRTRK_SENT_IND IS 'Whether the customer has been sent to PowerTrack';
COMMENT ON COLUMN CUSTOMER.CUST_TAX_ID_OVRRD_IND IS 'indicates whether tax identifier is Overridden or not';


create table CUSTOMER_ADDRESS
(
   CUST_ADDR_ID         NUMERIC(31)            not null,
   CUST_ID              NUMERIC(31)            not null,
   CUST_ADDR_TYP_CD     VARCHAR(15)            not null,
   STT_ID		NUMERIC(31),
   CNTRY_ID		NUMERIC(31),
   CUST_ADDR_EFF_DT     TIMESTAMP              not null,
   CUST_ADDR_END_DT     TIMESTAMP,
   CUST_ADDR_STR_1      VARCHAR(40)            not null,
   CUST_ADDR_STR_2      VARCHAR(40),
   CUST_ADDR_COMT       VARCHAR(256),
   CUST_ADDR_LNK_IND    CHAR(1)                not null default 'n',
   CUST_ADDR_POST_CD		VARCHAR(15),
   CUST_ADDR_CITY_NM		VARCHAR(80),
   CUST_ADDR_CITY_UPCS_SRCH_NM	VARCHAR(80),
   CUST_ADDR_CNTY_NM		VARCHAR(80),
   CUST_ADDR_CNTY_UPCS_SRCH_NM	VARCHAR(80),
   CUST_ADDR_POST_BRCD		VARCHAR(16),
   CUST_ADDR_LAT        NUMERIC(31, 10),
   CUST_ADDR_LONG       NUMERIC(31, 10),
   CUST_ADDR_EXTRNL_ADDR_NO     VARCHAR(80),
   CUST_ADDR_UPDT_SRC			VARCHAR(20),
   CUST_ADDR_OVRRD_IND   CHAR(1)  not null default 'n',
   CUST_ADDR_VLDT_IND   CHAR(1)   not null default 'n',
   CUST_ADDR_VLDT_PRFM_IND   CHAR(1)   not null default 'n',
   CUST_ADDR_SRCH_NRMLIZED_STR VARCHAR(80),
   CUST_LNK_ADDR_ID	NUMERIC(31),
   AUDIT_USER_ID_CREA   NUMERIC(31)            not null,
   AUDIT_USER_CREA_DTM  TIMESTAMP              not null,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   VOID_IND   CHAR(1)                not null default 'n',
   constraint XPK_CUSTA primary key (CUST_ADDR_ID)
);


COMMENT ON TABLE CUSTOMER_ADDRESS IS 'Data table containing address records for use with customers and risk locations.';
COMMENT ON COLUMN CUSTOMER_ADDRESS.CUST_ADDR_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
