   CUST_CNTC_CMT        VARCHAR(256),
   AUDIT_USER_ID_CREA   NUMERIC(31),
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   CUST_CNTC_VOID_IND   CHAR(1)                default 'n' not null,
   constraint XPK_CC primary key (CUST_CNTC_ID)
);


COMMENT ON TABLE CUSTOMER_CONTACT IS 'Data table facilitating the addition of contact information for a customer.';
COMMENT ON COLUMN CUSTOMER_CONTACT.CUST_CNTC_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN CUSTOMER_CONTACT.CUST_ID IS 'This is a foreign key to the customer table specifying which customer record this contact record pertains to.';
COMMENT ON COLUMN CUSTOMER_CONTACT.CNTC_TYP_CD IS 'This is a foreign key to the contact_type table indicating what type of contact has been associated with the customer (attorney, parent, roommate, etc).';
