COMMENT ON COLUMN CUSTOMER_CONTACT.CUST_CNTC_EFF_DT IS 'The date that this contact record became effective.';
COMMENT ON COLUMN CUSTOMER_CONTACT.CUST_CNTC_END_DT IS 'The date that this contact record is no longer in effect.';
COMMENT ON COLUMN CUSTOMER_CONTACT.CUST_CNTC_CMT IS 'Comment text field for the contact.';
COMMENT ON COLUMN CUSTOMER_CONTACT.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CUSTOMER_CONTACT.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CUSTOMER_CONTACT.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CUSTOMER_CONTACT.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN CUSTOMER_CONTACT.CUST_CNTC_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table CUSTOMER_CONTACT_DETAIL
(
   CUST_CNTC_DTL_ID     NUMERIC(31)            not null,
   CUST_CNTC_ID         NUMERIC(31),
   CUST_CNTC_DTL_TYP_CD VARCHAR(15),
   CUST_CNTC_DTL_SUB_TYP VARCHAR(15),
   CUST_CNTC_CNTRY_CD              VARCHAR(15),
   CUST_CNTC_DIAL_CD              VARCHAR(15),
   CUST_CNTC_DTL_VAL_1  VARCHAR(256),
   CUST_CNTC_DTL_VAL_2  VARCHAR(256),
   AUDIT_USER_ID_CREA   NUMERIC(31),
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   CUST_CNTC_DTL_VOID_IND CHAR(1)                default 'n' not null,
   constraint XPK_CCD primary key (CUST_CNTC_DTL_ID)
);


COMMENT ON TABLE CUSTOMER_CONTACT_DETAIL IS 'Data table facilitating the entry of one-to-many contact detail records (contact information).';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.CUST_CNTC_DTL_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.CUST_CNTC_ID IS 'This is a foreign key to the customer_contact table specifying which customer_contact record this detail record pertains to.';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.CUST_CNTC_DTL_TYP_CD IS 'This is a foreign key to the customer_contact_detail_type table indicating the different types of customer contacts (phone number, e-mail, fax number, web site, etc?).';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.CUST_CNTC_DTL_SUB_TYP IS 'This is a foreign key to the customer_contact_detail_sub_type indicating the different sub types of customer contact types  -  use b for business, c for celular, f for fax, h for home, m for movbile, o for other, p for pager, or t for TTY/TTD.';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.CUST_CNTC_DTL_VAL_1 IS 'Column representing the value of the contact detail type (like business phone number).';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.CUST_CNTC_CNTRY_CD IS ' Contact detail pnone Country  code.';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.CUST_CNTC_DIAL_CD IS 'Contact detail phone dial code.';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.CUST_CNTC_DTL_VAL_2 IS 'Column representing the value of the contact detail sub-type (like business phone extension number).';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL.CUST_CNTC_DTL_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table CUSTOMER_CONTACT_DETAIL_TYPE
(
   CUST_CNTC_DTL_TYP_CD VARCHAR(15)            not null,
   CUST_CNTC_DTL_TYP_NM VARCHAR(80) NOT NULL,
   CUST_CNTC_DTL_TYP_VOID_IND CHAR(1)                default 'n',
   constraint XPK_CCDT primary key (CUST_CNTC_DTL_TYP_CD)
);


COMMENT ON TABLE CUSTOMER_CONTACT_DETAIL_TYPE IS 'This code table defines the classification of customer contact details. (phone number, e-mail, fax number, etc?).';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL_TYPE.CUST_CNTC_DTL_TYP_CD IS 'A unique code value to identify the specific customer contact detail.';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL_TYPE.CUST_CNTC_DTL_TYP_NM IS 'A user friendly name to describe the customer contact detail.';
COMMENT ON COLUMN CUSTOMER_CONTACT_DETAIL_TYPE.CUST_CNTC_DTL_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table CUSTOMER_CONTACT_XREF
(
