   constraint XPK_CCRX primary key (CCRX_ID)
);

comment on table CUSTOMER_CUSTOMER_ROLE_XREF is 'Cross-reference table specifying which customer roles may be added to what customer types.';
comment on column CUSTOMER_CUSTOMER_ROLE_XREF.CUST_TYP_CD is 'This is a foreign key to the customer_type table indicating the type of customer type (person, business) for which the associated customer role is valid.';
comment on column CUSTOMER_CUSTOMER_ROLE_XREF.CUST_ROLE_TYP_CD is 'This is a foreign key to the customer_role_type table indicating a customer role (agency, provider, etc) valid for the customer type.';
comment on column CUSTOMER_CUSTOMER_ROLE_XREF.CUST_CUST_ROLE_XREF_VOID_IND is 'An indicator identifying whether the record has been logically deleted or not.';

create table CUSTOMER_ADDRESS_APP_CNTX_XREF
(
CAACX_ID			        NUMERIC(31)	 not null,
APP_CNTX_TYP_CD       	VARCHAR(15),
APP_SUB_CNTX_TYP_CD	VARCHAR(15),
CUST_ADDR_TYP_CD     	VARCHAR(15)  ,
CAACX_PRTY_NO 		SMALLINT ,             				
VOID_IND 			        CHAR(1) not null default 'n',
constraint XPK_CAACX primary key (CAACX_ID)
);


COMMENT ON TABLE CUSTOMER_ADDRESS_APP_CNTX_XREF IS 'This table defines all the address types applicable for a particular application context and subcontext combination. It also defines the order in which identified processes will search for and use valid addresses existing for a customer.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_APP_CNTX_XREF.CAACX_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_APP_CNTX_XREF.APP_CNTX_TYP_CD IS 'This is a foreign key to the application_context_type table specifying to which application context this record is applicable for.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_APP_CNTX_XREF.APP_SUB_CNTX_TYP_CD IS 'This is a foreign key to the application_sub_context_type table specifying to which application sub context this record is applicable for.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_APP_CNTX_XREF.CUST_ADDR_TYP_CD IS 'This is a foreign key to the customer_address_type table specifying the valid address type for given application context and sub context.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_APP_CNTX_XREF.CAACX_PRTY_NO IS 'This identifies the priority of the address type for a given application context and sub context combination.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_APP_CNTX_XREF.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE CUSTOMER_HISTORY (
      HIST_ID                     NUMERIC(31)  NOT NULL,
      CUST_ID                     NUMERIC(31)   ,
      CUST_TYP_CD                 VARCHAR(15) ,
		CNTRY_ID			    NUMERIC(31),      
      CUST_NO                     VARCHAR(30) ,
      CUST_1099_RECV_IND          CHAR(1) ,
      CUST_W9_RECV_IND            CHAR(1) ,
      CUST_TAX_EXMT_IND           CHAR(1) ,
      CUST_FRGN_CITZ_IND          CHAR(1) ,
      CUST_TAX_ID_UNAVL_IND       CHAR(1) ,
	  CUST_TAX_ID_OVRRD_IND        CHAR(1)   NOT NULL DEFAULT 'n',
      CNTIRT_CD                   VARCHAR(15) ,
      CRST_CD                     VARCHAR(15) ,
      CUST_PWRTRK_SENT_IND        CHAR(1),		
      CUST_SGNTR_FILE_NM           VARCHAR(80),
      CUST_OFAC_WTCH_LIST_IND      CHAR(1)			   NOT NULL DEFAULT 'n',
   	  CUST_OFAC_OVRRD_IND     		CHAR(1)			   NOT NULL DEFAULT 'n',
      CUST_OFAC_SRCH_DT      		TIMESTAMP,
      AUDIT_USER_ID_CREA          NUMERIC(31) ,
      AUDIT_USER_CREA_DTM         TIMESTAMP   ,
      AUDIT_USER_ID_UPDT          NUMERIC(31) ,
      AUDIT_USER_UPDT_DTM         TIMESTAMP ,
      HIST_EFF_DTM                TIMESTAMP     NOT NULL,
      HIST_END_DTM                TIMESTAMP ,
      VOID_IND               	    CHAR(1)  not null default 'n',
      CONSTRAINT XPK_CUSTH        PRIMARY KEY (HIST_ID)
);


COMMENT ON TABLE CUSTOMER_HISTORY IS 'This is a history table which captures historical information about customers.';
COMMENT ON COLUMN CUSTOMER_HISTORY.HIST_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CUST_ID IS 'This is a foreign key to the customer table defining the specific customer record that this history record pertains to.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CUST_TYP_CD IS 'This is a foreign key to the customer_type table specifying the customer type associated with this record. Indicates whether the customer is a person or a business.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CNTRY_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CUST_NO IS 'External customer reference number.  Set by system or user.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CUST_1099_RECV_IND IS 'Indicator identifying whether or not the customer is a 1099 receiver.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CUST_W9_RECV_IND IS 'Indicator identifying whether or not the customer is a W9 reciever.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CUST_TAX_EXMT_IND IS 'Indicator identifying whether or not the customer is tax exempt.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CUST_FRGN_CITZ_IND IS 'Indicator identifying whether or not the customer is a foreign citizen.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CUST_TAX_ID_UNAVL_IND IS 'Indicator identifying whether or not no tax identifier is available for the customer.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CNTIRT_CD IS 'This is a foreign key to the customer_no_tax_id_reason_type table specifying the reason tax id was given.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CRST_CD IS 'This is a foreign key to the customer_record_source_type table specifying where the record came from. (PowerComp, legacy system, etc?).';
COMMENT ON COLUMN CUSTOMER_HISTORY.CUST_OFAC_WTCH_LIST_IND IS 'Indicator identifying whether or not the customer is under OFAC watchlist.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CUST_OFAC_OVRRD_IND IS 'Indicator identifying whether or not the customer to be excluded for OFAC Search.';
COMMENT ON COLUMN CUSTOMER_HISTORY.CUST_OFAC_SRCH_DT IS 'Date and time the customer was searched for OFAC Watchlist.';
COMMENT ON COLUMN CUSTOMER_HISTORY.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CUSTOMER_HISTORY.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CUSTOMER_HISTORY.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CUSTOMER_HISTORY.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN CUSTOMER_HISTORY.HIST_EFF_DTM IS 'The date this history record took effect.';
COMMENT ON COLUMN CUSTOMER_HISTORY.HIST_END_DTM IS 'The date that this history record is no longer in effect.';
COMMENT ON COLUMN CUSTOMER_HISTORY.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table CUSTOMER_NAME(
   CUST_NM_ID           NUMERIC(31)            not null,
   CUST_ID              NUMERIC(31)            not null,
   CUST_NM_TYP_CD       VARCHAR(15)            not null,
   CUST_NM_EFF_DT       TIMESTAMP              not null,
   CUST_NM_END_DT       TIMESTAMP,
   CUST_NM_NM           VARCHAR(161)           not null,
   CUST_NM_ALIAS_TYP_CD	VARCHAR(15), 
   CUST_NM_DRV_UPCS_NM  VARCHAR(161)           not null,
   CUST_NM_SRCH_NRMLIZED_NM VARCHAR(161),
   CUST_NM_TTL_TYP_CD   VARCHAR(15),
   CUST_NM_FST          VARCHAR(40),
   CUST_NM_MID          CHAR(1),
   CUST_NM_LST          VARCHAR(118),
   CUST_NM_SFX_TYP_CD   VARCHAR(15),
   CUST_NM_TAX_VRF_IND           CHAR(1) NOT NULL DEFAULT 'n',    
   AUDIT_USER_ID_CREA   NUMERIC(31)            not null,
   AUDIT_USER_CREA_DTM  TIMESTAMP              not null,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   VOID_IND             CHAR(1)                not null default 'n',
   constraint XPK_CN primary key (CUST_NM_ID)
);


COMMENT ON TABLE CUSTOMER_NAME IS 'Data table used to associate one-to-many names with a customer.';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_ID IS 'This is a foreign key to the customer table specifying which customer record this name record pertains to.';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_TYP_CD IS 'This is a foreign key to the customer_name_type table indicating which name type is associated with this record (business legal name, primary dba name, person name, person alias, etc?).';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_EFF_DT IS 'The date that this customers name record became effective.';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_END_DT IS 'The date that this customers name record is no longer in effect.';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_NM IS 'The customers single field name.  For persons, contains last name, first name middle initial format.';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_ALIAS_TYP_CD IS 'This is a foreign key to the customer_name_alias_type table indicating which alias name type is associated with this record.';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_DRV_UPCS_NM IS 'Uppercase derived customer name for use in customer searches (retains index usablity).';
Comment on column CUSTOMER_NAME.CUST_NM_SRCH_NRMLIZED_NM is 'contains the customer name in a normalized form to facilitate name search';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_TTL_TYP_CD IS 'This is a foreign key to the customer_name_title_type table indicating the type of title associated with this person customer (Mr., Ms., Mrs., etc?).';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_FST IS 'First name of the customer.';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_MID IS 'Middle initial of the customer.';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_LST IS 'Last name of the customer.';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_SFX_TYP_CD IS 'This is a foreign key to the customer_name_suffix_type table indicating the type of suffix associated with this person customer (Jr., Sr., etc?).';
COMMENT ON COLUMN CUSTOMER_NAME.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CUSTOMER_NAME.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CUSTOMER_NAME.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CUSTOMER_NAME.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN CUSTOMER_NAME.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN CUSTOMER_NAME.CUST_NM_TAX_VRF_IND IS 'indicates whether customer name should be used for Tax id verification or not';

create table CUSTOMER_NAME_SUFFIX_TYPE
