COMMENT ON COLUMN CUSTOMER_NAME_TITLE_TYPE.CUST_NM_TTL_TYP_NM IS 'A user friendly name to describe the customer name title.';
COMMENT ON COLUMN CUSTOMER_NAME_TITLE_TYPE.CUST_NM_TTL_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table CUSTOMER_NAME_TYPE
(
