COMMENT ON COLUMN CUSTOMER_NAME_TYPE.CUST_NM_TYP_NM IS 'A user friendly name to describe the customer name type.';
COMMENT ON COLUMN CUSTOMER_NAME_TYPE.CUST_NM_TYP_PRTY_NO IS 'Priority order for customer name types.';
COMMENT ON COLUMN CUSTOMER_NAME_TYPE.CUST_NM_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table CUSTOMER_ROLE
(
   ROLE_ID                   	NUMERIC(31)            not null,
