COMMENT ON TABLE PAYMENT_PLAN_PROFILE IS 'This code table defines the applicable payment plans and the range of premium associated to each plan.';
COMMENT ON COLUMN PAYMENT_PLAN_PROFILE.PYMT_PLAN_PRFL_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN PAYMENT_PLAN_PROFILE.CTL_ELEM_SUB_TYP_ID IS 'This is a foreign key to the control_element_sub_type table defining the applicable payment plan for the payment plan control element.';
COMMENT ON COLUMN PAYMENT_PLAN_PROFILE.PYMT_PLAN_PRFL_DESC IS 'A user friendly name to describe the payment plan.';
Comment on column PAYMENT_PLAN_PROFILE.PYMT_PLAN_PRFL_DVND_PLAN_IND is 'indicator which determines if plan can have deferred dividend';
COMMENT ON COLUMN PAYMENT_PLAN_PROFILE.CTL_ELEM_SUB_TYP_CD is 'This column holds control element sub type code for policy type control element';
COMMENT ON COLUMN PAYMENT_PLAN_PROFILE.PYMT_PLAN_PRFL_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table PAYMENT_PLAN_PROFILE_DETAIL
(
   PPPD_ID              NUMERIC(31)            not null,
   PYMT_PLAN_PRFL_ID    NUMERIC(31),
   PPPD_DTL_NO          NUMERIC(31)            not null,
   PPPD_BILL_DT_OFST_MM NUMERIC(31),
