   PPPD_END_DT          TIMESTAMP,
   constraint XPK_PAYPPD primary key (PPPD_ID)
);


COMMENT ON TABLE PAYMENT_PLAN_PROFILE_DETAIL IS 'This code table defines the applicable timeframes for each payment plan by identifying the detail number of the plan and the number of offset months for billing scheduling.';
COMMENT ON COLUMN PAYMENT_PLAN_PROFILE_DETAIL.PPPD_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN PAYMENT_PLAN_PROFILE_DETAIL.PYMT_PLAN_PRFL_ID IS 'This is a foreign key to the payment_plan_profile table defining the applicable payment plan for this record.';
COMMENT ON COLUMN PAYMENT_PLAN_PROFILE_DETAIL.PPPD_DTL_NO IS 'This is the detail number of the payment plan for this record.';
COMMENT ON COLUMN PAYMENT_PLAN_PROFILE_DETAIL.PPPD_BILL_DT_OFST_MM IS 'This is the number of months to offset the billing dates of this installment from the previous installment (first installment is always the policy period effective date).';
COMMENT ON COLUMN PAYMENT_PLAN_PROFILE_DETAIL.PPPD_EFF_DT IS 'The date the payment plan profile became effective.';
COMMENT ON COLUMN PAYMENT_PLAN_PROFILE_DETAIL.PPPD_END_DT IS 'The date the payment plan profile expired.';

create table PAYMENT_PLAN_PROFILE_DTL_AMT
(
