create table PAYROLL_REPORT
(
   PYRL_RPT_ID          NUMERIC(31)            not null,
   PYRL_RPT_TYP_CD      VARCHAR(15),
   PYRL_RPT_EFF_DT      TIMESTAMP,
   PYRL_RPT_END_DT      TIMESTAMP,
   PYRL_RPT_ISS_DT      TIMESTAMP,
   PYRL_RPT_DUE_DT      TIMESTAMP,
   PYRL_RPT_RECV_DT     TIMESTAMP,
   PYRL_RPT_MOD_DT         TIMESTAMP,
   PYRL_RPT_STS_TYP_CD  VARCHAR(15),
   PYRL_RPT_STS_RSN_TYP_CD VARCHAR(15),
   PYRL_RPT_EST_RPT_IND CHAR(1)                default 'n',
   PYRL_RPT_ENT_BY_USER_VAL VARCHAR(80),
   PLCY_PRD_GRP_ID     	NUMERIC(31),
   DOCM_ID               NUMERIC(31), 
   PAY_TRK_SRC_TYP_CD              VARCHAR(15),
   PYRL_RPT_PAY_TRK_SRC_TYP_NM     VARCHAR(80),
   AUDIT_USER_ID_CREA   NUMERIC(31),
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
