   PYRL_RPT_STS_TYP_VOID_IND CHAR(1)                not null default 'n',
   constraint XPK_PRST primary key (PYRL_RPT_STS_TYP_CD)

);


COMMENT ON TABLE PAYROLL_REPORT_STATUS_TYPE IS 'This code table defines the applicable types of statuses that can be associated with payroll report within the system.';
COMMENT ON COLUMN PAYROLL_REPORT_STATUS_TYPE.PYRL_RPT_STS_TYP_CD IS 'A unique code value to identify the specific payroll report status.';
COMMENT ON COLUMN PAYROLL_REPORT_STATUS_TYPE.PYRL_RPT_STS_TYP_NM IS 'A user friendly name to describe the payroll report status.';
COMMENT ON COLUMN PAYROLL_REPORT_STATUS_TYPE.PYRL_RPT_STS_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table PAYROLL_REPORT_TYPE
(
   PYRL_RPT_TYP_CD      VARCHAR(15)            not null,
   PYRL_RPT_TYP_NM      VARCHAR(80) NOT NULL,
   PYRL_RPT_TYP_VOID_IND CHAR(1)                not null default 'n',
   constraint XPK_PRT primary key (PYRL_RPT_TYP_CD)
);


COMMENT ON TABLE PAYROLL_REPORT_TYPE IS 'This code table defines the applicable types of payroll reports that can be created. within the system.';
COMMENT ON COLUMN PAYROLL_REPORT_TYPE.PYRL_RPT_TYP_CD IS 'A unique code value to identify the specific payroll report type.';
COMMENT ON COLUMN PAYROLL_REPORT_TYPE.PYRL_RPT_TYP_NM IS 'A user friendly name to describe the payroll report type.';
COMMENT ON COLUMN PAYROLL_REPORT_TYPE.PYRL_RPT_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


 create table PAYROLL_RPT_STS_STS_RSN_XREF
(
   PRSSRX_ID            NUMERIC(31)            not null,
   PYRL_RPT_STS_TYP_CD  VARCHAR(15)            not null,
