   constraint XPK_PRSR primary key (PRSSRX_ID)
);


COMMENT ON TABLE PAYROLL_RPT_STS_STS_RSN_XREF IS 'This code table serves as a cross reference table which maps applicable payroll report status reasons to the applicable payroll report statuses that can use each reason.';
COMMENT ON COLUMN PAYROLL_RPT_STS_STS_RSN_XREF.PRSSRX_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN PAYROLL_RPT_STS_STS_RSN_XREF.PYRL_RPT_STS_TYP_CD IS 'This is a foreign key to the payroll_report_status_type table defining the specific status type for this status/reason combination.';
COMMENT ON COLUMN PAYROLL_RPT_STS_STS_RSN_XREF.PYRL_RPT_STS_RSN_TYP_CD IS 'This is a foreign key to the payroll_report_status_rsn_typ table defining the specific reason type for this status/reason combination.';
Comment on column PAYROLL_RPT_STS_STS_RSN_XREF.PRSSRX_REQD_2ND_LVL_RVW_IND is 'used to determine if the payroll report need a second level review';
Comment on column PAYROLL_RPT_STS_STS_RSN_XREF.PRSSRX_REJ_RVW_REQD_IND is 'used to determine if the payroll report is rejected for review required';
COMMENT ON COLUMN PAYROLL_RPT_STS_STS_RSN_XREF.PRSSRX_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table PERSON
(
   CUST_ID               NUMERIC(31)	not null,
   TAX_FILE_STS_TYP_CD   VARCHAR(15),
   PRSN_TAX_EXMT_NO      SMALLINT,
   GNDR_TYP_CD           VARCHAR(15),
   GNDR_PRON_CD			 VARCHAR(15),
   MAR_STS_TYP_CD        VARCHAR(15),
   PRSN_BRTH_DTM         TIMESTAMP,
   PRSN_DTH_DTM          TIMESTAMP,
   PRSN_TAX_INCMPT_IND   CHAR(1)    NOT NULL DEFAULT 'n',
   constraint XPK_PRSN primary key (CUST_ID)
);


COMMENT ON TABLE PERSON IS 'Contains data specific to a customer with a customer type of "person".';
COMMENT ON COLUMN PERSON.CUST_ID IS 'This is a foreign key to the customer table indicating the person customer for whom the person data is relevent.';
COMMENT ON COLUMN PERSON.TAX_FILE_STS_TYP_CD IS 'This is a foreign key to the tax_filing_status_type table indicating the tax filing status of the person customer.';
COMMENT ON COLUMN PERSON.PRSN_TAX_EXMT_NO IS 'The number of tax exemptions for the person customer.';
COMMENT ON COLUMN PERSON.GNDR_TYP_CD IS 'This is a foreign key to the gender_type table indicating the gender of the person customer.';
COMMENT ON COLUMN PERSON.GNDR_PRON_CD IS 'This is a foreign key to the gender_pronoun table indicating the gender pronoun of the person customer.';
COMMENT ON COLUMN PERSON.MAR_STS_TYP_CD IS 'This is a foreign key to the marital_status_type table indicating the marital status of the person customer.';
COMMENT ON COLUMN PERSON.PRSN_BRTH_DTM IS 'The birthdate of the person customer.';
COMMENT ON COLUMN PERSON.PRSN_DTH_DTM IS 'The death date of the person customer.';
COMMENT ON COLUMN PERSON.PRSN_TAX_INCMPT_IND IS 'Indicates that the person customer is incompetent.';


create table PHONE_NUMBER_TYPE
(
   PHN_NO_TYP_CD        VARCHAR(15)            not null,
   PHN_NO_TYP_NM        VARCHAR(80)            not null,
   PHN_NO_TYP_VOID_IND  CHAR(1)                not null default 'n',
   constraint XPK_PNT primary key (PHN_NO_TYP_CD)
);


COMMENT ON TABLE PHONE_NUMBER_TYPE IS 'Code table containing possible phone number types (e.g. business, cellular, home, etc?).';
COMMENT ON COLUMN PHONE_NUMBER_TYPE.PHN_NO_TYP_CD IS 'A unique code value to identify the specific phone number code.';
COMMENT ON COLUMN PHONE_NUMBER_TYPE.PHN_NO_TYP_NM IS 'A user friendly name to describe the phone number code.';
COMMENT ON COLUMN PHONE_NUMBER_TYPE.PHN_NO_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table POLICY
(
   AGRE_ID                NUMERIC(31)            not null,
   QUOT_VER_NO            VARCHAR(10),
   QUOT_NO                VARCHAR(30),
   LOB_TYP_CD             VARCHAR(15)            not null,
   MRKT_TYP_CD            VARCHAR(15)            not null,
   INS_CMPY_TYP_ID        NUMERIC(31)            not null,
   CUST_ID_ACCT_HLDR      NUMERIC(31)            not null,
   QUOT_VER_DESC          VARCHAR(256),
   PLCY_SHLL_IND          CHAR(1)                not null default 'n',
   PLCY_KY_ACCT_IND       CHAR(1)                not null default 'n',
   PLCY_OUT_OF_CYC_IND    CHAR(1)                not null default 'n',
   PLCY_WEB_QUOT_IND      CHAR(1)                not null default 'n',
   PLCY_ORIG_DT			      TIMESTAMP, 
   QUOT_RECV_DT		  TIMESTAMP, 
   AUDIT_USER_ID_CREA     NUMERIC(31),
   AUDIT_USER_CREA_DTM    TIMESTAMP,
   AUDIT_USER_ID_UPDT     NUMERIC(31),
   AUDIT_USER_UPDT_DTM    TIMESTAMP,
   VOID_IND 			        CHAR(1)                not null default 'n',     
   constraint XPK_PLCY primary key (AGRE_ID)
);


COMMENT ON TABLE POLICY IS 'This data table captures policy level information and attributes about both policies and quotes.';
COMMENT ON COLUMN POLICY.AGRE_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.  Also a foreign key to the agreement table.';
COMMENT ON COLUMN POLICY.QUOT_VER_NO IS 'This is the version number of the quote and is only applicable if this is a quote record.';
COMMENT ON COLUMN POLICY.QUOT_NO IS 'The system or user generated number to identify the specific quote.';
COMMENT ON COLUMN POLICY.LOB_TYP_CD IS 'This is a foreign key to the line_of_business_type table defining the specific line of business for this policy or quote.';
COMMENT ON COLUMN POLICY.MRKT_TYP_CD IS 'This is a foreign key to the market_type table defining the specific market type for this policy or quote.';
COMMENT ON COLUMN POLICY.INS_CMPY_TYP_ID IS 'This is a foreign key to the insurance_company_type table defining the specific insurance company for this policy or quote.';
COMMENT ON COLUMN POLICY.CUST_ID_ACCT_HLDR IS 'This is a foreign key to the customer table defining the specific customer who is the account holder for this policy or quote.';
COMMENT ON COLUMN POLICY.QUOT_VER_DESC IS 'This is a user enterable description for the quote version allowing the user to uniquely identify the quote in user friendly terms.';
COMMENT ON COLUMN POLICY.PLCY_SHLL_IND IS 'This is an indicator used to determine if a policy or quote is a policy shell and therefore can only be accessed with limited processing in the system.';
COMMENT ON COLUMN POLICY.PLCY_KY_ACCT_IND IS 'This is an indicator used to determine if a policy is identified as a key account (usually exceeding a pre-defined Estimated Annual Premium amount.';
Comment on column POLICY.PLCY_OUT_OF_CYC_IND is 'used to determine if the policy should be used for out of cycle invoicing';
COMMENT ON COLUMN POLICY.PLCY_ORIG_DT IS 'The date of policy orgin.  In policies orginating in PowerComp, this ends up being the minimum policy period effective date for the policy.';
COMMENT ON COLUMN POLICY.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN POLICY.PLCY_WEB_QUOT_IND IS 'Whether or not the quote was done on the web application';
COMMENT ON COLUMN POLICY.QUOT_RECV_DT IS 'The date the quote was received';

create table POLICY_CARRIER_INFORMATION
(
   PLCY_CAR_INFO_ID     NUMERIC(31)            not null,
