   AUDIT_USER_ID_CREA   NUMERIC(31),
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   PLCY_CAR_INFO_VOID_IND CHAR(1)                default 'n',
   constraint XPK_PCI primary key (PLCY_CAR_INFO_ID)
);


COMMENT ON TABLE POLICY_CARRIER_INFORMATION IS 'This data table captures information about any prior carrier(s) associated with this policy.';
COMMENT ON COLUMN POLICY_CARRIER_INFORMATION.PLCY_CAR_INFO_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN POLICY_CARRIER_INFORMATION.PLCY_PRD_ID IS 'This is a foreign key to the policy_period table defining the specific policy period to which this information is applicable.';
COMMENT ON COLUMN POLICY_CARRIER_INFORMATION.PLCY_CAR_INFO_NM IS 'This is a user entered name for the company which was the policys prior carrier.';
COMMENT ON COLUMN POLICY_CARRIER_INFORMATION.PLCY_CAR_INFO_PLCY_NO IS 'This is the policy number that the policy used while with the prior carrier.';
COMMENT ON COLUMN POLICY_CARRIER_INFORMATION.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY_CARRIER_INFORMATION.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_CARRIER_INFORMATION.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY_CARRIER_INFORMATION.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_CARRIER_INFORMATION.PLCY_CAR_INFO_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table POLICY_CONTROL_ELEMENT
(
   PLCY_CTL_ELEM_ID     NUMERIC(31)            not null,
   PLCY_PRD_ID          NUMERIC(31)			   not null,
   CTL_ELEM_TYP_CD      VARCHAR(15),
   CTL_ELEM_SUB_TYP_ID  NUMERIC(31),
   PLCY_CTL_ELEM_EFF_DT TIMESTAMP,
   PLCY_CTL_ELEM_END_DT TIMESTAMP,
   PLCY_CTL_ELEM_RN_IND CHAR(1)                default 'y',
   CTL_ELEM_AUDT_RSN_TYP_CD       VARCHAR(15),
   PLCY_CTL_ELEM_OVRRD_RN_DEP_PCT NUMERIC(5,2),
   PLCY_CTL_ELEM_OVRRD_RN_DEP_AMT NUMERIC(15,2),
   PLCY_CTL_ELEM_NOTE_IND CHAR(1),
   PLCY_CTL_ELEM_EXTRNL_STS VARCHAR(15),                  
   PLCY_CTL_ELEM_EXTRNL_DT TIMESTAMP,
   AUDIT_USER_ID_CREA   NUMERIC(31),
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   MNL_PAY_PLN_OVRRD_IND             CHAR(1) default 'n',
   VOID_IND             CHAR(1)                not null default 'n',   
   constraint XPK_PCE primary key (PLCY_CTL_ELEM_ID)
);
Comment on column POLICY_CONTROL_ELEMENT.MNL_PAY_PLN_OVRRD_IND is 'Indicates if the control element value for payment plan has been overridden manually.';


COMMENT ON TABLE POLICY_CONTROL_ELEMENT IS 'This data table captures information about elements that control the processing of a policy at a period level.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.PLCY_CTL_ELEM_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.PLCY_PRD_ID IS 'This is a foreign key to the policy_period table defining the specific policy period on which this control element controlled processing.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.CTL_ELEM_TYP_CD IS 'This is a foreign key to the control_element_type table defining the specific control element for this record.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.CTL_ELEM_SUB_TYP_ID IS 'This is a foreign key to the  control_element_sub_type table defining the specific value for the control element for this record.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.PLCY_CTL_ELEM_EFF_DT IS 'The date that this control element went into effect.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.PLCY_CTL_ELEM_END_DT IS 'The date that this control element is no longer in effect.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.PLCY_CTL_ELEM_RN_IND IS 'An indicator specifying whether or not this control element and its associated value should be renewed.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.CTL_ELEM_AUDT_RSN_TYP_CD is 'The reason code for audit type.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.PLCY_CTL_ELEM_OVRRD_RN_DEP_PCT IS 'This is the percentage of estimated annual premium that is to be considered and charged as deposit on a reporting policy.  This is only used to override the default percentage if desired.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.PLCY_CTL_ELEM_OVRRD_RN_DEP_AMT IS 'This is the amount of estimated annual premium that is to be considered and charged as deposit on a reporting policy.  This is only used to override the default amount if desired.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.PLCY_CTL_ELEM_NOTE_IND IS 'This is an indicator specifying whether or not this control element record has a note attached to it.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.PLCY_CTL_ELEM_EXTRNL_STS is 'The externalized column to hold the status of control element';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.PLCY_CTL_ELEM_EXTRNL_DT is 'The externalized column to hold the date of control element';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_CONTROL_ELEMENT.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table POLICY_COVERAGE_PART_3
(
   PLCY_COV_PRT_3_ID    NUMERIC(31)            not null,
   PLCY_PRD_ID          NUMERIC(31)			   not null,
   PLCY_COV_PRT_3_EFF_DT TIMESTAMP             not null,
   PLCY_COV_PRT_3_END_DT TIMESTAMP             not null,
   META_STT_ID          NUMERIC(31),
   JUR_TYP_CD           VARCHAR(15),
   PLCY_COV_PRT_3_RN_IND CHAR(1)                default 'n',
   NOTE_IND              CHAR(1),
   AUDIT_USER_ID_CREA   NUMERIC(31),
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   VOID_IND CHAR(1)                not null default 'n',
   constraint XPK_PCP3 primary key (PLCY_COV_PRT_3_ID)
);


COMMENT ON TABLE POLICY_COVERAGE_PART_3 IS 'This data table captures information about other states where coverage is extended to businesses with primary locations in a single state (NCCI term for other states coverage on a policy).';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.PLCY_COV_PRT_3_ID IS 'This is a foreign key to the policy coverage part 3 table defining the specific policy coverage part 3 record that this record pertains to.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.PLCY_PRD_ID IS 'This is a foreign key to the policy_period table defining the policy period that this coverage exists for.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.PLCY_COV_PRT_3_EFF_DT IS 'The date that this coverage went into effect.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.PLCY_COV_PRT_3_END_DT IS 'The date that this coverage was no longer in effect.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.META_STT_ID IS 'This is a foreign key to the meta_state table defining the state and the other states attribute for this coverage.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.JUR_TYP_CD IS 'This is a foreign key to the jurisdiction_type table defining the applicable jurisdiction (state) for the attribute defined for this record.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.PLCY_COV_PRT_3_RN_IND IS 'An indicator specifying whether or not this coverage should be renewed.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.NOTE_IND IS 'An indicator specifying whether or not a note exists on this particular coverage part 3  record.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_COVERAGE_PART_3.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table POLICY_NON_RENEWAL_REASON_TYPE
(
   PLCY_NRN_RSN_TYP_CD  VARCHAR(15)            not null,
   PLCY_NRN_RSN_TYP_NM  VARCHAR(80)            not null,
   PLCY_NRN_RSN_TYP_NTC_PRD_DD NUMERIC(31),
   PLCY_NRN_RSN_TYP_SYS_IND    CHAR(1)         not null default 'n',
   VOID_IND             CHAR(1)                not null default 'n',
   constraint XPK_PNRRT primary key (PLCY_NRN_RSN_TYP_CD)
);


COMMENT ON TABLE POLICY_NON_RENEWAL_REASON_TYPE IS 'This code table defines the applicable types of reasons for non renewal in the system.';
COMMENT ON COLUMN POLICY_NON_RENEWAL_REASON_TYPE.PLCY_NRN_RSN_TYP_CD IS 'A unique code value to identify the specific non renewal reason.';
COMMENT ON COLUMN POLICY_NON_RENEWAL_REASON_TYPE.PLCY_NRN_RSN_TYP_NM IS 'A user friendly name to describe the non renewal reason.';
COMMENT ON COLUMN POLICY_NON_RENEWAL_REASON_TYPE.PLCY_NRN_RSN_TYP_NTC_PRD_DD IS 'The number of days of notice which must be given with this non renewal reason.';
COMMENT ON COLUMN POLICY_NON_RENEWAL_REASON_TYPE.PLCY_NRN_RSN_TYP_SYS_IND IS 'Represents system owned indicator for the reason';
COMMENT ON COLUMN POLICY_NON_RENEWAL_REASON_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table POLICY_PERIOD
(
   PLCY_PRD_ID                NUMERIC(31)  not null,
   AGRE_ID                    NUMERIC(31) not null,
   PLCY_NO                    VARCHAR(30),
   PLCY_PRD_NO_1              VARCHAR(30),
   PLCY_PRD_EFF_DT            TIMESTAMP    not null,
   PLCY_PRD_END_DT            TIMESTAMP    not null,
   PLCY_PRD_ORIG_END_DT       TIMESTAMP,
   PLCY_PRD_PRO_RT_IND        CHAR(1)      not null default 'n',
   PLCY_PRD_RECALC_E_PREM_IND CHAR(1)      not null default 'n',
   PLCY_PRD_RECALC_R_PREM_IND CHAR(1)      not null default 'n',
   PLCY_PRD_RECALC_A_PREM_IND CHAR(1)      not null default 'n',
   PLCY_PRD_RECALC_PFT_IND    CHAR(1)      not null default 'n',
   PLCY_PRD_DO_NOT_RN_IND     CHAR(1)      not null default 'n',
   PLCY_PRD_CONT_COV_IND      CHAR(1)      not null default 'n',
   PLCY_PRD_RN_TO_PLCY_IND    CHAR(1)      not null default 'n',
   PLCY_PRD_KEY_ACCT_IND      CHAR(1)      not null default 'n',
   PLCY_PRD_RTSP_PLMNRY_CALC_IND   CHAR(1)      not null default 'n',
   PLCY_PRD_RTSP_DFR_RVS_CALC_IND  CHAR(1) 	   not null default 'n',
   PLCY_PRD_RTSP_PLMNRY_VAL_DT	  TIMESTAMP,
   PLCY_PRD_NRN_DT	          TIMESTAMP,
   PLCY_NRN_RSN_TYP_CD        VARCHAR(15), 
   INVC_DD_TYP_CD             VARCHAR(15),
   LDD_ID				  	  NUMERIC(31),
   SHR_TRM_RSN_TYP_CD	      VARCHAR(15),
   PLCY_PRD_EXTRNL_PLCY_NO    VARCHAR(30),
   PLCY_PRD_DO_NOT_CALC_PREM_IND CHAR(1)   not null default 'n',
   PLCY_PRD_DESC_OF_OPER      VARCHAR(4000),
   PLCY_PRD_RTSP_MNL_IND	  CHAR(1) 	   not null default 'n',
   CAR_COMS_USED_IND 	      CHAR(1) 	   not null default 'n',
   AUDIT_USER_ID_CREA         NUMERIC(31),
   AUDIT_USER_CREA_DTM        TIMESTAMP,
   AUDIT_USER_ID_UPDT         NUMERIC(31),
   AUDIT_USER_UPDT_DTM        TIMESTAMP,
   VOID_IND                   CHAR(1)      not null default 'n',
   constraint XPK_PLCYP primary key (PLCY_PRD_ID)
);


COMMENT ON TABLE POLICY_PERIOD IS 'This data table captures specific period level information for a policy.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN POLICY_PERIOD.AGRE_ID IS 'This is a foreign key to the agreement and policy tables defining what policy this information is for.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_NO IS 'This is an alphanumeric field representing the carriers defined policy number.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_NO_1 IS 'This is a numeric field representing the submission number created during the quote creation.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_EFF_DT IS 'This is the date that this policy period became effective.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_END_DT IS 'This is the date that the policy period is no longer in effect.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_ORIG_END_DT IS 'This is the original end date of the policy period or the original date that the policy period was no longer in effect.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_PRO_RT_IND IS 'This is an indicator representing whether or not the premium for this policy period should be pro-rated or not.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_RECALC_E_PREM_IND IS 'This is an indicator representing whether or not the estimated premium for this policy period should be recalculated or not.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_RECALC_R_PREM_IND IS 'This is an indicator representing whether or not the reported premium for this policy period should be recalculated or not.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_RECALC_A_PREM_IND IS 'This is an indicator representing whether or not the audited premium for this policy period should be recalculated or not.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_RECALC_PFT_IND IS 'An indicator specifying whether or not financial transactions need to be re-evaluated/updated.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_DO_NOT_RN_IND IS 'This is an indicator representing whether or not the policy can be renewed into a new period from this period.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_CONT_COV_IND IS 'This is an indicator indicating continuous coverage';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_RN_TO_PLCY_IND IS 'This is an indicator representing whether or not the policy is to be renewed to a quote or policy from this period.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_KEY_ACCT_IND IS 'This is an indicator representing whether or not the policy is to identified as a key account (usually above a certain premium threshold) for this period.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_RTSP_PLMNRY_CALC_IND IS 'This is an indicator representing whether the Preliminary Calculation set for this period.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_RTSP_DFR_RVS_CALC_IND IS 'This is an indicator representing whether the Deferred Revised Calculation set for this period.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_RTSP_PLMNRY_VAL_DT IS 'This is an indicator representing whether the Preliminary Calculation Valuation Date for this period.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_NRN_DT IS 'The date specified as the non renewal date for the policy period based on notice period days.';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_NRN_RSN_TYP_CD IS 'This is a foreign key to the policy_non_rn_rsn table specifying the reason a policy is not to renew if the plcy_prd_do_not_rn_ind is set.';
COMMENT ON COLUMN POLICY_PERIOD.INVC_DD_TYP_CD IS 'This is a foreign key to the invoice_day_type table specifying which day of the month a policy is to invoice on for this period.';
COMMENT ON COLUMN POLICY_PERIOD.LDD_ID IS 'This is a foreign key to the large_deductible_detail table specifying the details for the large deductible rating element associated with this policy for this period.';
COMMENT ON COLUMN POLICY_PERIOD.SHR_TRM_RSN_TYP_CD is 'Foreign Key into the SHORT_TERM_REASON_TYPE table';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_EXTRNL_PLCY_NO IS 'The policy number that is not generated in the system and coming from an external source';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_DO_NOT_CALC_PREM_IND IS 'This is an indicator representing whether the premium needs to calculated or not';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_DESC_OF_OPER is 'Represents description of operation';
COMMENT ON COLUMN POLICY_PERIOD.PLCY_PRD_RTSP_MNL_IND is 'Represents if the policy period is manual retrospective or not';
COMMENT ON COLUMN POLICY_PERIOD.CAR_COMS_USED_IND is 'Represents if the policy period is using carrier commission or not';
COMMENT ON COLUMN POLICY_PERIOD.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY_PERIOD.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PERIOD.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY_PERIOD.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PERIOD.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table POLICY_PERIOD_HISTORY
(
   HIST_ID                    NUMERIC(31)  NOT NULL,	
   PLCY_PRD_ID                NUMERIC(31)  not null,
   AGRE_ID                    NUMERIC(31)  not null,
   PLCY_NO                    VARCHAR(30),
   PLCY_PRD_NO_1              VARCHAR(30),
   PLCY_PRD_EFF_DT            TIMESTAMP    not null,
   PLCY_PRD_END_DT            TIMESTAMP    not null,
   PLCY_PRD_ORIG_END_DT       TIMESTAMP,
   PLCY_PRD_PRO_RT_IND        CHAR(1)      not null default 'n',
   PLCY_PRD_RECALC_E_PREM_IND CHAR(1)      not null default 'n',
   PLCY_PRD_RECALC_R_PREM_IND CHAR(1)      not null default 'n',
   PLCY_PRD_RECALC_A_PREM_IND CHAR(1)      not null default 'n',
   PLCY_PRD_RECALC_PFT_IND    CHAR(1)      not null default 'n',
   PLCY_PRD_DO_NOT_RN_IND     CHAR(1)      not null default 'n',
   PLCY_PRD_CONT_COV_IND      CHAR(1)      not null default 'n',
   PLCY_PRD_RN_TO_PLCY_IND    CHAR(1)      not null default 'n',
   PLCY_PRD_KEY_ACCT_IND      CHAR(1)      not null default 'n',
   PLCY_PRD_RTSP_PLMNRY_CALC_IND   CHAR(1)      not null default 'n',
   PLCY_PRD_RTSP_DFR_RVS_CALC_IND  CHAR(1) 	   not null default 'n',
   PLCY_PRD_RTSP_PLMNRY_VAL_DT	  TIMESTAMP,
   PLCY_PRD_NRN_DT	          TIMESTAMP,
   PLCY_NRN_RSN_TYP_CD        VARCHAR(15), 
   INVC_DD_TYP_CD             VARCHAR(15),
   LDD_ID				  	  NUMERIC(31),
   SHR_TRM_RSN_TYP_CD	      VARCHAR(15),
   PLCY_PRD_EXTRNL_PLCY_NO    VARCHAR(30),
   PLCY_PRD_DO_NOT_CALC_PREM_IND        CHAR(1)      not null default 'n',
   PLCY_PRD_DESC_OF_OPER      VARCHAR(4000),
   PLCY_PRD_RTSP_MNL_IND	  CHAR(1) 	   not null default 'n',
   CAR_COMS_USED_IND 	  	  CHAR(1) 	   not null default 'n',
   AUDIT_USER_ID_CREA         NUMERIC(31),
