   AUDIT_USER_ID_UPDT         NUMERIC(31),
   AUDIT_USER_UPDT_DTM        TIMESTAMP,
   HIST_EFF_DTM 			  TIMESTAMP not null,
   HIST_END_DTM 			  TIMESTAMP,
   VOID_IND                   CHAR(1)      not null default 'n',
   constraint XPK_PLCYPH primary key (HIST_ID)
);

COMMENT ON TABLE POLICY_PERIOD_HISTORY IS 'This data table captures history for specific period level information for a policy.';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.HIST_ID IS 'Primary Key';   
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_ID IS 'Foreign key reference to policy_period table';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.AGRE_ID IS 'Foreign key reference to agreement table';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_NO IS 'Represents policy number';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_NO_1 IS 'This is a numeric field representing the submission number created during the quote creation.';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_EFF_DT IS 'Represents effective date';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_END_DT IS 'Represents end date';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_ORIG_END_DT IS 'Represents orifginal end date';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_PRO_RT_IND IS 'Pro rate indicator';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_RECALC_E_PREM_IND IS 'Recalculate estimated premium indicator';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_RECALC_R_PREM_IND IS 'Recalculate reported premium indicator';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_RECALC_A_PREM_IND IS 'Recalculate audited premium indicator';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_RECALC_PFT_IND  IS 'Recalculate PFT indicator';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_CONT_COV_IND  IS 'Continuous coverage indicator';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_RTSP_PLMNRY_CALC_IND IS 'Preliminary Calculation set indicator.';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_RTSP_DFR_RVS_CALC_IND IS 'Deferred Revised Calculation set indicator.';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_DO_NOT_RN_IND IS 'Do not renew indicator';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_RN_TO_PLCY_IND IS 'Renewal to policy indicator';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_KEY_ACCT_IND IS 'Policy period key account indicator';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_RTSP_PLMNRY_VAL_DT IS 'Policy period preliminary calculation valuation date';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_NRN_DT IS 'Policy period non renewal date';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_NRN_RSN_TYP_CD IS 'Policy period non renewal reason type';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.INVC_DD_TYP_CD IS 'Invoice date type';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.LDD_ID  IS 'Foreign key to table large_deductible_detail';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.SHR_TRM_RSN_TYP_CD IS 'Short term reason type';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_EXTRNL_PLCY_NO  IS 'External policy indicator';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_DO_NOT_CALC_PREM_IND IS 'Premium calculate indicator';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_DESC_OF_OPER is 'Represents description of operation';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.PLCY_PRD_RTSP_MNL_IND is 'Represents if the policy period is manual retrospective or not';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.CAR_COMS_USED_IND is 'Represents if the policy period is using carrier commission or not';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.AUDIT_USER_ID_CREA IS 'User id created';
COMMENT ON COLUMN POLICY_PERIOD_HISTORY.AUDIT_USER_CREA_DTM IS 'User created date timestamp';
