CREATE TABLE POLICY_PERIOD_LOSS_RATIO (
  PLCY_PRD_LOSS_RATIO_ID 		NUMERIC(31)      NOT NULL,
  PLCY_PRD_ID    	            NUMERIC(31)		 NOT NULL,
  PLCY_PRD_CALC_LOSS_RATIO      NUMERIC(15,2)    NOT NULL,
  PLCY_PRD_NET_LOSS_RATIO       NUMERIC(15,2)    NOT NULL,
  PLCY_PRD_ERN_PREM_AMT         NUMERIC(15,2)    NOT NULL,
  PLCY_PRD_PD_LOSS_AMT 			NUMERIC(15,2)    NOT NULL,
  PLCY_PRD_INC_LOSS_AMT         NUMERIC(15,2)    NOT NULL,
  PLCY_PRD_INS_SHR_INC_AMT      NUMERIC(15,2)    NOT NULL,
  PLCY_PRD_CALC_DTM 			TIMESTAMP        NOT NULL,
  PLCY_PRD_PREM_AMT			 	NUMERIC(15,2)    NOT NULL,
  PLCY_PRD_OPN_CLM_CNT		 	NUMERIC(31)      NOT NULL,
  PLCY_PRD_TOT_CLM_CNT		 	NUMERIC(31)      NOT NULL,
  CONSTRAINT XPK_PPLR PRIMARY KEY (PLCY_PRD_LOSS_RATIO_ID)
);


COMMENT ON TABLE POLICY_PERIOD_LOSS_RATIO is 'A table that is used to store the primary Loss Ratio calculation details when the batch Calculate Loss Ratio is run';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_LOSS_RATIO_ID is 'The primary key that uniquely identifies a row in this table';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_ID is 'it is a foreign key which references POLICY_PERIOD table';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_CALC_LOSS_RATIO is 'it is the proportionate relationship of incurred losses to earned premiums expressed as a ratio';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_NET_LOSS_RATIO is 'it can be defined as (Incurred Losses - Insureds Share of Incurred) / Earned Premium';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_ERN_PREM_AMT is 'it can be defined as (Written Premium - Earned Premium = Unearned Premium)';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_PD_LOSS_AMT is 'it is used to store paid losses amount';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_INC_LOSS_AMT is 'it will be calculated differently based on the system option Loss Ratio Includes Expenses';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_INS_SHR_INC_AMT is 'it is the sum of the Insureds Share of Paid and Insureds Share of Reserve';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_CALC_DTM is 'This is the Valuation Date of the calculation';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_PREM_AMT is 'Premium represents the payments made to an insurance company to cover the cost of insurance';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_OPN_CLM_CNT is 'Open Claims Count represents claims that are actively being reviewed or are receiving payments';
COMMENT ON COLUMN POLICY_PERIOD_LOSS_RATIO.PLCY_PRD_TOT_CLM_CNT is 'Total Claims Count represents the total number of accepted claims filed on a policy period';


