   PPPAGNT_COMM_EFF_DTM	    TIMESTAMP,
   PPPAGNT_COMM_END_DTM     TIMESTAMP,
   PPPAGNT_DOCM_RECV_IND   CHAR(1)                not null default 'n',
   constraint XPK_PPPAGNT primary key (PTCP_ID)
);


COMMENT ON TABLE POLICY_PERIOD_PTCP_AGNT IS 'This data table captures information about agent participation on a policy for a distinct policy period.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_AGNT.PTCP_ID IS 'This is the primary key as well as a foreign key to the policy_period_participation table specifying the specific participation for a policy period.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_AGNT.COMS_SCH_TYP_CD IS 'This is a foreign key to the commission_schedule_type table specifying the specific commission schedule applicable to this participation.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_AGNT.COMS_TYP_CD IS 'Foreign key, references COMS_TYP_CD from COMMISSION_TYPE table.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_AGNT.PPPAGNT_OVRRD_RN_IND IS 'This is an indicator specifying commission override renewal indicator for agent participation on a policy.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_AGNT.PPPAGNT_COMS_RT IS 'This is an override rate specified by the user to pre-empt the commission schedule rate for this participation.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_AGNT.PPPAGNT_COMM_RECV_IND IS 'This is an indicator specifying whether or not this agent receives the commission for this policy period.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_AGNT.PPPAGNT_COMM_EFF_DTM IS 'This is the date that this agent starts receiving commissions for the entire policy agreement.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_AGNT.PPPAGNT_COMM_END_DTM IS 'This is the date that this agent stops receiving commissions for the policy agreement.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_AGNT.PPPAGNT_DOCM_RECV_IND IS 'This is an indicator specifying whether or not this agent receives the documents for this policy period.';


create table POLICY_PERIOD_PTCP_INCL_EXCL
(
   PTCP_ID              NUMERIC(31)			   not null,
   COV_TYP_CD           VARCHAR(15)            not null,
   TTL_TYP_CD           VARCHAR(15),
   STT_ID               NUMERIC(31)            not null,
   PPPIE_OWN_PCT        NUMERIC(5,2),
   PPPIE_COV_IND        CHAR(1)                default 'n',
   PPPIE_RECV_PAY_IND   CHAR(1)                not null default 'n',
   PPPIE_RLTVE_IND      CHAR(1)                not null default 'n',
   PPPIE_HSE_EMPL_IND   CHAR(1)                not null default 'n',
   PPPIE_DTY_IND        CHAR(1)                not null default 'n',
   PPPIE_DTY_TXT        VARCHAR(2000),
   constraint XPK_PPPIE primary key (PTCP_ID)
);


COMMENT ON TABLE POLICY_PERIOD_PTCP_INCL_EXCL IS 'This data table captures information about included/excluded individual (aka corporate officer) participation on a policy for a distinct policy period.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_INCL_EXCL.PTCP_ID IS 'This is a foreign key to the policy_period_participation table specifying the specific participation for a policy period.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_INCL_EXCL.COV_TYP_CD IS 'This is a foreign key to the coverage_type table specifying the type of coverage that an included individual qualifies for.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_INCL_EXCL.TTL_TYP_CD IS 'This is a foreign key to the title_type table specifying the title of the included/excluded individual.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_INCL_EXCL.STT_ID IS 'This is a foreign key to the state table specifying the state that this included individual can be covered in.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_INCL_EXCL.PPPIE_OWN_PCT IS 'This is the percentage of ownership of the company covered by this policy that this individual holds.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_INCL_EXCL.PPPIE_COV_IND IS 'This is an indicator specifying whether or not this individual is covered under the policy or not.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_INCL_EXCL.PPPIE_RECV_PAY_IND IS 'This is an indicator specifying whether or not this individual receives pay in the company or not.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_INCL_EXCL.PPPIE_RLTVE_IND IS 'This is an indicator specifying whether or not this individual is a relative of an owner of the company or not.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_INCL_EXCL.PPPIE_HSE_EMPL_IND IS 'This is an indicator specifying whether or not this individual is classified as a household employee or not.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_INCL_EXCL.PPPIE_DTY_IND IS 'This is an indicator specifying whether or not this individual performs duties in the company or not.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_INCL_EXCL.PPPIE_DTY_TXT IS 'This is a user entered description of the duties performed by the individual if they do perform duties in the company.';

create table POLICY_PERIOD_PTCP_INS
(
