COMMENT ON TABLE POLICY_PERIOD_PTCP_OCIP_LOC IS 'This data table captures information about OCIP location participation on a policy for a distinct policy period.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.PPPOL_ID IS 'This is a foreign key to the policy period ptcp ocip loc table defining the specific policy period ptcp ocip loc record that this record pertains to.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.PTCP_ID IS 'This is a foreign key to the policy_period_participation table specifying the specific participation for a policy period.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.PPPOL_ST IS 'The street address of the OCIP location.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.PPPOL_CITY IS 'The city in which the OCIP location is located.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.PPPOL_STT IS 'The state in which the OCIP location is located.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.PPPOL_POST_CD IS 'The zip (postal) code for the address of the OCIP location.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.PPPOL_EFF_DT IS 'The date that the OCIP locations address became effective.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.PPPOL_END_DT IS 'The date that the OCIP locations address is no longer in effect.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_OCIP_LOC.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table POLICY_PERIOD_PTCP_WVR_SRGT
(
   PTCP_ID              NUMERIC(31)				NOT NULL,
   WVR_TYP_CD           VARCHAR(15)            not null,
   PLCY_PRD_PTCP_WVR_SRGT_DESC VARCHAR(2000),
   PLCY_PRD_PTCP_WVR_SRGT_PCT_AMT NUMERIC(5,2),
   constraint XPK_PPPWS primary key (PTCP_ID)
);


COMMENT ON TABLE POLICY_PERIOD_PTCP_WVR_SRGT IS 'This data table captures information about waiver of subrogation participation on a policy for a distinct policy period.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_WVR_SRGT.PTCP_ID IS 'This is a foreign key to the policy_period_participation table specifying the specific participation for a policy period.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_WVR_SRGT.WVR_TYP_CD IS 'This is a foreign key to the waiver_type table specifying the specific type of waiver associated to this participant.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_WVR_SRGT.PLCY_PRD_PTCP_WVR_SRGT_DESC IS 'This is a user entered description of the waiver of subrogation agreement.';
COMMENT ON COLUMN POLICY_PERIOD_PTCP_WVR_SRGT.PLCY_PRD_PTCP_WVR_SRGT_PCT_AMT IS 'This is the percentage amount agreed to in the waiver of subrogation agreement.';

create table POLICY_PERIOD_RATING_ELEMENT (
