   PPRE_END_DT                   TIMESTAMP        NOT NULL,
   PPRE_RT                       NUMERIC(15,6),
   WC_CLS_RT_TIER_TYP_CD         VARCHAR(15),
   RT_ELEM_DED_TYP_ID            NUMERIC(31),
   EL_FELA_TYP_CD                VARCHAR(15),
   EMPLR_LIAB_TYP_CD             VARCHAR(15),
   EXPRN_MOD_ID                  NUMERIC(31),
   RISK_GRP_SUFX_TYP_CD          VARCHAR(15),
   PPRE_ARD_DT                   TIMESTAMP,
   PPRE_ARD_OVRRD_IND            CHAR(1),
   LDD_ID                        NUMERIC(31),
   DVND_TYP_CD					 VARCHAR(15),
   PPRE_DVND_OVRD_AMT			 NUMERIC(15,2),
   PPRE_DVND_OVRD_PCT			 NUMERIC(15,2),	
   PPRE_RTSP_TAX_MLTPLR          NUMERIC(15,6),    
   PPRE_RTSP_EXCS_LOSS_PREM_FCTR NUMERIC(15,6), 
   PPRE_RTSP_OVRRD_FST_DEV_FCTR  NUMERIC(15,4),
   PPRE_RTSP_OVRRD_SCND_DEV_FCTR NUMERIC(15,4), 
   PPRE_RTSP_OVRRD_THRD_DEV_FCTR NUMERIC(15,4),
   PPRE_RTSP_INDM_CLM_FEE 		 NUMERIC(15,6),	
   PPRE_RTSP_MED_ONLY_CLM_FEE 	 NUMERIC(15,6),
   PPRE_RTSP_ICDN_CLM_FEE 		 NUMERIC(15,6),
   PPRE_DED_PREM_BS_AMT          NUMERIC(15,2),
   PPRE_DED_AGG_AMT              NUMERIC(15,2),
   PPRE_RN_IND                   CHAR(1)          DEFAULT 'y',
   WKSHT_ID                      NUMERIC(31),
