COMMENT ON TABLE POLICY_PERIOD_RELATIONSHIP IS 'This data table captures relationships between two policies at the policy period level.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.PLCY_PRD_RLT_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.PLCY_PRD_RLT_ID_RLT IS 'This is a self-join foreign key used in multi-tiered relationships between policies (NOTE : Not implemented in this version of the software).';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.PLCY_PRD_ID IS 'This is a foreign key to the policy_period table specifying which policy/period this record is associated with.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.PLCY_PRD_ID_RLT IS 'This is a foreign key to the policy_period table specifying which policy/period this record is associated with.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.PLCY_PRD_RLT_TYP_ID IS 'This is a foreign key to the policy period relationship type table defining the specific policy period relationship type record that this record pertains to.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.PLCY_PRD_RLT_EFF_DT IS 'The date that this relationship became effective.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.PLCY_PRD_RLT_END_DT IS 'The date that this relationship is no longer in effect.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.PLCY_PRD_RLT_RVRS_IND IS 'An indicator specifying whether or not the relationship exists in a "reverse" context.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN POLICY_PERIOD_RELATIONSHIP.PLCY_PRD_RLT_NOTE_IND IS 'Indicator saying that a note exists that is associated with this relationship.';


create table POLICY_PERIOD_RLT_HIST
(
   HIST_ID 				 NUMERIC(31) NOT NULL,
   PLCY_PRD_RLT_ID       NUMERIC(31) NOT NULL,
   PLCY_PRD_RLT_ID_RLT   NUMERIC(31),
