   TASK_LNK_ID          NUMERIC(31),
   USER_ID              NUMERIC(31),
   COND_ID              NUMERIC(31),
   FOLD_ID              NUMERIC(31),
   TASK_COMT_REQD_IND        CHAR(1) not null default 'n',
   TASK_COMT            VARCHAR(4000),
   TASK_EMAIL_EVNT_ID	VARCHAR(500),
   AUDIT_USER_ID_CREA   NUMERIC(31),
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   VOID_IND             CHAR(1)                not null,
   constraint XPK_TASK primary key (TASK_ID)
);


COMMENT ON TABLE TASK IS 'This data table contains all workflow tasks created by the system or by a user.';
COMMENT ON COLUMN TASK.TASK_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN TASK.TASK_TYP_CD IS 'This is a foreign key to the task_type table that identifies the source of the task, was it created by the system as the result of an event or was it created online by a user.';
COMMENT ON COLUMN TASK.TASK_STS_TYP_CD IS 'This is a foreign key to the task_status_type table that identifies the current status of the task.';
COMMENT ON COLUMN TASK.TASK_PR_STS_TYP_CD IS 'This is a foreign key to the task_status_type table that identifies the previous status of the task.';
COMMENT ON COLUMN TASK.TASK_PR_ESCL_DT IS 'The previous escalation date.';
COMMENT ON COLUMN TASK.TASK_ESCL_TO_SUPR_IND IS 'An indicator that identifies whether or not the task should escalate to supervisor upon reaching overdue priority.';
COMMENT ON COLUMN TASK.TASK_NM IS 'A user friendly name to describe the task.  It is dynamically constructed based on task template information and functions as a short description of the task.';
COMMENT ON COLUMN TASK.TASK_NM_DRV_UPCS_NM IS 'The derived uppercase name of the task.';
COMMENT ON COLUMN TASK.TASK_DESC IS 'A long description descriptions for the task.  Dynamically constructed based on task template information.';
COMMENT ON COLUMN TASK.TASK_TMPL_ID IS 'This is a foreign key to the task_template table that identifies the task template that was used to create the task.';
COMMENT ON COLUMN TASK.TASK_DUE_DT IS 'The task due date.  It is derived based on the escalation instructions and priority of the task.';
COMMENT ON COLUMN TASK.TASK_DT IS 'The task work date. The date the task should be worked on.';
COMMENT ON COLUMN TASK.TASK_PRTY_TYP_CD IS 'This is a foreign key to the task_priority_type table that identifies the current priority of the task.  It may be moved up automatically due to escalation.';
COMMENT ON COLUMN TASK.APP_CNTX_TYP_CD IS 'This is a foreign key to the application_context_type table that identifies the primary context of the task.  This is used to determine the tasks folder (in combination with the task_cntx_id).';
COMMENT ON COLUMN TASK.APP_SUB_CNTX_TYP_CD IS 'This is a foreign key to the application_sub_context_type table that identifies the sub-context of the task (if one exists).  Identifies a particular record with the primary context (e.g. bill within claim).';
COMMENT ON COLUMN TASK.TASK_CNTX_ID IS 'Used to store the surrogate key for the primary context (e.g. agre_id for claim, plcy_prd_id for policy period).';
COMMENT ON COLUMN TASK.TASK_SUB_CNTX_ID IS 'Used to stores the surrogate key for the sub-context (if applicable) (e.g. bill_id).';
COMMENT ON COLUMN TASK.TASK_PND_RSN IS 'The reason for pending the task.';
COMMENT ON COLUMN TASK.TASK_USER_MOD_IND IS 'An indicator that identifies whether or not the task is modifiable by a user.';
COMMENT ON COLUMN TASK.APP_DTL_LVL_CD IS 'Not used.';
COMMENT ON COLUMN TASK.ORG_UNT_ID IS 'This is a foreign key to the organizational_unit table that identifies the team the task is assigned to.  Mutually exclusive with user_id (i.e. either the task is assigned to a user or a team, not both).';
COMMENT ON COLUMN TASK.TASK_LNK_ID IS 'This is a foreign key to the task table that identifies another task which is linked to this task.';
COMMENT ON COLUMN TASK.USER_ID IS 'This is a foreign key to the users table that identifies the user the task is assigned to.  Mutually exclusive with org_unt_id (i.e. either the task is assigned to a user or a team, not both).';
COMMENT ON COLUMN TASK.COND_ID IS 'This is a foreign key to the condition table that identifies the task completed condition, that is, under what conditions may the task be assumed complete.';
COMMENT ON COLUMN TASK.FOLD_ID IS 'This is a foreign key to the folder table that identifies the folder within which the task resides.';
COMMENT ON COLUMN TASK.TASK_COMT_REQD_IND IS 'This is an indicator identifying whether or not a comment is required on the task';
COMMENT ON COLUMN TASK.TASK_COMT IS 'This represents the comments made for a task';
COMMENT ON COLUMN TASK.TASK_EMAIL_EVNT_ID IS 'An id of a outlook Calendar item to use later when task is reassigned or completed.';
COMMENT ON COLUMN TASK.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN TASK.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN TASK.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN TASK.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN TASK.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
comment on column TASK.TASK_CTG_APP_CNTX_XREF_ID is 'Task category application context xref Id';

create table TASK_ESCALATION
(
   TASK_ESCL_ID         NUMERIC(31)            not null,

   TASK_ESCL_DD         NUMERIC(5),
   TASK_PRTY_TYP_CD     VARCHAR(15),
   TASK_ID              NUMERIC(31),
  AUDIT_USER_ID_CREA numeric(31,0),
AUDIT_USER_CREA_DTM timestamp,
AUDIT_USER_ID_UPDT numeric(31,0) , 
AUDIT_USER_UPDT_DTM timestamp, 
   VOID_IND             CHAR(1),
   constraint XPK_TE primary key (TASK_ESCL_ID)
);


COMMENT ON TABLE TASK_ESCALATION IS 'This data table provides escalation instructions to the task.  There should be a record for each priority for each task.';
COMMENT ON COLUMN TASK_ESCALATION.TASK_ESCL_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN TASK_ESCALATION.TASK_ESCL_DD IS 'The number of days to priority escalation for the task for each priority.';
COMMENT ON COLUMN TASK_ESCALATION.TASK_PRTY_TYP_CD IS 'This is a foreign key to the task_priority_type table that identifies the priority for which the associated days is relevant.';
COMMENT ON COLUMN TASK_ESCALATION.TASK_ID IS 'This is a foreign key to the task table that identifies the task for which the escalation instructions apply.';
COMMENT ON COLUMN TASK_ESCALATION.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN TASK_ESCALATION.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN TASK_ESCALATION.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN TASK_ESCALATION.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN TASK_ESCALATION.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table TASK_PRIORITY_TYPE
(
   TASK_PRTY_TYP_CD     VARCHAR(15)            not null,
   TASK_PRTY_TYP_NM     VARCHAR(80) NOT NULL,
   VOID_IND             CHAR(1)                not null,
   constraint XPK_TPT primary key (TASK_PRTY_TYP_CD)
);


COMMENT ON TABLE TASK_PRIORITY_TYPE IS 'This is a code table which contains all possible priorities for a task (e.g. low, medium, high, urgent, overdue).';
COMMENT ON COLUMN TASK_PRIORITY_TYPE.TASK_PRTY_TYP_CD IS 'A unique code value to identify the specific task priority.';
COMMENT ON COLUMN TASK_PRIORITY_TYPE.TASK_PRTY_TYP_NM IS 'A user friendly name to describe the task priority.';
COMMENT ON COLUMN TASK_PRIORITY_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table TASK_STATUS_TYPE
(
   TASK_STS_TYP_CD      VARCHAR(15)            not null,
   TASK_STS_TYP_NM      VARCHAR(80) NOT NULL,
   VOID_IND             CHAR(1)                not null,
   constraint XPK_TST primary key (TASK_STS_TYP_CD)
);


COMMENT ON TABLE TASK_STATUS_TYPE IS 'This is a code table which contains all possible statuses for a task (e.g. new, active, pending, completed).';
COMMENT ON COLUMN TASK_STATUS_TYPE.TASK_STS_TYP_CD IS 'A unique code value to identify the specific task status.';
COMMENT ON COLUMN TASK_STATUS_TYPE.TASK_STS_TYP_NM IS 'A user friendly name to describe the task priority.';
COMMENT ON COLUMN TASK_STATUS_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table TASK_CATEGORY_TYPE
(
   TASK_CTG_TYP_CD      VARCHAR(15)            not null,
   TASK_CTG_TYP_NM      VARCHAR(80) NOT NULL,
   VOID_IND             CHAR(1)                not null default 'n',
   constraint XPK_TASKCT primary key (TASK_CTG_TYP_CD)
);


comment on table  TASK_CATEGORY_TYPE is 'Task category types';
comment on column TASK_CATEGORY_TYPE.TASK_CTG_TYP_CD is 'Task category type code';
comment on column TASK_CATEGORY_TYPE.TASK_CTG_TYP_NM is 'Task category type name';
comment on column TASK_CATEGORY_TYPE.VOID_IND is 'Void indicator';


create table TASK_TYPE
(
   TASK_TYP_CD          VARCHAR(15)            not null,
   TASK_TYP_NM          VARCHAR(80) NOT NULL,
   VOID_IND             CHAR(1)                not null,
   constraint XPK_TTYP primary key (TASK_TYP_CD)
);


COMMENT ON TABLE TASK_TYPE IS 'This is a code table which contains all possible task types (e.g. user or system).';
COMMENT ON COLUMN TASK_TYPE.TASK_TYP_CD IS 'A unique code value to identify the specific type of task.';
COMMENT ON COLUMN TASK_TYPE.TASK_TYP_NM IS 'A user friendly name to describe the type of task.';
COMMENT ON COLUMN TASK_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table TAX_IDENTIFIER
(
   TAX_ID_ID            NUMERIC(31)            not null,
   CUST_ID              NUMERIC(31)            not null,
   TAX_ID_TYP_CD        VARCHAR(15)            not null,
   TAX_ID_NO            VARCHAR(30)                    ,
   TAX_ID_DRV_UPCS_NO   VARCHAR(30)                    ,
   TAX_ID_SEQ_NO        VARCHAR(4),
   TAX_ID_EFF_DT        TIMESTAMP,
   TAX_ID_END_DT        TIMESTAMP,
   TAX_ID_USE_FOR_1099_IND CHAR(1)        NOT NULL DEFAULT 'n',
   AUDIT_USER_ID_CREA   NUMERIC(31)            not null,
   AUDIT_USER_CREA_DTM  TIMESTAMP              not null,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   VOID_IND      CHAR(1)                not null default 'n',
   constraint XPK_TI primary key (TAX_ID_ID)
);


COMMENT ON TABLE TAX_IDENTIFIER IS 'This data table contains the tax identifiers associated to a customer.';
COMMENT ON COLUMN TAX_IDENTIFIER.TAX_ID_ID IS 'This is a foreign key from the tax_identifier table.';
COMMENT ON COLUMN TAX_IDENTIFIER.CUST_ID IS 'This is a foreign key from the customer table represents the customer the tax identifier is associated to.';
COMMENT ON COLUMN TAX_IDENTIFIER.TAX_ID_TYP_CD IS 'This is a foriegn key from the tax_identifier_type table defining the type of the tax identifier.';
COMMENT ON COLUMN TAX_IDENTIFIER.TAX_ID_NO IS 'This is the number of the tax identifier for the customer.';
COMMENT ON COLUMN TAX_IDENTIFIER.TAX_ID_DRV_UPCS_NO IS 'This is the upper case version of the tax_id_no used in the customer search.';
COMMENT ON COLUMN TAX_IDENTIFIER.TAX_ID_SEQ_NO IS 'This is the tax identifier sequence number.';
COMMENT ON COLUMN TAX_IDENTIFIER.TAX_ID_EFF_DT IS 'This date represents the date the tax identifier is efftective.';
COMMENT ON COLUMN TAX_IDENTIFIER.TAX_ID_END_DT IS 'This date represents the date the tax identifier expires.';
Comment on column TAX_IDENTIFIER.TAX_ID_USE_FOR_1099_IND is 'For Business Customer - Indicator Used for 1099';
COMMENT ON COLUMN TAX_IDENTIFIER.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN TAX_IDENTIFIER.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN TAX_IDENTIFIER.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN TAX_IDENTIFIER.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN TAX_IDENTIFIER.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table TAX_IDENTIFIER_TYPE
(
   TAX_ID_TYP_CD        VARCHAR(15)            not null,
   TAX_ID_TYP_NM        VARCHAR(80)            not null,
   TAX_ID_1099_TYP_CD   VARCHAR(15)            ,
   TAX_ID_TYP_VOID_IND  CHAR(1)                not null default 'n',
   constraint XPK_TIT primary key (TAX_ID_TYP_CD)
);


COMMENT ON TABLE TAX_IDENTIFIER_TYPE IS 'This code table contains the list of tax identifier types such as SSN and FIEN.';
COMMENT ON COLUMN TAX_IDENTIFIER_TYPE.TAX_ID_TYP_CD IS 'A unique code value to identify the specific type of a tax identifier.';
COMMENT ON COLUMN TAX_IDENTIFIER_TYPE.TAX_ID_TYP_NM IS 'A user friendly name to describe the type of a tax filing identifier.';
Comment on COLUMN TAX_IDENTIFIER_TYPE.TAX_ID_1099_TYP_CD is 'Identifies the 1099 code corresponding to the Tax Identifier Type used for 1099 reporting';
COMMENT ON COLUMN TAX_IDENTIFIER_TYPE.TAX_ID_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table TITLE_TYPE
(
   TTL_TYP_CD           VARCHAR(15)            not null,
   TTL_TYP_NM           VARCHAR(80)            not null,
   TTL_TYP_VOID_IND     CHAR(1)                not null default 'n',
   constraint XPK_TT primary key (TTL_TYP_CD)
);


COMMENT ON TABLE TITLE_TYPE IS 'This is a code table which defines the types of titles for employees which can be used in the system.';
COMMENT ON COLUMN TITLE_TYPE.TTL_TYP_CD IS 'A unique code value to identify the specific type of title.';
COMMENT ON COLUMN TITLE_TYPE.TTL_TYP_NM IS 'A user friendly name to describe the type of title.';
COMMENT ON COLUMN TITLE_TYPE.TTL_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table USERS
(
   USER_ID              NUMERIC(31)            not null,
   CNCURNCY_ID     	NUMERIC(31)  DEFAULT 1 NOT NULL,
   USER_TYP_CD          VARCHAR(15),
   USER_LGN_NM          VARCHAR(80),
   USER_LGN_NM_SECD     VARCHAR(80),
   USER_PSWD            VARCHAR(80),
   USER_NM_FST          VARCHAR(40),
   USER_NM_MID          CHAR(1),
   USER_NM_LST          VARCHAR(120),
   USER_NM_RSRC         VARCHAR(80),
   USER_DRV_UPCS_NM     VARCHAR(162)  not null,
   USER_TTL             VARCHAR(50),
   USER_PHN_CNTRY_CD           VARCHAR(20),
   USER_PHN_DIAL_CD           VARCHAR(20),
   USER_PH_NO           VARCHAR(20),
   USER_PH_EXT_NO       VARCHAR(5),
   USER_EMAIL           VARCHAR(80), 
   USER_LOGIN_ATTMPT_CNT NUMERIC(3),
   USER_LOGIN_SUSPD_DTM TIMESTAMP, 
   USER_EFF_DT          TIMESTAMP                   not null,
   USER_END_DT          TIMESTAMP,
   PSWD_EFF_DT          TIMESTAMP 	       not null,
   USER_SUPR_ID         NUMERIC(31),
   USER_SUPR_IND        CHAR(1)                not null default 'n',
   USER_WKFLW_ELIG_IND  CHAR(1)                not null default 'y',
   USER_WKFLW_EMAIL_IND CHAR(1)                not null default 'n',
   USER_CSWC_ACCS_IND 	CHAR(1)                not null default 'y',
   USER_SGNTR_FILE_NM   VARCHAR(80),
   TIME_ZN_CD_USER      VARCHAR(15),
   SMS_KEY           	VARCHAR(80),
   USER_CAL_EVNT_OPT_IN_IND	CHAR(1) NOT NULL DEFAULT 'n',
   AUDIT_USER_ID_CREA   NUMERIC(31)            not null,
   AUDIT_USER_CREA_DTM  TIMESTAMP                   not null,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   USER_VOID_IND        CHAR(1)                not null default 'n',
   USER_WEB_IND        CHAR(1)                not null default 'n',
   constraint XPK_USER primary key (USER_ID)
);


COMMENT ON TABLE USERS IS 'This a code table which contains complete set of all possible users of the application.';
COMMENT ON COLUMN USERS.USER_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN USERS.USER_TYP_CD IS 'This is a foreign key to the users_type table that identifies whether the user is internal (local access) or external (web access).';
COMMENT ON COLUMN USERS.USER_LGN_NM IS 'The login name for the user.  Name entered when logging onto the application.';
COMMENT ON COLUMN USERS.USER_LGN_NM_SECD IS 'The secondary login name for the user.  Name entered when logging onto secondary application.';
COMMENT ON COLUMN USERS.USER_PSWD IS 'The password for the user.';
COMMENT ON COLUMN USERS.USER_NM_FST IS 'The users first name.';
COMMENT ON COLUMN USERS.USER_NM_MID IS 'The users middle initial.';
COMMENT ON COLUMN USERS.USER_NM_LST IS 'The users last name.';
COMMENT ON COLUMN USERS.USER_DRV_UPCS_NM IS 'A derived uppercase user name search field.  Used to allow an indexed search on the combined user name.';
COMMENT ON COLUMN USERS.USER_TTL IS 'The users title description.';
COMMENT ON COLUMN USERS.USER_PH_NO IS 'The users phone number.';
COMMENT ON COLUMN USERS.USER_PHN_CNTRY_CD IS 'The users country calling code';
COMMENT ON COLUMN USERS.USER_PHN_DIAL_CD IS 'The users country dial code';
COMMENT ON COLUMN USERS.USER_PH_EXT_NO IS 'The users phone extension.';
COMMENT ON COLUMN USERS.USER_EMAIL IS 'The users email address.';
COMMENT ON COLUMN USERS.USER_LOGIN_ATTMPT_CNT IS 'The number of failed login attempts for the user.';
COMMENT ON COLUMN USERS.USER_LOGIN_SUSPD_DTM IS 'The date the user was suspended due to failed login attempts.';
COMMENT ON COLUMN USERS.USER_EFF_DT IS 'The date the user was granted access to the application.';
COMMENT ON COLUMN USERS.USER_END_DT IS 'The date the user was denied access to the application.';
COMMENT ON COLUMN USERS.USER_SUPR_ID IS 'This is a foreign key to the users table that identifies the users supervisor.';
COMMENT ON COLUMN USERS.USER_SUPR_IND IS 'An indicator that identifies whether or not the user is a supervisor of other users.';
COMMENT ON COLUMN USERS.USER_WKFLW_EMAIL_IND IS 'An indicator that identifies whether or not the user wants to be notified via e-mail when new tasks are assigned to this user.';
COMMENT ON COLUMN USERS.USER_CSWC_ACCS_IND IS 'An indicator that identifies whether or not the user has access to Coresuite.';
COMMENT ON COLUMN USERS.USER_CAL_EVNT_OPT_IN_IND IS 'An indicator that identifies whether this user is opted-in for outlook calendar integration.';
COMMENT ON COLUMN USERS.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN USERS.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN USERS.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN USERS.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN USERS.USER_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN USERS.USER_WEB_IND IS 'An indicator identifying whether the record is a portal user or not';


create table USERS_CUSTOMER_ACCESS_XREF
(
   USER_CUST_ACCS_XREF_ID NUMERIC(31)            not null,
   APP_CNTX_TYP_CD        VARCHAR(15),
   USER_ID              NUMERIC(31),
   CUST_ID              NUMERIC(31),
   PTCP_TYP_CD          VARCHAR(15),
   CASE_PTCP_TYP_CD	    VARCHAR(30),
   RLT_TYP_CD           VARCHAR(15),
   UCAX_CRS_CNTXT_IND CHAR(1) NOT NULL DEFAULT 'n',
   USER_CUST_ACCS_XREF_EFF_DT TIMESTAMP                   not null,
   USER_CUST_ACCS_XREF_END_DT TIMESTAMP,
   AUDIT_USER_ID_CREA   NUMERIC(31)            not null,
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   VOID_IND             CHAR(1)                not null default 'n',
   STATUS       VARCHAR(15),
   STATUS_COMMENT       VARCHAR(500),
   constraint XPK_UCAX primary key (USER_CUST_ACCS_XREF_ID)
);


COMMENT ON TABLE USERS_CUSTOMER_ACCESS_XREF IS 'This is a code table which contains a cross-reference associating external users with customers they access for.  For instance, a single user may access on behaf of a set of insured customers.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.USER_CUST_ACCS_XREF_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.USER_ID IS 'This is a foreign key to the users table that identifies the external user who has access for the associated customer. ';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.CUST_ID IS 'This is a foreign key to the customer table that identifies a customer for whom the associated user has access.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.PTCP_TYP_CD IS 'This is a foreign key to the participation_type table that identifies the manner in which the user has access to the customer.  For example, as an insured on the policy.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.USER_CUST_ACCS_XREF_EFF_DT IS 'The date the customer access became effective.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.USER_CUST_ACCS_XREF_END_DT IS 'The date the customer access expired.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.APP_CNTX_TYP_CD IS 'This is a foreign key to the application context type table that identifies the external user who has access for the associated context.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.CASE_PTCP_TYP_CD IS 'This is a foreign key to the case_participation_type table that identifies the way the user has access to the customer.  For example, as an third party insured on the policy.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.RLT_TYP_CD IS 'This is a foreign key to relationship_type table, specifying the valid relationship type for a given customer role type.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.UCAX_CRS_CNTXT_IND IS 'An indicator identifying whether the external user has access to cross contexts.';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.STATUS IS 'This is a foreign key to the USERS_CUST_ACCS_STS_TYP table that identifies the status of the portal user- it could be pending, approved or denied ';
COMMENT ON COLUMN USERS_CUSTOMER_ACCESS_XREF.STATUS_COMMENT IS 'This is a field which would be user entered and may have the reason for the status being declined';

create table USERS_TYPE
(
   USER_TYP_CD          VARCHAR(15)            not null,
   USER_TYP_NM          VARCHAR(80)            not null,
   VOID_IND             CHAR(1)                not null default 'n',
   constraint XPK_UT primary key (USER_TYP_CD)
);


COMMENT ON TABLE USERS_TYPE IS 'This is a code table which contains possible user types (e.g. internal, external).  Internal represents users operating on the local network.  External represents web users who have been granted access on a per customer basis.';
COMMENT ON COLUMN USERS_TYPE.USER_TYP_CD IS 'A unique code value to identify the specific type of user.';
COMMENT ON COLUMN USERS_TYPE.USER_TYP_NM IS 'A user friendly name to describe the type of user.';
COMMENT ON COLUMN USERS_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table WC_CLAIM
(
   WC_CLM_ID            NUMERIC(31)            not null,
   AGRE_ID              NUMERIC(31) not null,
   WC_CLM_SAL_CONT_IND  CHAR(1)                default 'n',
   WC_CLM_EQMT_MTRL_CHEM_USD_DESC VARCHAR(500),
   WC_CLM_SAFE_EQMT_PROV_IND CHAR(1)                default 'n',
