COMMENT ON TABLE WC_CLAIM IS 'This table holds the additional claims details for the claim along with the CLAIM table.';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_ID IS 'Surrogate primary key identifier for the WC_Claim table.';
COMMENT ON COLUMN WC_CLAIM.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN WC_CLAIM.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN WC_CLAIM.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN WC_CLAIM.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN WC_CLAIM.INJR_TYP_CD IS 'The injury type code of the claim from the injury_type table';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_WRK_SUN_IND IS 'The specific days in the week that the employee regularly works -indicate if the employee works on Sunday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_WRK_THU_IND IS 'The specific days in the week that the employee regularly works -indicate if the employee works on Thursday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_WRK_TUE_IND IS 'The specific days in the week that the employee regularly works -indicate if the employee works on Tuesday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_SAFE_EQMT_PROV_IND IS 'Indicator showing that safety equipment had been provided';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_RVW_DT IS 'The Date the claim is to be reviewed';
COMMENT ON COLUMN WC_CLAIM.AGRE_ID IS 'Surrogate primary key identifier for the agreement table.';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_CLMT_USLH_IND IS 'Indicates that the claim is a USLH claim';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_RVW_INTV IS 'The interval of time that is to be used to determine the next review date';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_EQMT_MTRL_CHEM_USD_DESC IS 'Description of the equipment, material, chemicals used';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_WRK_MON_IND IS 'The specific days in the week that the employee regularly works -indicate if the employee works on Monday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_CLMT_BL_IND IS 'Indicator that the claim is a black lung claim';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_WRK_SAT_IND IS 'The specific days in the week that the employee regularly works -indicate if the employee works on Saturday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_WRK_WED_IND IS 'The specific days in the week that the employee regularly works -indicate if the employee works on Wednesday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_SAFE_EQMT_USED_IND IS 'Indicator showing that safety equipment had been used';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_DD_WK_NO IS 'The number of days worked per period (week if paid weekly or month, if paid monthly)';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_INSRD_DD_WK_NO IS 'The number of days insured worked per period (week if paid weekly or month, if paid monthly)';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_INSRD_WRK_MON_IND IS 'The specific days in the week that the insured regularly works -indicate if the insured works on Monday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_INSRD_WRK_TUE_IND IS 'The specific days in the week that the insured regularly works -indicate if the insured works on Tuesday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_INSRD_WRK_WED_IND IS 'The specific days in the week that the insured regularly works -indicate if the insured works on Wednesday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_INSRD_WRK_THU_IND IS 'The specific days in the week that the insured regularly works -indicate if the insured works on Thursday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_INSRD_WRK_FRI_IND IS 'The specific days in the week that the insured regularly works -indicate if the insured works on Friday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_INSRD_WRK_SAT_IND IS 'The specific days in the week that the insured regularly works -indicate if the insured works on Saturday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_INSRD_WRK_SUN_IND IS 'The specific days in the week that the insured regularly works -indicate if the insured works on Sunday';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_SAL_CONT_IND IS 'Indicator showing that the claimants salary is continuing to be paid';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_INJR_WHILE_IN_EMPL_IND IS 'Indicates that the claimant was employed when the injury occurred';
COMMENT ON COLUMN WC_CLAIM.WC_CLM_WRK_FRI_IND IS 'The specific days in the week that the employee regularly works -indicate if the employee works on Friday';
COMMENT ON COLUMN WC_CLAIM.WRK_WK_TYP_CD IS 'Represents the work week type of the claimant';

create table WC_CLASS
(
   WC_CLS_ID            NUMERIC(31)            not null,
   CNCURNCY_ID     	NUMERIC(31)  DEFAULT 1 NOT NULL,
   WC_CLS_CLS_CD        VARCHAR(15),
   JUR_TYP_CD           VARCHAR(15),
   WC_CLS_TYP_CD        VARCHAR(15),
   INDST_SCH_TYP_CD     VARCHAR(15),
   INDST_GRP_TYP_CD     VARCHAR(15),
   PREM_BS_TYP_CD       VARCHAR(15),
   HZRD_GRP_TYP_CD      VARCHAR(15),
   EMPLR_LIAB_PGM_TYP_CD VARCHAR(15),
   WC_CLS_NON_RT_ELEM_IND CHAR(1)                default 'n',
   WC_CLS_DIV_IND       CHAR(1)                default 'y',
   WC_CLS_DSCNT_IND     CHAR(1)                default 'n',
   WC_CLS_EFF_DT        TIMESTAMP,
   WC_CLS_END_DT        TIMESTAMP,
   WC_CLS_STND_EXCP_IND CHAR(1)                default 'n',
   EXTRNL_CLS_REQD_IND      CHAR(1)                default 'n',
   WC_CLS_WEB_DISP_IND   CHAR(1)               default 'y',
   AUDIT_USER_ID_CREA   NUMERIC(31),
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   WC_CLS_VOID_IND      CHAR(1)                default 'n',
   constraint XPK_WCC primary key (WC_CLS_ID)
);


COMMENT ON TABLE WC_CLASS IS 'This is a code table which defines the class codes that can be used per state in the system for certain periods of time.';
COMMENT ON COLUMN WC_CLASS.wc_cls_id IS 'Primary key to the WC_CLASS  table';
COMMENT ON COLUMN WC_CLASS.wc_cls_cls_cd  IS 'The actual class code as furnished by NCCI or other appropriate governing agency.';
COMMENT ON COLUMN WC_CLASS.jur_typ_cd  IS 'This is a foreign key to the jurisdiction_type table specifying the jurisdiction (state) that this class record exists for.';
COMMENT ON COLUMN WC_CLASS.wc_cls_typ_cd  IS 'This is a foreign key to the wc_class_type table specifying the type of class that this is.';
COMMENT ON COLUMN WC_CLASS.indst_sch_typ_cd  IS 'This is a foreign key to the industry_schedule_type table specifying the industry schedule associated to this class code.';
COMMENT ON COLUMN WC_CLASS.indst_grp_typ_cd  IS 'This is a foreign key to the industry_group_type table specifying the industry group associated to this class code.';
COMMENT ON COLUMN WC_CLASS.prem_bs_typ_cd  IS 'This is a foreign key to the premium_basis_type table specifying the premium basis (per capita or total remuneration) for this class code.';
COMMENT ON COLUMN WC_CLASS.hzrd_grp_typ_cd  IS 'This is a foreign key to the hazard_group_type table specifying the hazard group associated to this class code.';
COMMENT ON COLUMN WC_CLASS.emplr_liab_pgm_typ_cd  IS 'This is a foreign key to the employers_liability_program_typ table specifying the normal employers liability program type for this class code.';
COMMENT ON COLUMN WC_CLASS.wc_cls_non_rt_elem_ind  IS 'This is an indicator to specify whether or not this class code is deemed as non-rateable.';
COMMENT ON COLUMN WC_CLASS.wc_cls_div_ind  IS 'This is an indicator to specify whether or not the premium developed from this class code is used in the dividend calculation.';
COMMENT ON COLUMN WC_CLASS.wc_cls_dscnt_ind  IS 'This is an indicator to specify whether or not this class code has been discontinued.';
COMMENT ON COLUMN WC_CLASS.wc_cls_eff_dt  IS 'The effective date for this class code';
COMMENT ON COLUMN WC_CLASS.wc_cls_end_dt  IS 'The ending date for this class code';
COMMENT ON COLUMN WC_CLASS.audit_user_id_crea  IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN WC_CLASS.audit_user_crea_dtm  IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN WC_CLASS.audit_user_id_updt  IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN WC_CLASS.audit_user_updt_dtm  IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN WC_CLASS.wc_cls_void_ind IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN WC_CLASS.cncurncy_id  IS 'The concurrency id - always use 1';
Comment on column WC_CLASS.EXTRNL_CLS_REQD_IND is 'Indicates externalized class code required indicator column for wc_class';

