COMMENT ON TABLE WC_CLASS_RATE_TIER IS 'This is a code table that defines the rates and minimums associated with a class for a certain rating tier.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_id IS 'Primary key to the WC_CLASS_RATE_TIER table';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_id IS 'Primary key to the WC_CLASS  table - ties the rate tier information and the class together';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_typ_cd IS 'This is a foreign key to the wc_class_rate_tier_type table specifying the rating tier for this record.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.mrkt_typ_cd IS 'This is a foreign key to the market_type table specifying the market used for this record.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.ins_cmpy_typ_id IS 'This is a foreign key to the insurance_company_type table specifying the company used for this record.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_eff_dt IS 'The effective date for this class rate tier';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_end_dt IS 'The ending date for this class rate tier';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_file_dt IS 'The date which the company filed its rates with the appropriate governing body.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_loss_cst_amt IS 'This is the loss cost amount associated with the NCCI rate file.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_aprv_ind IS 'An indicator specifying whether or not this record has been approved by the appropriate governing body.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_plmnry_rt IS 'The pre-file rate for this class code and rate tier.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_rt IS 'The final, approved rate for this class code and rate tier.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_mnm_prem_amt IS 'The minimum premium amount which can be associated to a policy if this class code and rate tier are present on that policy.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_a_rt_ind IS 'An indicator specifying whether or not this combination of class code and rate tier is considered a-rated ( manually entered rate).';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.audit_user_id_crea IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.audit_user_crea_dtm IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.audit_user_id_updt IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.audit_user_updt_dtm IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN WC_CLASS_RATE_TIER.wc_cls_rt_tier_void_ind IS 'An indicator identifying whether the record has been logically deleted or not.';


create table WC_CLASS_RATE_TIER_TYPE
