COMMENT ON COLUMN WC_CLASS_RATE_TIER_TYPE.WC_CLS_RT_TIER_TYP_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
Comment on column WC_CLASS_RATE_TIER_TYPE.WC_CLS_RT_TIER_TYP_WCIO_CD is 'represents the class rate tier code.';


create table WC_CLASS_RELATIONSHIP
