   WC_COV_PREM_DRV_MNL_PREM_A_A NUMERIC(15,2),
   WC_COV_PREM_DRV_MNM_PREM_AMT NUMERIC(15,2),
   WC_COV_PREM_DRV_RT   		NUMERIC(15,2),
   WC_COV_PREM_DRV_CNSN_TO_RT   NUMERIC(15,2),
   WC_COV_PREM_COMPN_COV_IND CHAR(1)           default 'n',
   WC_COV_PREM_RT_OVRRD_IND CHAR(1)  not null  default 'n',
   EXTRNL_PRI_IND  CHAR(1)                default 'n',
   EXTRNL_AMT1 NUMERIC(15,2),   
   EXTRNL_AMT2 NUMERIC(15,2),   
   EXTRNL_AMT3 NUMERIC(15,2),   
   WC_COV_PREM_TOT_PYRL      NUMERIC(15,2),
   WC_COV_PREM_CMRC_PYRL_PCT NUMERIC(5,2),
   WC_COV_PREM_NO_1 NUMERIC(15, 6),
   WC_COV_PREM_NO_2 NUMERIC(15, 6),
   WC_COV_PREM_AMT_1 NUMERIC(15,2),
   WC_COV_PREM_TXT_1   VARCHAR (15),
   AUDIT_USER_ID_CREA   NUMERIC(31),
   AUDIT_USER_CREA_DTM  TIMESTAMP,
   AUDIT_USER_ID_UPDT   NUMERIC(31),
   AUDIT_USER_UPDT_DTM  TIMESTAMP,
   WC_COV_PREM_VOID_IND CHAR(1)                default 'n',
   constraint XPK_WCCP primary key (WC_COV_PREM_ID)
);


COMMENT ON TABLE WC_COVERAGE_PREMIUM IS 'This is a data table which captures information about premium that stems from coverage (I.e. class and exposure).';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.CUST_ID_PTCP_BUSN_INS IS 'This is a foreign key to the customer table specifying the customer identified as the insured on this coverage record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.PREM_PRD_ID IS 'This is a foreign key to the premium_period table specifying the premium period for this coverage record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.RISK_ID IS 'This is a foreign key to the risk table specifying the risk for this coverage record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_CLS_SUFX_ID IS 'This is a foreign key to the wc_class_suffix table specifying the combination of class code and suffix for this coverage record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.RT_TYP_CD IS 'This is a foreign key to the rating_type table specifying the type of rating for this coverage record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.CUST_ID_COV IS 'This is a foreign key to the customer table specifying the customer who is actually covered on this coverage record.';
Comment on column WC_COVERAGE_PREMIUM.PTCP_ID_COV is 'represents Covered Participant Id';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_NO IS 'This is a sequential number per policy and premium period identifying this specific coverage record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.RT_CLS_TYP_CD IS 'This is a foreign key to the rating_class_type table specifying the rating class for this coverage record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.PYRL_RPT_ID IS 'This is a foreign key to the payroll_report table specifying the payroll report for these coverage records if they are reported records.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_EFF_DT IS 'The date that this coverage record took effect.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_END_DT IS 'The date that this coverage record is no longer in effect.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_RN_IND IS 'An indicator specifying whether or not this coverage record should be renewed.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_BS_VAL IS 'The expsoure or payroll amount for this coverage record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_BS_UNT IS 'The number of employees of other items (in the case of per capita records) for this coverage record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_BS_VAL_ADJ IS 'A calculated adjustment amount between the current and previous val amounts.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_BS_UNT_ADJ IS 'A calculated adjustment amount between the current and previous unt amounts.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_DRV_MNL_PREM_AMT IS 'The manual premium amount of the coverage record calculated by multiply the val amount by the unt amount by an applicable filing.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_DRV_MNL_PREM_A_A IS 'A calculated adjustment amount between the current and previous mnl_prem amounts.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_DRV_MNM_PREM_AMT IS 'The minimum premium amount that can be associated with this coverage record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_DRV_RT IS 'The actual rate used in calculation of the manual premium amount of this coverage record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_DRV_CNSN_TO_RT IS 'The consent to rate rate in effect if the policy period has a consent to rate rating element added.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_COMPN_COV_IND IS 'An indicator specifying whether or not this coverage record is involved in companion coverage (I.e. exposure and number of employees are captured for information purposes only while premium is gathered from a companion record).';
Comment on column WC_COVERAGE_PREMIUM.WC_COV_PREM_TOT_PYRL is 'represents Total Payroll';
Comment on column WC_COVERAGE_PREMIUM.WC_COV_PREM_CMRC_PYRL_PCT is 'represents Commercial Payroll Percentage';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_NO_1 IS 'Represents number. Refer EXTERNAL_COLUMN_METADATA table for usage.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_NO_2 IS 'Represents number. Refer EXTERNAL_COLUMN_METADATA table for usage.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_AMT_1 IS 'Represents number. Refer EXTERNAL_COLUMN_METADATA table for usage.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_TXT_1 IS 'Represents string. Refer EXTERNAL_COLUMN_METADATA table for usage.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.WC_COV_PREM_RT_OVRRD_IND IS 'Indicator identifying that the rate is  being overridden';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.EXTRNL_PRI_IND is 'Indicates externalized primary indicator column for coverage premium ';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.EXTRNL_AMT2 is 'Indicates externalized amount column #2 for coverage premium.  Potentially used for client specific requirements.';
COMMENT ON COLUMN WC_COVERAGE_PREMIUM.EXTRNL_AMT3 is 'Indicates externalized amount column #3 for coverage premium.  Potentially used for client specific requirements.';

create table WITNESS_TYPE
(
   WTN_TYP_CD           VARCHAR(15)            not null,
   WTN_TYP_NM           VARCHAR(80) NOT NULL,
   VOID_IND             CHAR(1)                default 'n',
   constraint XPK_WT primary key (WTN_TYP_CD)
);


COMMENT ON TABLE WITNESS_TYPE IS 'This table identifies the Witness Type that may be associated to a person that is participating in a claim as a witness.  The on-line application uses these values in the Witness Participation area of the system.';
COMMENT ON COLUMN WITNESS_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

CREATE TABLE SERVICE_CODE_COST_CENTER_XREF (
    scccx_id                 NUMERIC(31) NOT NULL,
    bill_ln_item_cd_typ_cd   VARCHAR(15) NOT NULL,
    scccx_serv_cd_fr         VARCHAR(15) NOT NULL,
    scccx_serv_cd_to         VARCHAR(15) NOT NULL,
    cst_cntr_typ_cd          VARCHAR(15) NOT NULL,           
    cst_cntr_upd_ind         CHAR(1),  
    void_ind                 CHAR(1) NOT NULL DEFAULT 'n',
    CONSTRAINT XPK_SCCCX PRIMARY KEY (scccx_id)
);
COMMENT ON TABLE SERVICE_CODE_COST_CENTER_XREF IS 'Table holds cross reference between Bill Line Item Code Type to Cost Center Type.';
COMMENT ON COLUMN SERVICE_CODE_COST_CENTER_XREF.VOID_IND IS 'Indicates whether the record is voided';



CREATE TABLE CARRIER_SPECIFIC_SERVICE (
	CAR_SPFC_SERV_ID NUMERIC(31) NOT NULL,
	CAR_SPFC_SERV_CD VARCHAR(15),
	CAR_SPFC_SERV_EFF_DT TIMESTAMP,
	CAR_SPFC_SERV_END_DT TIMESTAMP,
	CAR_SPFC_SERV_DESC VARCHAR(2000),
	VOID_IND CHAR(1) DEFAULT 'n',
	CONSTRAINT XPK_CSC PRIMARY KEY (car_spfc_serv_id)
);


COMMENT ON TABLE CARRIER_SPECIFIC_SERVICE IS 'This table is used to define a carriers specific procedure codes defined by the client for use when processing Bills.';
COMMENT ON COLUMN CARRIER_SPECIFIC_SERVICE.CAR_SPFC_SERV_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN CARRIER_SPECIFIC_SERVICE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

CREATE TABLE BILL (
	BILL_ID 			NUMERIC(31) NOT NULL,
	AGRE_ID 			NUMERIC(31) NOT NULL,
	BILL_NO 			VARCHAR(30),
	BILL_EDI_REF_NO_PRI		VARCHAR(30),			
	BILL_EDI_REF_NO_SECD		VARCHAR(30),			
	BILL_CNV_NO 			VARCHAR(30),
	BILL_EDI_SRC_TYP_CD		VARCHAR(15),			
	BILL_PTCP_PAY_IND 		CHAR(1) DEFAULT 'n',
	BILL_SECD_PAYE_IND		CHAR(1) DEFAULT 'n',
	BILL_SECD_PAYE_CUST_ID	NUMERIC(31),
	CUST_ID_BILL_PROV 		NUMERIC(31),
	CUST_ID_SERV_PROV 		NUMERIC(31),
	CUST_ID_FACIL_PROV		NUMERIC(31),
	BILL_OVRRD_CUST_BLK_IND CHAR(1) DEFAULT 'n' NOT NULL,
	BOCBRT_CD				VARCHAR(15),
	BOCB_RSN_TYP_CMNT		VARCHAR(4000),
	BILL_TRN_ID_FR 			NUMERIC(31),
	BILL_TRN_ID_TO 			NUMERIC(31),
	BILL_LNK_BILL_ID 		NUMERIC(31),
	CUST_LIEN_REST_ID       NUMERIC(31),
	BILL_LNK_TYP_CD   		VARCHAR(15),
	BILL_NOTE_IND 			CHAR(1) DEFAULT 'n',
	BILL_OPN_DT 			TIMESTAMP,
	BILL_COMP_DT			TIMESTAMP,
	BILL_REOPN_DT 			TIMESTAMP,	
	BILL_RECOMP_DT 			TIMESTAMP,
	BILL_LST_TRAN_DT 		TIMESTAMP,	
	BILL_OVRRD_PAYE_CUST_NM_ID   NUMERIC(31),
	BILL_MAIL_TO_IND CHAR(1)  DEFAULT 'n' NOT NULL,
	BILL_MAIL_TO_CUST_ID NUMERIC(31),
	BILL_ATTCH_REQD_IND CHAR(1)  DEFAULT 'n' NOT NULL,
	BILL_PAY_BY_CHCK_OVRRD_IND CHAR(1)  DEFAULT 'n' NOT NULL,
	PAY_SPL_HNDL_TYP_CD VARCHAR(15),
	BILL_SPL_HNDL_RSN VARCHAR(256),
	CNCURNCY_ID     	    NUMERIC(31)  DEFAULT 1 NOT NULL,
	AUDIT_USER_ID_CREA 		NUMERIC(31),
	AUDIT_USER_CREA_DTM 		TIMESTAMP,
	AUDIT_USER_ID_UPDT 		NUMERIC(31),
	AUDIT_USER_UPDT_DTM 		TIMESTAMP,
	CONSTRAINT XPK_BILL PRIMARY KEY (BILL_ID)
);	


COMMENT ON TABLE BILL IS 'Table containing header information for a bill. This information is common for all the line items on a bill and does not change as a result of adjustments or transfers. It may, however, change in the interval prior to the bill reaching a complete state.';
COMMENT ON COLUMN BILL.BILL_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN BILL.AGRE_ID IS 'The agreement with which the bill is associated (i.e. claim or policy).';
COMMENT ON COLUMN BILL.BILL_NO IS 'External reference number of the bill, generated by the system.';
Comment on column BILL.BILL_EDI_REF_NO_PRI is 'Edi primary reference number assigned by edi bill reviewr.';
Comment on column BILL.BILL_EDI_REF_NO_SECD is 'Edi secondary reference number assigned by earlier bill reviewer';
COMMENT ON COLUMN BILL.BILL_CNV_NO IS 'Number allocated to a converted bill.';
COMMENT ON COLUMN BILL.BILL_PTCP_PAY_IND IS 'Indicator indentifying whether the payment is to be made to a particpant based on agreement type.  (e.g. For claim, the participant would be the claimant).';
COMMENT ON COLUMN BILL.CUST_ID_BILL_PROV IS 'Customer identifier of the billing provider. The entity that will receive payment for the services provided.';
COMMENT ON COLUMN BILL.CUST_ID_SERV_PROV IS 'Customer identifier of the service provider. The entity who performed the services provided.';
COMMENT ON COLUMN BILL.CUST_ID_FACIL_PROV IS 'Customer identifier of the facility provider. The entity where the services were performed.';
COMMENT ON COLUMN BILL.BILL_OVRRD_CUST_BLK_IND IS 'When you need to override a provider customer block, set this indicator to yes to override.';
COMMENT ON COLUMN BILL.BOCBRT_CD IS 'The override reason associated with this bill, if any.';
COMMENT ON COLUMN BILL.BOCB_RSN_TYP_CMNT IS 'Allows a free form comment to be entered for the override reason, when the reason chosen has its comment required indicator set to yes.';
COMMENT ON COLUMN BILL.BILL_TRN_ID_FR IS 'Identifier of the bill from which this bill has been transferred.';
COMMENT ON COLUMN BILL.BILL_TRN_ID_TO IS 'Identifier of the bill to which this bill is being transferred.';
COMMENT ON COLUMN BILL.BILL_LNK_BILL_ID IS 'Foreign key to BILL that is set on a child bill to linked it to an associated parent bill.';
COMMENT ON COLUMN BILL.BILL_OPN_DT IS 'Date on which bill went in open state.';
COMMENT ON COLUMN BILL.CUST_LIEN_REST_ID IS 'Foreign key to CUSTOMER_LIEN_RESTRICTION table.';
COMMENT ON COLUMN BILL.BILL_LNK_TYP_CD IS 'Foreign key to BILL_LINK_TYPE table.';
COMMENT ON COLUMN BILL.BILL_COMP_DT IS 'Date on which bill went in completed state.';
COMMENT ON COLUMN BILL.BILL_REOPN_DT IS 'Date on which bill went back into open state.';
COMMENT ON COLUMN BILL.BILL_RECOMP_DT IS 'Date on which bill went back into completed state.';
COMMENT ON COLUMN BILL.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN BILL.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN BILL.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN BILL.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN BILL.BILL_SECD_PAYE_IND IS 'Secondary payee indicator.';
COMMENT ON COLUMN BILL.BILL_SECD_PAYE_CUST_ID IS 'Secondary payee customer ID.';
COMMENT ON COLUMN BILL.BILL_NOTE_IND IS 'Whether a note exists on the bill';
COMMENT ON COLUMN BILL.BILL_EDI_SRC_TYP_CD IS 'Code value indicating the source of the EDI bill from the bill_edi_source_type table.  From which EDI provider.';
COMMENT ON COLUMN BILL.BILL_LST_TRAN_DT IS 'The date of the most recent  Financial Transaction that was generated for the Bill, if any';
COMMENT ON COLUMN BILL.BILL_OVRRD_PAYE_CUST_NM_ID is 'Bill Override payee customer name ID. Foreign key, references CUST_NM_ID of CUSTOMER_NAME table.';
COMMENT ON COLUMN BILL.BILL_MAIL_TO_IND IS 'Indicator on the bill to identify if bill has a different mail to customer to whom the payment is mailed to.';
COMMENT ON COLUMN BILL.BILL_MAIL_TO_CUST_ID IS 'Customer ID of the customer selected as the Mail to payee.';
COMMENT ON COLUMN BILL.BILL_ATTCH_REQD_IND IS 'Attachments required indicator.';
COMMENT ON COLUMN BILL.BILL_PAY_BY_CHCK_OVRRD_IND IS 'Pay by check override indicator.';
COMMENT ON COLUMN BILL.PAY_SPL_HNDL_TYP_CD IS 'Foreign key, references PAY_SPL_HNDL_TYP_CD of PAYMENT_SPECIAL_HANDLING_TYPE.';
COMMENT ON COLUMN BILL.BILL_SPL_HNDL_RSN IS 'Payment special handling reason.';
COMMENT ON COLUMN BILL.CNCURNCY_ID IS 'This column needs to be used only for optimistic concurrency support ONLY.';


CREATE TABLE BILL_FORMAT_TYPE (
	BILL_FRMT_TYP_CD VARCHAR(15) NOT NULL,
	BILL_FRMT_TYP_NM VARCHAR(80) NOT NULL,
	BILL_LN_ITEM_TYP_CD_DFLT VARCHAR(15),
