	VOID_IND CHAR(1) DEFAULT 'n',
	CONSTRAINT XPK_BILLFT PRIMARY KEY (BILL_FRMT_TYP_CD)
);


COMMENT ON TABLE BILL_FORMAT_TYPE IS 'Code table containing the possible bill format codes (UB92, HCFA, etc...).';
COMMENT ON COLUMN BILL_FORMAT_TYPE.BILL_LN_ITEM_TYP_CD_DFLT IS 'The default bill line item type for the bill format.  Set by the system automatically as line items are added to the bill.';
Comment on column bill_format_type.bill_frmt_typ_fee_sch_ind is 'Indicator determining whether any fee schedule should be applied to bills with this bill format type';
COMMENT ON COLUMN BILL_FORMAT_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_PROVIDER_TYPE (
	bill_prov_typ_cd VARCHAR(15) NOT NULL,
	bill_prov_typ_nm VARCHAR(80) NOT NULL,
	void_ind CHAR(1) not null default 'n',
	CONSTRAINT XPK_BILLPT PRIMARY KEY (bill_prov_typ_cd)
);


COMMENT ON TABLE BILL_PROVIDER_TYPE IS 'This is a code table specifying the Billing Provider Type for the Bill Context.';
COMMENT ON COLUMN BILL_PROVIDER_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_SOURCE_TYPE (
	bill_src_typ_cd VARCHAR(15) NOT NULL,
	bill_src_typ_nm VARCHAR(80) NOT NULL,
	bill_src_typ_dflt_ind  CHAR(1)  NOT NULL DEFAULT 'n',
	void_ind CHAR(1) DEFAULT 'n',
