);


COMMENT ON TABLE BILL_SOURCE_TYPE IS 'Code table containing the possible sources of a bill.';
COMMENT ON COLUMN BILL_SOURCE_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_MEDIA_TYPE (
	bill_meda_typ_cd VARCHAR(15) NOT NULL,
	bill_meda_typ_nm VARCHAR(80) NOT NULL,
	bill_meda_typ_dflt_ind  CHAR(1)  NOT NULL DEFAULT 'n',
	void_ind CHAR(1) DEFAULT 'n',
	CONSTRAINT XPK_BILLMT PRIMARY KEY (bill_meda_typ_cd)
);


COMMENT ON TABLE BILL_MEDIA_TYPE IS 'Code table containing the possible media types by which a bill might be received.';
COMMENT ON COLUMN BILL_MEDIA_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_REVIEW_OVERRIDE_RSN_TYP (
	bill_rvw_ovrrd_rsn_typ_cd VARCHAR(15) NOT NULL,
	bill_rvw_ovrrd_rsn_typ_nm VARCHAR(80) NOT NULL,
