	void_ind CHAR(1) DEFAULT 'n',
	CONSTRAINT XPK_BRORT PRIMARY KEY (bill_rvw_ovrrd_rsn_typ_cd)
);


COMMENT ON TABLE BILL_REVIEW_OVERRIDE_RSN_TYP IS 'Code table containing possible reasons for a bill review override.';
COMMENT ON COLUMN BILL_REVIEW_OVERRIDE_RSN_TYP.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_FAST_TRACK_ACTION_TYPE (
	bill_fast_trk_actn_typ_cd VARCHAR(15) NOT NULL,
	bill_fast_trk_actn_typ_nm VARCHAR(80) NOT NULL,
	void_ind CHAR(1) DEFAULT 'n',
	CONSTRAINT XPK_BFTAT PRIMARY KEY (bill_fast_trk_actn_typ_cd)
);


COMMENT ON TABLE BILL_FAST_TRACK_ACTION_TYPE IS 'Code table containing possible fast track processing actions.';
COMMENT ON COLUMN BILL_FAST_TRACK_ACTION_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_FAST_TRACK_ACTION_RSN_TYP (
	bftart_cd VARCHAR(15) NOT NULL,
	bftart_nm VARCHAR(80) NOT NULL,
	bftart_eob_cd VARCHAR(15),
	bill_fast_trk_actn_typ_cd VARCHAR(15) NOT NULL,
	void_ind CHAR(1) NOT NULL DEFAULT 'n',
	CONSTRAINT XPK_BFTART PRIMARY KEY (bftart_cd)
);


COMMENT ON TABLE BILL_FAST_TRACK_ACTION_RSN_TYP IS 'This code table is used to create Fast Track Action Reasons when a Fast Track Action is selected for a Bill.';
COMMENT ON COLUMN BILL_FAST_TRACK_ACTION_RSN_TYP.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_PROCEDURE (
	BILL_PROC_ID 			NUMERIC(31) NOT NULL,
	BILL_CYC_ID 			NUMERIC(31),			-- NEW COLUMN
	BILL_PROC_NO 			NUMERIC(31),
	PROC_CD_TYP_CD 			VARCHAR(15),			
	BILL_PROC_CD			VARCHAR(15),			-- NEW COLUMN
	BILL_PROC_DESC			VARCHAR(2000),			-- NEW COLUMN
	BILL_PROC_VRFD_IND		CHAR(1) DEFAULT 'n',		-- NEW COLUMN
	BILL_PROC_DT 			TIMESTAMP,			
	AUDIT_USER_ID_CREA 		NUMERIC(31),
	AUDIT_USER_CREA_DTM 		TIMESTAMP,
	AUDIT_USER_ID_UPDT 		NUMERIC(31),
	AUDIT_USER_UPDT_DTM 		TIMESTAMP,
	VOID_IND 			CHAR(1) not null DEFAULT 'n',
	CONSTRAINT XPK_BP PRIMARY KEY (BILL_PROC_ID)
);


COMMENT ON TABLE BILL_PROCEDURE IS 'Table to record multiple procedure codes for UB92 bill formats.';
COMMENT ON COLUMN BILL_PROCEDURE.BILL_PROC_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN BILL_PROCEDURE.PROC_CD_TYP_CD IS 'Code value indentifying the type of procedure code being recorded.';
Comment on column BILL_PROCEDURE.BILL_PROC_CD is 'To store bill procedure code for bill cycle';
Comment on column BILL_PROCEDURE.BILL_PROC_DESC is 'To store bill procedure code description for bill cycle';
Comment on column BILL_PROCEDURE.BILL_PROC_VRFD_IND is 'To indicate whether bill procedure code is being verified or not';
COMMENT ON COLUMN BILL_PROCEDURE.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN BILL_PROCEDURE.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN BILL_PROCEDURE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN BILL_PROCEDURE.BILL_CYC_ID IS 'Surrogate primary key identifier for the bill_cycle table.';
COMMENT ON COLUMN BILL_PROCEDURE.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN BILL_PROCEDURE.BILL_PROC_NO IS 'The procedure code number.';
COMMENT ON COLUMN BILL_PROCEDURE.BILL_PROC_DT IS 'Procedure code date.';
COMMENT ON COLUMN BILL_PROCEDURE.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';

CREATE TABLE EMERGENCY_CODE_TYPE (
	EMRG_CD_TYP_CD			VARCHAR(15) NOT NULL,
	EMRG_CD_TYP_NM			VARCHAR(80) NOT NULL,
	VOID_IND			CHAR(1) not null DEFAULT 'n',
	CONSTRAINT XPK_EMRGCT PRIMARY KEY (EMRG_CD_TYP_CD)
);


COMMENT ON TABLE EMERGENCY_CODE_TYPE IS 'To store emergency codes';
Comment on column EMERGENCY_CODE_TYPE.EMRG_CD_TYP_CD is 'To store unique emergency code';
Comment on column EMERGENCY_CODE_TYPE.EMRG_CD_TYP_NM is 'To store description for emergency code';


CREATE TABLE BILL_DIAGNOSIS (
	BILL_DIAG_ID 			NUMERIC(31) NOT NULL,
	BILL_CYC_ID 			NUMERIC(31),			
	BILL_DIAG_NO 			NUMERIC(31),
	BILL_DIAG_ICD_CD		VARCHAR(15),			
	BILL_DIAG_ICD_DESC		VARCHAR(2000),			
	BILL_DIAG_ICD_VRFD_IND		CHAR(1) DEFAULT 'n',		
	AUDIT_USER_ID_CREA 		NUMERIC(31),
	AUDIT_USER_CREA_DTM 		TIMESTAMP,
	AUDIT_USER_ID_UPDT 		NUMERIC(31),
	AUDIT_USER_UPDT_DTM 		TIMESTAMP,
	BILL_DIAG_TYP_CD                VARCHAR(15) NOT NULL,
	VOID_IND 			CHAR(1)  not null  DEFAULT 'n',
	CONSTRAINT XPK_BILLD PRIMARY KEY (BILL_DIAG_ID)
);


COMMENT ON TABLE BILL_DIAGNOSIS IS 'Table to record multiple ICD diagnosis codes for certain bill formats.';
COMMENT ON COLUMN BILL_DIAGNOSIS.BILL_DIAG_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN BILL_DIAGNOSIS.BILL_DIAG_NO IS 'The unique number for this bill diagnosis code on the bill cycle';
Comment on column BILL_DIAGNOSIS.BILL_DIAG_ICD_CD is 'To store diagnosis icd code for bill cycle';
Comment on column BILL_DIAGNOSIS.BILL_DIAG_ICD_DESC is 'To store diagnosis icd code description for bill cycle';
Comment on column BILL_DIAGNOSIS.BILL_DIAG_ICD_VRFD_IND is 'To indicate whether diagnosis icd code on bill cycle is being verified or not';
COMMENT ON COLUMN BILL_DIAGNOSIS.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN BILL_DIAGNOSIS.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN BILL_DIAGNOSIS.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN BILL_DIAGNOSIS.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
Comment on column BILL_DIAGNOSIS.BILL_DIAG_TYP_CD is 'To indicate the Type of Diagnosis code ; Diagnosis, External etc';
COMMENT ON COLUMN BILL_DIAGNOSIS.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN BILL_DIAGNOSIS.BILL_CYC_ID IS 'Surrogate primary key identifier for the bill_cycle table.';

CREATE TABLE BILL_CYCLE (
	BILL_CYC_ID 			NUMERIC(31) NOT NULL,
	BILL_ID 			NUMERIC(31),
	BILL_CYC_NO 			VARCHAR(30),
	BILL_CYC_ICD_VER_CD		VARCHAR(15),
	BILL_FRMT_TYP_CD 		VARCHAR(15),			
	BILL_SRC_TYP_CD 		VARCHAR(15),			
	BILL_MEDA_TYP_CD 		VARCHAR(15),			
	BILL_CYC_SERV_EFF_DT 		TIMESTAMP,			
	BILL_CYC_SERV_END_DT 		TIMESTAMP,			
	BILL_CYC_RECV_DT 		TIMESTAMP,			
	BILL_CYC_NOTE_ATTCH_IND 	CHAR(1) DEFAULT 'n',
	BILL_CYC_FEE_SCH_OVRRD_IND	CHAR(1) NOT NULL DEFAULT 'n',	
	FEE_SCH_OVRRD_RSN_TYP_CD	VARCHAR(15),			
	BILL_FAST_TRK_ACTN_TYP_CD 	VARCHAR(15),			
	BFTART_CD 			VARCHAR(15),			
	BILL_CYC_CHR_TOT_AMT 		NUMERIC(31,10),			
	CST_CNTR_TYP_CD			VARCHAR(15),			
	PAY_TYP_CD			VARCHAR(15),			
	BILL_CYC_SUM_SBM_AMT		NUMERIC(31,10),		
	BILL_CYC_SUM_APRV_AMT		NUMERIC(31,10),		
	BILL_CYC_SUM_PD_AMT		NUMERIC(31,10),		
	BILL_CYC_REF_NO 		VARCHAR(30),			
	BILL_CYC_MED_REC_NO 		VARCHAR(30),			
	BILL_CYC_ADMIT_DTM 		TIMESTAMP,			
	BILL_CYC_DISC_DTM 		TIMESTAMP,			
	BILL_CYC_LNGTH_OF_STAY_DD	NUMERIC(5),			
	BILL_CYC_DRG_CD			VARCHAR(15),			
	BILL_CYC_DRG_DESC		VARCHAR(500),			
	BILL_CYC_DRG_VRFD_IND		CHAR(1) DEFAULT 'n',		
	BILL_CYC_ADJ_DRG_CD		VARCHAR(15),			
	BILL_CYC_ADJ_DRG_DESC		VARCHAR(500),			
	BILL_CYC_ADJ_DRG_VRFD_IND	CHAR(1) DEFAULT 'n',		
	BILL_CYC_UB92_TYP_CD		VARCHAR(15),			
	BILL_CYC_UB92_TYP_DESC		VARCHAR(500),			
	BILL_CYC_UB92_TYP_VRFD_IND	CHAR(1) DEFAULT 'n',		
	BILL_CYC_ICD_SBM_ADMIT_CD	VARCHAR(15),			
	BILL_CYC_ICD_SBM_ADMIT_DESC	VARCHAR(2000),			
	BILL_CYC_ICD_ADMIT_CD		VARCHAR(15),			
	BILL_CYC_ICD_ADMIT_DESC		VARCHAR(2000),			
	BILL_CYC_ICD_ADMIT_VRFD_IND	CHAR(1) DEFAULT 'n',		
	PAY_REQS_ID			NUMERIC(31),
	AUDIT_USER_ID_CREA 		NUMERIC(31),
	AUDIT_USER_CREA_DTM 		TIMESTAMP,
