	BILL_CYC_NET_WK_SERV_IND        CHAR(1) DEFAULT 'n',
	BILL_CYC_DNY_OVRRD_IND 			CHAR(1) NOT NULL DEFAULT 'n',
	CNCURNCY_ID     	    		NUMERIC(31)  DEFAULT 1 NOT NULL,
	CONSTRAINT XPK_BC PRIMARY KEY (BILL_CYC_ID)
);	


COMMENT ON TABLE BILL_CYCLE IS 'Table to store information for each life-cycle of a bill.  Whenever a bill reaches a completed state, it must be reborn in a new cycle before adjustments can be made.';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN BILL_CYCLE.BILL_ID IS 'The bill to which the cycle belongs.';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_NO IS 'External reference number for the bill cycle.  Generated by the system.  Each bill begins with cycle number "1".  Each subsequent cycle added to a bill results in a cycle number incremented by 1.';
Comment on column BILL_CYCLE.BILL_CYC_SUM_SBM_AMT is 'This is sum of line items submitted amount to this cycle';
Comment on column BILL_CYCLE.BILL_CYC_SUM_APRV_AMT is 'This is the sum of line items aproved amount to this cycle (after deducting bill cycle reductions)';
Comment on column BILL_CYCLE.BILL_CYC_SUM_PD_AMT is 'This is the sum of line items aproved amount to this cycle (after deducting bill cycle reductions)';
Comment on column BILL_CYCLE.BILL_CYC_DRG_CD is 'To store drg code';
Comment on column BILL_CYCLE.BILL_CYC_DRG_DESC is 'To store drg code description';
Comment on column BILL_CYCLE.BILL_CYC_DRG_VRFD_IND is 'To indicate whether drg code is being verified or not';
Comment on column BILL_CYCLE.BILL_CYC_ADJ_DRG_CD is 'To store adjusted drg code';
Comment on column BILL_CYCLE.BILL_CYC_ADJ_DRG_DESC is 'To store adjusted drg code description';
Comment on column BILL_CYCLE.BILL_CYC_ADJ_DRG_VRFD_IND is 'To indicate whether adjusted drg code is being verified or not';
Comment on column BILL_CYCLE.BILL_CYC_UB92_TYP_CD is 'To store ub92 type code';
Comment on column BILL_CYCLE.BILL_CYC_UB92_TYP_DESC is 'To store ub92 type code description';
Comment on column BILL_CYCLE.BILL_CYC_UB92_TYP_VRFD_IND is 'To indicate whether ub92 code is being verified or not';
Comment on column BILL_CYCLE.BILL_CYC_ICD_SBM_ADMIT_CD is 'To store submitted admitted icd code';
Comment on column BILL_CYCLE.BILL_CYC_ICD_SBM_ADMIT_DESC is 'To store submitted admitted icd code description';
Comment on column BILL_CYCLE.BILL_CYC_ICD_ADMIT_CD is 'To store icd admitted code';
Comment on column BILL_CYCLE.BILL_CYC_ICD_ADMIT_DESC is 'To store icd admitted code description';
Comment on column BILL_CYCLE.BILL_CYC_ICD_ADMIT_VRFD_IND is 'To indicate whether admitted code is being verified or not';
COMMENT ON COLUMN BILL_CYCLE.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN BILL_CYCLE.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN BILL_CYCLE.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN BILL_CYCLE.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
Comment on column BILL_CYCLE.BILL_CYC_PPY_TYP_CD is 'To store prepaid type code';
Comment on column BILL_CYCLE.BILL_CYC_PHY_LIC_NO is 'To store Physician License number';
Comment on column BILL_CYCLE.BILL_ADMIS_TYP_CD is 'To store Admission Type Code';
Comment on column BILL_CYCLE.BILL_SCH_TYP_CD is 'To store Schedule Type code';
Comment on column BILL_CYCLE.BILL_CYC_OP_PHYS_LIC_NO is 'To store Operating Physician License number';
Comment on column BILL_CYCLE.BILL_SPL_PROC_TYP_CD is 'To store Procedure Type code';
Comment on column BILL_CYCLE.BILL_CYC_DO_NOT_RPT_IND is 'To indicate whether to report or not';
Comment on column BILL_CYCLE.BILL_DO_NOT_RPT_RSN_TYP_CD is 'To store Do Not Report Reason Type';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_NOTE_ATTCH_IND IS 'Indicator identifying whether a note is attached to the bill.';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_DISC_DTM IS 'Represents the date and time the patient was discharged from the facility';
COMMENT ON COLUMN BILL_CYCLE.PAY_REQS_ID IS 'This is a foreign key to the payment_request table specifying the id of the payment request.';
COMMENT ON COLUMN BILL_CYCLE.BILL_SRC_TYP_CD IS 'Code value indicating the source of the bill from the bill_source_type table.  From who or what was the bill obtained (e.g. Provider, Claimant, Attorney, etc...).';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_REF_NO IS 'Service providers account or control number.  May be a patient number, customer number, or invoice number.';
COMMENT ON COLUMN BILL_CYCLE.BFTART_CD IS 'The BILL_FAST_TRACK_ACTION_RSN_TYP code associated with the bill';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_LNGTH_OF_STAY_DD IS 'The number of days for the length of stay on this bill';
COMMENT ON COLUMN BILL_CYCLE.BILL_FAST_TRK_ACTN_TYP_CD IS 'Code value specifying the type of fast track processing the bill will undergo (i.e. Pay Bill or Deny Bill).';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_SPL_PROC_AMT IS 'Represents the total amount of the bill applicable to the Special Procedure Type selected.  The user must enter a Special Procedure Amount  that is less than or equal to the Bill Total Charge if a Special Procedure Type has been selected.';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_FEE_SCH_OVRRD_IND IS 'Indicator identifying whether there is a system or user override of fee_schedule repricing on this bill.';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_SERV_END_DT IS 'End date for services provided on the bill cycle';
COMMENT ON COLUMN BILL_CYCLE.BILL_MEDA_TYP_CD IS 'The media type by which the bill was received (e.g. Document, EDI, etc...).';
COMMENT ON COLUMN BILL_CYCLE.PAY_TYP_CD IS 'This is a foreign key to the payment type table that identifies the payment type of the financial transaction such as Medical, Expense.';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_MED_REC_NO IS 'Medical record number assigned to the patients medical/health record by the provider.';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_SERV_EFF_DT IS 'Effective date for services provided on the bill cycle.';
COMMENT ON COLUMN BILL_CYCLE.FEE_SCH_OVRRD_RSN_TYP_CD IS 'Code value indicating the reason given for the fee_schedule override.';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_CHR_TOT_AMT IS 'Total amount charged on the bill.';
COMMENT ON COLUMN BILL_CYCLE.CST_CNTR_TYP_CD IS 'The cost center for the bill.';
COMMENT ON COLUMN BILL_CYCLE.BILL_FRMT_TYP_CD IS 'Code value indicating the format of the bill.  Generally this indicates what document or submission template the bill was received on (e.g. UB92, HCFA, etc...).';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_RECV_DT IS 'The date on which the invoice was received by the carrier.';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_ADMIT_DTM IS 'Represents the date and time the patient was admitted to the provider for inpatient care, outpatient service or start of care ';
COMMENT ON COLUMN BILL_CYCLE.BILL_CYC_NET_WK_SERV_IND is 'To indicate if the service provided under the bill are in Network or out of Network';
COMMENT ON COLUMN BILL_CYCLE.CNCURNCY_ID IS 'This column needs to be used only for optimistic concurrency support ONLY.';


CREATE TABLE BILL_CYCLE_STATUS (
	BILL_CYC_ID			NUMERIC(31) not null,
	BILL_STS_TYP_CD 		VARCHAR(15),
	BILL_CYC_STS_EFF_DT 		TIMESTAMP,	
	BILL_STT_TYP_CD 		VARCHAR(15),
	BILL_CYC_STS_STT_EFF_DT 	TIMESTAMP,
	BILL_TRANS_RSN_TYP_CD 		VARCHAR(15),
	BILL_CYC_STS_COMT 		VARCHAR(500),
	CNCURNCY_ID     	    NUMERIC(31)  DEFAULT 1 NOT NULL,
	AUDIT_USER_ID_CREA 		NUMERIC(31),
	AUDIT_USER_CREA_DTM 		TIMESTAMP,
	AUDIT_USER_ID_UPDT 		NUMERIC(31),
	AUDIT_USER_UPDT_DTM 		TIMESTAMP,
	VOID_IND			CHAR(1) not null DEFAULT 'n',
	CONSTRAINT XPK_BCSTS PRIMARY KEY (BILL_CYC_ID)	
);


COMMENT ON TABLE BILL_CYCLE_STATUS IS 'To store bill cycle current status';
Comment on column BILL_CYCLE_STATUS.BILL_CYC_STS_EFF_DT is 'To store bill cycles current status effective date';
Comment on column BILL_CYCLE_STATUS.BILL_STT_TYP_CD is 'To store bill cycles current state';
Comment on column BILL_CYCLE_STATUS.BILL_CYC_STS_STT_EFF_DT is 'To store bill cycles state effective date';
Comment on column BILL_CYCLE_STATUS.BILL_CYC_STS_COMT is 'To store free form text for bill cycle status';
COMMENT ON COLUMN BILL_CYCLE_STATUS.BILL_TRANS_RSN_TYP_CD IS 'The reason given by the system or user that the current status and/or state were transitioned to.';
COMMENT ON COLUMN BILL_CYCLE_STATUS.BILL_CYC_ID IS 'Surrogate primary key identifier for the bill_cycle table.';
COMMENT ON COLUMN BILL_CYCLE_STATUS.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN BILL_CYCLE_STATUS.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN BILL_CYCLE_STATUS.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN BILL_CYCLE_STATUS.BILL_STS_TYP_CD IS 'The current Status of the bill cycle.  Each cycle has its own status.';
COMMENT ON COLUMN BILL_CYCLE_STATUS.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN BILL_CYCLE_STATUS.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN BILL_CYCLE_STATUS.CNCURNCY_ID IS 'This column needs to be used only for optimistic concurrency support ONLY.';

CREATE TABLE BILL_ADJUSTMENT (
	bill_adj_id NUMERIC(31) NOT NULL,
