	audit_user_updt_dtm TIMESTAMP,
	CONSTRAINT XPK_BA PRIMARY KEY (bill_adj_id)
);
COMMENT ON TABLE BILL_ADJUSTMENT IS 'Table holds the data record details for the Adjusted Bills.';

CREATE TABLE BILL_ADJUSTMENT_LINE_ITEM (
