COMMENT ON TABLE BILL_ADJUSTMENT_LINE_ITEM IS 'Table holds the data record details for the Adjusted Bill Line Items.';
COMMENT ON COLUMN BILL_ADJUSTMENT_LINE_ITEM.BILL_ADJ_LN_ITEM_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN BILL_ADJUSTMENT_LINE_ITEM.BILL_ADJ_ID IS 'The bill adjustment to which the bill adjustment line item belongs.';
COMMENT ON COLUMN BILL_ADJUSTMENT_LINE_ITEM.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN BILL_ADJUSTMENT_LINE_ITEM.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN BILL_ADJUSTMENT_LINE_ITEM.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN BILL_ADJUSTMENT_LINE_ITEM.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN BILL_ADJUSTMENT_LINE_ITEM.BILL_LN_ITEM_ID_TO IS 'The resulting bill line item ID showing the adjustment';
COMMENT ON COLUMN BILL_ADJUSTMENT_LINE_ITEM.BILL_LN_ITEM_ID_FR IS 'The bill line item ID that was adjusted';

CREATE TABLE BILL_ADJUSTMENT_TYPE (
	bill_adj_typ_cd VARCHAR(15) NOT NULL,
	bill_adj_typ_nm VARCHAR(80) NOT NULL,
	void_ind CHAR(1) DEFAULT 'n',
	CONSTRAINT XPK_BAT PRIMARY KEY (bill_adj_typ_cd)
);


COMMENT ON TABLE BILL_ADJUSTMENT_TYPE IS 'This is a code table specifying the types of adjustments which is applicable for the bill context.';
COMMENT ON COLUMN BILL_ADJUSTMENT_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


