    BILL_LN_ITEM_EDI_REF_NO 	VARCHAR(30),			
	BILL_LN_ITEM_SERV_EFF_DT 	TIMESTAMP,
	BILL_LN_ITEM_SERV_END_DT 	TIMESTAMP,
	BILL_LN_ITEM_TYP_CD 		VARCHAR(15),
	BILL_LN_ITEM_CD_TYP_CD_PRI 	VARCHAR(15),
	CST_CNTR_TYP_CD 		VARCHAR(15),
	PAY_TYP_CD 			VARCHAR(15),	
	BILL_LN_ITEM_SBM_PRI_CD		VARCHAR(15),			
	BILL_LN_ITEM_SBM_PRI_DESC	VARCHAR(2000),			
	BILL_LN_ITEM_PRI_CD		VARCHAR(15),			
	BILL_LN_ITEM_PRI_DESC		VARCHAR(2000),			
	BILL_LN_ITEM_PRI_VRFD_IND	CHAR(1) DEFAULT 'n',
	BILL_LN_ITEM_SUB_VRFD_IND	CHAR(1) DEFAULT 'n',
	BILL_LN_ITEM_STR_TIME		TIMESTAMP,			
	BILL_LN_ITEM_END_TIME		TIMESTAMP,			
	BILL_LN_ITEM_UNT 		NUMERIC(31,10),
	BILL_LN_ITEM_MIN		NUMERIC(31,10),
	BILL_LN_ITEM_CD_TYP_CD_SECD 	VARCHAR(15),
	BILL_LN_ITEM_SECD_CD		VARCHAR(15),			
	BILL_LN_ITEM_SECD_DESC		VARCHAR(2000),		
	BILL_LN_ITEM_SECD_VRFD_IND	CHAR(1) DEFAULT 'n',	
	BILL_LN_ITEM_SERV_PLC_CD	VARCHAR(15),			
	BILL_LN_ITEM_SERV_PLC_DESC	VARCHAR(80),			
	BILL_LN_ITEM_SERV_PLC_VRFD_IND	CHAR(1) DEFAULT 'n',		
	BILL_LN_ITEM_SERV_TYP_CD	VARCHAR(15),			
	BILL_LN_ITEM_SERV_TYP_DESC	VARCHAR(80),			
	BILL_LN_ITEM_SERV_TYP_VRFD_IND	CHAR(1) DEFAULT 'n',		
	BILL_LN_ITEM_EMRG_TYP_CD	VARCHAR(15),			
	BILL_LN_ITEM_EMRG_TYP_DESC	VARCHAR(80),			
	BILL_LN_ITEM_EMRG_TYP_VRFD_IND	CHAR(1) DEFAULT 'n',		
	BILL_LN_ITEM_RX_NO 		VARCHAR(30),
	BILL_LN_ITEM_RX_SUPP_DD 	NUMERIC(5),
	BILL_LN_ITEM_RX_PROV_NM 	VARCHAR(80),
	BILL_LN_ITEM_RX_PROV_ID 	VARCHAR(30),
	PROV_ID_TYP_CD 			VARCHAR(15),
	BILL_LN_ITEM_RX_DT 		TIMESTAMP,	
	BILL_LN_ITEM_TOOTH_CD		VARCHAR(15),			
	BILL_LN_ITEM_TOOTH_DESC		VARCHAR(80),			
	BILL_LN_ITEM_TOOTH_VRFD_IND	CHAR(1) DEFAULT 'n',		
	BILL_LN_ITEM_TOOTHSRF_CD	VARCHAR(15),			
	BILL_LN_ITEM_TOOTHSRF_DESC	VARCHAR(80),			
	BILL_LN_ITEM_TOOTHSRF_VRFD_IND	CHAR(1) DEFAULT 'n',		
	BILL_LN_ITEM_TRVL_FR 		VARCHAR(80),
	BILL_LN_ITEM_TRVL_TO 		VARCHAR(80),
	TRVL_TYP_CD			VARCHAR(15),
	BILL_LN_ITEM_TRVL_DST		NUMERIC(31,10),
	BILL_LN_ITEM_TRVL_NET		NUMERIC(31,10),
	BILL_LN_ITEM_TRVL_EXCL		NUMERIC(31,10),
	BILL_LN_ITEM_TRVL_RT		NUMERIC(31,10),
	BILL_LN_ITEM_TRVL_REMB		NUMERIC(31,10),
	BILL_LN_ITEM_COMT 		VARCHAR(500),
	BILL_LN_ITEM_SBM_AMT 		NUMERIC(31,10),
	BILL_LN_ITEM_PAY_SBM_IND	CHAR(1) DEFAULT 'n',
	BILL_LN_ITEM_APRV_AMT 		NUMERIC(31,10),
	BILL_LN_ITEM_PD_AMT 		NUMERIC(31,10),
	BILL_LN_ITEM_TAX_AMT		NUMERIC(31,10),  			
