	BILL_LN_ITEM_OVRRD_FLG_IND  CHAR(1) not null DEFAULT 'n',
	CONSTRAINT XPK_BLI PRIMARY KEY (BILL_LN_ITEM_ID)
);	


COMMENT ON TABLE BILL_LINE_ITEM IS 'The line items associated with a bill. Each line item must be associated with a single cycle. Line items within a completed state cannot be modified except through adjustments via adjusting line items in a new cycle.';
COMMENT ON COLUMN BILL_LINE_ITEM.BILL_LN_ITEM_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN BILL_LINE_ITEM.BILL_CYC_ID IS 'Bill cycle to which the bill line item belongs.';
COMMENT ON COLUMN BILL_LINE_ITEM.BILL_LN_ITEM_NO IS 'External reference number for the bill line item.  Generated by the system.  For each bill (not cycle!), the bill line item begins with "1".  Each new line item added for the bill results in a previous bill line item number incremented by 1.';
Comment on column BILL_LINE_ITEM.BILL_LN_ITEM_EDI_REF_NO is 'To store edi reference number for line item';
COMMENT ON COLUMN BILL_LINE_ITEM.BILL_LN_ITEM_SERV_EFF_DT IS 'Effective date for the service indicated on the line item.';
COMMENT ON COLUMN BILL_LINE_ITEM.BILL_LN_ITEM_SERV_END_DT IS 'End date for the service indicated on the line item.';
