COMMENT ON COLUMN BILL_LINE_ITEM_CODE_XREF.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE EOB (
	EOB_ID 				NUMERIC(31) NOT NULL,
	EOB_CD 				VARCHAR(15) NOT NULL,
	EOB_GRP_TYP_CD 			VARCHAR(15) NOT NULL,
	EOB_EFF_DT 			TIMESTAMP,
	EOB_END_DT 			TIMESTAMP,
	EOB_SHR_DESC 			VARCHAR(80),
	EOB_FULL_DESC 			VARCHAR(2000),
	EOB_NO_PAY_NTC_IND 		CHAR(1) DEFAULT 'n',
	EOB_BILL_LN_ITEM_PRNT_IND 	CHAR(1) DEFAULT 'n',
	EOB_REMIT_PRNT_IND 		CHAR(1) DEFAULT 'n',
	EOB_SYS_IND 			CHAR(1) DEFAULT 'n',
	VOID_IND 			CHAR(1)  not null DEFAULT 'n',
	CONSTRAINT XPK_EOB PRIMARY KEY (EOB_ID)
);


COMMENT ON TABLE EOB IS 'Explanation of benefit codes. Used to provide a description of occurrences with regard to a single line item. Some are used to communicate information on a remittance report while others may be used to control processing at a line item level.';
COMMENT ON COLUMN EOB.EOB_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN EOB.EOB_CD IS 'Short code to facilitate quick entry of EOB codes on bill line items.';
COMMENT ON COLUMN EOB.EOB_GRP_TYP_CD IS 'Code values identifying the EOB group to which the EOB belongs.';
COMMENT ON COLUMN EOB.EOB_EFF_DT IS 'Date the EOB code version became effective.';
COMMENT ON COLUMN EOB.EOB_END_DT IS 'Date the EOB code version was expired or changed.  When a code is changed, a new record beginning at the new effective date is entered and the previous version of the code is expired as of one day prior to the new effective date.';
COMMENT ON COLUMN EOB.EOB_SHR_DESC IS 'The description as it appears when selected via PowerComp.';
COMMENT ON COLUMN EOB.EOB_FULL_DESC IS 'The description as it would appear on a remittance report.';
COMMENT ON COLUMN EOB.EOB_NO_PAY_NTC_IND IS 'Indicator identifying  whether or not a seperate remittance report is to be produced to one or more participating customers.';
COMMENT ON COLUMN EOB.EOB_BILL_LN_ITEM_PRNT_IND IS 'Indicator identifying whether or not a bill line item with this code should show up on a remittance report.';
COMMENT ON COLUMN EOB.EOB_REMIT_PRNT_IND IS 'Indicator identifying whether or not an EOB should show up on a remittance report.';
COMMENT ON COLUMN EOB.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE PROCEDURE_CODE_TYPE (
	proc_cd_typ_cd VARCHAR(15) NOT NULL,
	proc_cd_typ_nm VARCHAR(80) NOT NULL,
	void_ind CHAR(1) DEFAULT 'n',
	CONSTRAINT XPK_PCT PRIMARY KEY (proc_cd_typ_cd)
);


COMMENT ON TABLE PROCEDURE_CODE_TYPE IS 'Code table containing possible procedure methods (ICD, CPT, HCPC). It was at one time required that this be recorded on the bill and that the procedure codes entered on the bill needed to be recorded by the chosen method. This is no longer a requirement.';
COMMENT ON COLUMN PROCEDURE_CODE_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_STATUS_STATE_XREF (
	bill_sts_stt_xref_id NUMERIC(31) NOT NULL,
	bill_sts_typ_cd VARCHAR(15),
	bill_stt_typ_cd VARCHAR(15),
	void_ind CHAR(1) DEFAULT 'n',
	CONSTRAINT XPK_BSSX PRIMARY KEY (bill_sts_stt_xref_id)
);


COMMENT ON TABLE BILL_STATUS_STATE_XREF IS 'Each bill status can only occur when the bill is within certain bill states.  This table is used to configure which bill statuses may occur with which bill states.';
COMMENT ON COLUMN BILL_STATUS_STATE_XREF.BILL_STS_STT_XREF_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN BILL_STATUS_STATE_XREF.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_STS_STT_ACTN_TRN_RSN_XREF (
	bssatrx_id NUMERIC(31) NOT NULL,
	bill_sts_stt_actn_typ_cd VARCHAR(15) NOT NULL,
	bill_trans_rsn_typ_cd VARCHAR(15),
	void_ind CHAR(1) DEFAULT 'n',
	CONSTRAINT XPK_BSSATRX PRIMARY KEY (bssatrx_id)
);


COMMENT ON TABLE BILL_STS_STT_ACTN_TRN_RSN_XREF IS 'Cross reference table used to indicate which transition reasons are available for each bill status / state combination.';
COMMENT ON COLUMN BILL_STS_STT_ACTN_TRN_RSN_XREF.BSSATRX_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN BILL_STS_STT_ACTN_TRN_RSN_XREF.BILL_TRANS_RSN_TYP_CD IS 'A bill transition reason available for the bill status / state combination.';
COMMENT ON COLUMN BILL_STS_STT_ACTN_TRN_RSN_XREF.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_STS_STT_ACTN_TRANS_XREF (
	bssatx_id NUMERIC(31) NOT NULL,
	bill_sts_stt_xref_id_fr NUMERIC(31),
	bill_sts_stt_xref_id_to NUMERIC(31),
	bill_sts_stt_actn_typ_cd VARCHAR(15),
	bill_trans_rsn_typ_cd_sys_dflt VARCHAR(15),
	bssatx_sys_ind CHAR(1) DEFAULT 'n',
	void_ind CHAR(1) DEFAULT 'n',
	CONSTRAINT XPK_BSSATX PRIMARY KEY (bssatx_id)
);


COMMENT ON TABLE BILL_STS_STT_ACTN_TRANS_XREF IS 'Defines possible transitions between bill status / state combinations. Each transition is associated with a single action, which is generally the method by which the user or system communicates the desired transition.';
COMMENT ON COLUMN BILL_STS_STT_ACTN_TRANS_XREF.BSSATX_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN BILL_STS_STT_ACTN_TRANS_XREF.BILL_STS_STT_XREF_ID_FR IS 'The bill status / state combination the action will transition from.  Used to determine what actions are currently available on a bill cycle.';
COMMENT ON COLUMN BILL_STS_STT_ACTN_TRANS_XREF.BILL_STS_STT_XREF_ID_TO IS 'The bill status / state combination the bill cycle will transition to, if the associated action is taken.';
COMMENT ON COLUMN BILL_STS_STT_ACTN_TRANS_XREF.BILL_STS_STT_ACTN_TYP_CD IS 'The action which results in the associated bill status / state transition.';
COMMENT ON COLUMN BILL_STS_STT_ACTN_TRANS_XREF.BILL_TRANS_RSN_TYP_CD_SYS_DFLT IS 'Code value indicating the system default code for bill status state transition reason.';
COMMENT ON COLUMN BILL_STS_STT_ACTN_TRANS_XREF.BSSATX_SYS_IND IS 'Indicator identifying whether the particular transition is available only to the system and should not be accessible by the user.';
COMMENT ON COLUMN BILL_STS_STT_ACTN_TRANS_XREF.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_STATUS_STATE_ACTION_TYPE (
	bill_sts_stt_actn_typ_cd VARCHAR(15) NOT NULL,
	bill_sts_stt_actn_typ_nm VARCHAR(80) NOT NULL,
	void_ind CHAR(1) DEFAULT 'n',
	CONSTRAINT XPK_BSSAT PRIMARY KEY (bill_sts_stt_actn_typ_cd)
);


COMMENT ON TABLE BILL_STATUS_STATE_ACTION_TYPE IS 'Code table containing all possible actions regarding bill status / state changes.';
COMMENT ON COLUMN BILL_STATUS_STATE_ACTION_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE EOB_CLASSIFICATION_TYPE (
	EOB_CLS_TYP_CD			VARCHAR(15) NOT NULL,
	EOB_CLS_TYP_NM			VARCHAR(80) NOT NULL,
	VOID_IND			CHAR(1) not null DEFAULT 'n',
	CONSTRAINT XPK_ECLST PRIMARY KEY(EOB_CLS_TYP_CD)
);


COMMENT ON TABLE EOB_CLASSIFICATION_TYPE IS 'To store eob classifaction types';
Comment on column EOB_CLASSIFICATION_TYPE.EOB_CLS_TYP_CD is 'To store unique code for eob classification type';
Comment on column EOB_CLASSIFICATION_TYPE.EOB_CLS_TYP_NM is 'To store description for eob classification type';


CREATE TABLE EOB_GROUP_TYPE (
	EOB_GRP_TYP_CD 			VARCHAR(15) NOT NULL,
	EOB_GRP_TYP_NM 			VARCHAR(80) NOT NULL,
	EOB_CLS_TYP_CD			VARCHAR(15),			
	EOB_GRP_TYP_PRTY_NO		NUMERIC(31) NOT NULL,			
	EOB_GRP_TYP_INTRNL_IND		CHAR(1) DEFAULT 'n',		
	EOB_GRP_TYP_SYS_IND		CHAR(1) DEFAULT 'n',		
	VOID_IND 			CHAR(1) not null DEFAULT 'n',
	CONSTRAINT XPK_EGT PRIMARY KEY (EOB_GRP_TYP_CD)
);


COMMENT ON TABLE EOB_GROUP_TYPE IS 'Code table containing EOB Groups.';
Comment on column EOB_GROUP_TYPE.EOB_GRP_TYP_PRTY_NO is 'To store priority number in which eob needs to be applied on line item/bill';
Comment on column EOB_GROUP_TYPE.EOB_GRP_TYP_INTRNL_IND is 'To indicate whether eob is created internally (not bill reviewer eob and not user eob)';
Comment on column EOB_GROUP_TYPE.EOB_GRP_TYP_SYS_IND is 'To indicate whether eob is created by system (not user)';
COMMENT ON COLUMN EOB_GROUP_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE EOB_ACTION_TYPE (
	eob_actn_typ_cd VARCHAR(15) NOT NULL,
	eob_actn_typ_nm VARCHAR(80) NOT NULL,
	void_ind CHAR(1) NOT NULL DEFAULT 'n',
	CONSTRAINT XPK_EAT PRIMARY KEY (eob_actn_typ_cd)
);


COMMENT ON TABLE EOB_ACTION_TYPE IS 'This table is used to define the actions associated to an EOB Code and will drive behavior as it relates to an amount associated to an EOB Code.';
COMMENT ON COLUMN EOB_ACTION_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BILL_LINE_ITEM_CODE_TYPE (
