);


COMMENT ON TABLE REGION_TYPE IS 'This table defines the various regions available for use in PowerComp?.';
COMMENT ON COLUMN REGION_TYPE.RGN_TYP_CD IS 'A unique code value to identify the specific type of region.';
COMMENT ON COLUMN REGION_TYPE.RGN_TYP_NM IS 'A user friendly name to describe the region type.';
COMMENT ON COLUMN REGION_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table APPOINTMENT_STATUS_TYPE
(
   APNT_STS_TYP_CD      VARCHAR(15)            not null,
