COMMENT ON TABLE APPOINTMENT_STATUS_TYPE IS 'This code table defines the applicable types of appointment statuses(Active, Pending, Expired).';
COMMENT ON COLUMN APPOINTMENT_STATUS_TYPE.APNT_STS_TYP_CD IS 'A unique code value to identify the specific appointment status.';
COMMENT ON COLUMN APPOINTMENT_STATUS_TYPE.APNT_STS_TYP_NM IS 'A user friendly name to describe the appointment status.';
COMMENT ON COLUMN APPOINTMENT_STATUS_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table APPOINTMENT_STATUS_REASON_TYPE
(
   APNT_STS_RSN_TYP_CD          VARCHAR(15)            not null,
   APNT_STS_RSN_TYP_NM          VARCHAR(80)            not null,
