);


COMMENT ON TABLE APPOINTMENT_STATUS_REASON_TYPE IS 'Code table containing all possible reasons for appointment status (License Copy Requested, Awaiting Confirmation, etc?).';
COMMENT ON COLUMN APPOINTMENT_STATUS_REASON_TYPE.APNT_STS_RSN_TYP_CD IS 'Internal appointment status reason code.';
COMMENT ON COLUMN APPOINTMENT_STATUS_REASON_TYPE.APNT_STS_RSN_TYP_NM IS 'External appointment status reason description.';
COMMENT ON COLUMN APPOINTMENT_STATUS_REASON_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table APPOINTMENT_STS_STS_RSN_XREF
(
