COMMENT ON COLUMN APPOINTMENT_STS_STS_RSN_XREF.VOID_IND is 'Indicates whether the record is voided';

create table ROLE_AGENT_AGENCY
(
   ROLE_AGNT_AGY_ID      	NUMERIC(31)            not null,
   ROLE_ID              	NUMERIC(31),
   ROLE_AGNT_AGY_NO		VARCHAR(30),
   AGY_TYP_CD           	VARCHAR(15),
   RGN_TYP_CD			VARCHAR(15),
   ROLE_AGNT_AGY_CNTC_NM 	VARCHAR(80),   
   ROLE_AGNT_AGY_ERR_OMSN_EXPR_DT TIMESTAMP,
   ROLE_AGNT_AGY_NO_UPCS        VARCHAR(30),  
   AUDIT_USER_ID_CREA   	NUMERIC(31),
   AUDIT_USER_CREA_DTM  	TIMESTAMP,
   AUDIT_USER_ID_UPDT   	NUMERIC(31),
   AUDIT_USER_UPDT_DTM  	TIMESTAMP,
   ROLE_AGNT_AGY_NO_1      NUMERIC(31),
   VOID_IND             	CHAR(1)        not null default 'n',
   constraint XPK_RAA primary key (ROLE_AGNT_AGY_ID)
);


COMMENT ON TABLE ROLE_AGENT_AGENCY IS 'This data table captures information about the role of agent and agency for a customer.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.ROLE_AGNT_AGY_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.ROLE_ID IS 'This is a foreign key to customer_role table, linking agent/agency details to base customer role information.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.ROLE_AGNT_AGY_NO IS 'This represents an agent/agency number assigned by the carrier to identify the agent/agency.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.AGY_TYP_CD IS 'This is a foreign key to agency_type table specifying the agency type associated with the record.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.RGN_TYP_CD IS 'This is a foreign key to region_type table specifying the region to which agent/agency belongs.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.ROLE_AGNT_AGY_CNTC_NM IS 'This represents a contact name associated with the agent/agency.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.ROLE_AGNT_AGY_NO_1 IS 'This represents the sub-type id of the Control Element Regional Office details.';
COMMENT ON COLUMN ROLE_AGENT_AGENCY.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

create table ROLE_AGENT_AGY_LOB
(
   RAAL_ID				NUMERIC(31)            not null,
   ROLE_AGNT_AGY_ID      		NUMERIC(31),
   LOB_TYP_CD           		VARCHAR(15),
   RAAL_APNT_EFF_DT			TIMESTAMP,
   RAAL_APNT_END_DT  			TIMESTAMP,
   RAAL_RECV_COMS_PAY_IND          CHAR(1)    NOT NULL DEFAULT 'n',
   APNT_STS_TYP_CD 			VARCHAR(15),
   APNT_STS_RSN_TYP_CD			VARCHAR(15),
   AUDIT_USER_ID_CREA                   NUMERIC(31)            not null,
   AUDIT_USER_CREA_DTM                  TIMESTAMP              not null,
   AUDIT_USER_ID_UPDT                   NUMERIC(31),
   AUDIT_USER_UPDT_DTM                  TIMESTAMP ,
   VOID_IND             		CHAR(1)      not null default 'n',
   constraint XPK_RAAL primary key (RAAL_ID)
);


COMMENT ON TABLE ROLE_AGENT_AGY_LOB IS 'This data table captures information about the line of businesses for agent/agency.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.RAAL_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.ROLE_AGNT_AGY_ID IS 'This is a foreign key to role_agent_agency table, specifying to which agent/agency this line of business detail belongs.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.LOB_TYP_CD IS 'This is a foreign key to line_of_business_type table, specifying the associated line of business to agent/agency.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.RAAL_APNT_EFF_DT IS 'This date represents the date on which the line of business is effective.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.RAAL_APNT_END_DT IS 'This date represents the date through which the line of business is effective.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.APNT_STS_TYP_CD IS 'This is a foreign key to appointment_status_type table, specifying the agents appointment status.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.APNT_STS_RSN_TYP_CD IS 'This is a foreign key to appointment_status_reason_type table, specifying the reason for agents appointment status.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN ROLE_AGENT_AGY_LOB.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
comment on column ROLE_AGENT_AGY_LOB.RAAL_RECV_COMS_PAY_IND is 'commission payment indicator';

CREATE TABLE FINANCIAL_TRANSACTION_CLASS
(
  FNCL_TRAN_CLS_ID           NUMERIC(31) NOT NULL,
  FNCL_TRAN_CLS_CD           VARCHAR(15) NOT NULL,
  FNCL_TRAN_CLS_NM           VARCHAR(80) NOT NULL,
  AGRE_TYP_CD                VARCHAR(15),
  FNCL_TRAN_CLS_DSPLY_IND    CHAR(1) NOT NULL DEFAULT 'y',
  VOID_IND                   CHAR(1) NOT NULL DEFAULT 'n',
  CONSTRAINT XPK_FTC PRIMARY KEY (FNCL_TRAN_CLS_ID)
);


COMMENT ON TABLE FINANCIAL_TRANSACTION_CLASS IS 'Code table to define the classes of financial transactions that can exist.  Used for classification of transactions.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_CLASS.FNCL_TRAN_CLS_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_CLASS.FNCL_TRAN_CLS_CD IS 'Code value used to identify the specific financial transaction class.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_CLASS.FNCL_TRAN_CLS_NM IS 'Name used to display to user the specific financial transaction class.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_CLASS.AGRE_TYP_CD IS 'Code value used to identify which agreement type this financial transaction class can be used for.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_CLASS.FNCL_TRAN_CLS_DSPLY_IND IS 'Indicator identifying whether or not the specific financial transaction class can display on the Java application.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_CLASS.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE FINANCIAL_TRANSACTION_FILTER
(
  FNCL_TRAN_FLTR_ID          NUMERIC(31) NOT NULL,
  FNCL_TRAN_FLTR_CD          VARCHAR(15) NOT NULL,
  FNCL_TRAN_FLTR_NM          VARCHAR(80) NOT NULL,
  AGRE_TYP_CD                VARCHAR(15),
  FNCL_TRAN_CLS_ID           NUMERIC(31),
  VOID_IND                   CHAR(1),
  CONSTRAINT XPK_FTF PRIMARY KEY (FNCL_TRAN_FLTR_ID)
);


COMMENT ON TABLE FINANCIAL_TRANSACTION_FILTER IS 'Code table to define the filter menus on the front end for what items will display based on the given context.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_FILTER.FNCL_TRAN_FLTR_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_FILTER.FNCL_TRAN_FLTR_CD IS 'A code value used to help identify what class of transactions or actions are represented by items in the financial transaction filter menus.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_FILTER.FNCL_TRAN_FLTR_NM IS 'Name used to display to the user the menu item in the filter.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_FILTER.AGRE_TYP_CD IS 'Code value used to identify which agreement type this filter menu item is for.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_FILTER.FNCL_TRAN_CLS_ID IS 'Foreign key reference to which financial transaction class this filter menu item maps to.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_FILTER.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE FINANCIAL_TRANSACTION_TYPE
(
  FNCL_TRAN_TYP_ID              NUMERIC(31) NOT NULL,
  FNCL_TRAN_TYP_CD              VARCHAR(15) NOT NULL,
  FNCL_TRAN_TYP_NM              VARCHAR(80) NOT NULL,
  AGRE_TYP_CD                   VARCHAR(15),
  FNCL_TRAN_TYP_GNRL_LDGR_IND   CHAR(1) NOT NULL DEFAULT 'n',
  GNRL_LDGR_ACCT_TYP_CD         VARCHAR(60),
  FNCL_TRAN_TYP_ACCT_PYBL_IND   CHAR(1) NOT NULL DEFAULT 'n',
  VOID_IND                      CHAR(1) NOT NULL DEFAULT 'n',
  CONSTRAINT XPK_FNCLTT PRIMARY KEY (FNCL_TRAN_TYP_ID)
);


COMMENT ON TABLE FINANCIAL_TRANSACTION_TYPE IS 'Code table to define the type of financial transactions that can exist by different agreement types.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_TYPE.FNCL_TRAN_TYP_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_TYPE.FNCL_TRAN_TYP_CD IS 'Code value used to identify the specific financial transaction type.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_TYPE.FNCL_TRAN_TYP_NM IS 'Name used to display to the user the specific financial transaction type.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_TYPE.AGRE_TYP_CD IS 'Code value used to identify which agreement type this financial transaction type can be used for.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_TYPE.FNCL_TRAN_TYP_GNRL_LDGR_IND IS 'Indicator identifying whether or not this financial transaction type is to be interfaced to the general ledger.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_TYPE.GNRL_LDGR_ACCT_TYP_CD IS 'Code value used to identify which general ledger account this financial transaction type interfaces to.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_TYPE.FNCL_TRAN_TYP_ACCT_PYBL_IND IS 'Indicator identifying whether or not this financial transaction type is to be interfaced to the accounts payable system.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';

