-- Adding FTAT_MNL_UNAPLD_IND column.
CREATE TABLE FINANCIAL_TRANSACTION_APP_TYP
(
  FTAT_ID                  NUMERIC(31) NOT NULL,
  FTAT_CD                  VARCHAR(15) NOT NULL,
  FTAT_NM                  VARCHAR(110) NOT NULL,

  AGRE_TYP_CD              VARCHAR(15),
  FTAT_GNRL_LDGR_IND       CHAR(1) NOT NULL DEFAULT 'n',
  GNRL_LDGR_ACCT_TYP_CD    VARCHAR(15),
  FTAT_ACCT_PYBL_IND       CHAR(1) NOT NULL DEFAULT 'n',
  FTAT_COMS_IND            CHAR(1) NOT NULL DEFAULT 'n',
  FTAT_MNL_UNAPLD_IND	   CHAR(1) NOT NULL DEFAULT 'n',
  VOID_IND                 CHAR(1) NOT NULL DEFAULT 'n',
  CONSTRAINT XPK_FTAT PRIMARY KEY (FTAT_ID)
);


COMMENT ON TABLE FINANCIAL_TRANSACTION_APP_TYP IS 'Code table to define the types of application scenarios that can exist between different types of financial transactions.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_APP_TYP.FTAT_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_APP_TYP.FTAT_CD IS 'Code value used to identify the specific finanical transaction application type.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_APP_TYP.FTAT_NM IS 'Name used to display to the user the specific financial transaction application type.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_APP_TYP.AGRE_TYP_CD IS 'Code value used to identify which agreement type this financial transaction application type can be used for.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_APP_TYP.FTAT_GNRL_LDGR_IND IS 'Indicator identifying whether or not this financial transaction application type is to be interfaced to the general ledger.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_APP_TYP.GNRL_LDGR_ACCT_TYP_CD IS 'Code value used to identify which general ledger account this financial transaction application type interfaces to.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_APP_TYP.FTAT_ACCT_PYBL_IND IS 'Indicator identifying whether or not this financial transaction application type is to be interfaced to the accounts payable system.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_APP_TYP.FTAT_COMS_IND IS 'Indicator identifying whether or not this financial transaction application type is to be used in the commissions process.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_APP_TYP.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE FINANCIAL_TRAN_APP_TRAN_TYP
(
  FNCL_TRAN_APP_TRAN_TYP_ID       NUMERIC(31) NOT NULL,
  FTAT_ID                         NUMERIC(31),
  AGRE_TYP_CD                     VARCHAR(15),
  FNCL_TRAN_TYP_ID_APLD_FR        NUMERIC(31),
  FNCL_TRAN_TYP_ID_APLD_TO        NUMERIC(31),
  FNCL_TRAN_APP_TRAN_TYP_MLTPLR   NUMERIC(31) NOT NULL,
  VOID_IND                        CHAR(1) NOT NULL DEFAULT 'n',
  CONSTRAINT XPK_FTATT PRIMARY KEY (FNCL_TRAN_APP_TRAN_TYP_ID)
);


COMMENT ON TABLE FINANCIAL_TRAN_APP_TRAN_TYP IS 'Code table to define the specific application scenarios between distinctive types of financial transactions.  In a credit to debit application scenario, defines what can be applied from what transaction type to what other financial transaction type.';
COMMENT ON COLUMN FINANCIAL_TRAN_APP_TRAN_TYP.FNCL_TRAN_APP_TRAN_TYP_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN FINANCIAL_TRAN_APP_TRAN_TYP.FTAT_ID IS 'Foreign key reference to the specific financial transaction application type defined for this application scenario.';
COMMENT ON COLUMN FINANCIAL_TRAN_APP_TRAN_TYP.AGRE_TYP_CD IS 'Code value used to identify which agreement type this financial transaction application scenario can be used for.';
COMMENT ON COLUMN FINANCIAL_TRAN_APP_TRAN_TYP.FNCL_TRAN_TYP_ID_APLD_FR IS 'Foreign key reference to the specific financial transaction type that can be applied from.';
COMMENT ON COLUMN FINANCIAL_TRAN_APP_TRAN_TYP.FNCL_TRAN_TYP_ID_APLD_TO IS 'Foreign key reference to the specific financial transaction type that can be applied to.';
COMMENT ON COLUMN FINANCIAL_TRAN_APP_TRAN_TYP.FNCL_TRAN_APP_TRAN_TYP_MLTPLR IS 'Numeric value specifying if the application scenario should be positive (1) or negetive (-1).';
COMMENT ON COLUMN FINANCIAL_TRAN_APP_TRAN_TYP.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE FINANCIAL_TRAN_CLS_TRAN_TYP
(
  FNCL_TRAN_CLS_TRAN_TYP_ID       NUMERIC(31) NOT NULL,
  FNCL_TRAN_CLS_ID                NUMERIC(31),
  FNCL_TRAN_TYP_ID                NUMERIC(31),
  FNCL_TRAN_CLS_TRAN_TYP_MLTPLR   NUMERIC(31) NOT NULL,
  FNCL_TRAN_CLS_TRAN_SYS_IND	  CHAR(1) NOT NULL DEFAULT 'n',
  VOID_IND                        CHAR(1) NOT NULL DEFAULT 'n',
  CONSTRAINT XPK_FTCTT PRIMARY KEY (FNCL_TRAN_CLS_TRAN_TYP_ID)
);


COMMENT ON TABLE FINANCIAL_TRAN_CLS_TRAN_TYP IS 'Code table to define the individual transaction types that can be present in a given financial class based on the sign (positive or negetive) of the originally created transaction.';
COMMENT ON COLUMN FINANCIAL_TRAN_CLS_TRAN_TYP.FNCL_TRAN_CLS_TRAN_TYP_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN FINANCIAL_TRAN_CLS_TRAN_TYP.FNCL_TRAN_CLS_ID IS 'Foreign key reference to the specific financial transaction class used.';
COMMENT ON COLUMN FINANCIAL_TRAN_CLS_TRAN_TYP.FNCL_TRAN_TYP_ID IS 'Foreign key reference to the specific financial transaction type used.';
COMMENT ON COLUMN FINANCIAL_TRAN_CLS_TRAN_TYP.FNCL_TRAN_CLS_TRAN_TYP_MLTPLR IS 'Numeric value specifying whether a transaction can exist in this class based on its original balance of positive (1) or negetive (-1).';
COMMENT ON COLUMN FINANCIAL_TRAN_CLS_TRAN_TYP.FNCL_TRAN_CLS_TRAN_SYS_IND IS 'An indicator identifying whether class and transaction combination is system only or not. If y, that means user will not have the access to those transactions in that class';
COMMENT ON COLUMN FINANCIAL_TRAN_CLS_TRAN_TYP.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE FINANCIAL_TRANSACTION_REVERSE
(
  FNCL_TRAN_RVRS_ID       NUMERIC(31) NOT NULL,
  FNCL_TRAN_TYP_ID        NUMERIC(31),
  FNCL_TRAN_TYP_ID_RVRS   NUMERIC(31),
  VOID_IND                CHAR(1) NOT NULL DEFAULT 'n',
  CONSTRAINT XPK_FTR PRIMARY KEY (FNCL_TRAN_RVRS_ID)
);


COMMENT ON TABLE FINANCIAL_TRANSACTION_REVERSE IS 'Code table to define what specific type of financial transaction can reverse or undo another specific type of financial transaction.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_REVERSE.FNCL_TRAN_RVRS_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_REVERSE.FNCL_TRAN_TYP_ID IS 'Foreign key reference to the specific financial transaction type which can be reversed.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_REVERSE.FNCL_TRAN_TYP_ID_RVRS IS 'Foreign key reference to the specific financial transaction type which can do the reversing.';
COMMENT ON COLUMN FINANCIAL_TRANSACTION_REVERSE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE POLICY_FINANCIAL_TRANSACTION
(
  PFT_ID                      NUMERIC(31) NOT NULL,
  FNCL_TRAN_TYP_ID            NUMERIC(31),
  AGRE_ID                     NUMERIC(31) NOT NULL,
  PFT_AMT                     NUMERIC(15,2) NOT NULL,
  PFT_DT                      TIMESTAMP NOT NULL,
  PFT_DUE_DT                  TIMESTAMP,
  PFT_RVS_DUE_DT 			  TIMESTAMP,
  PFT_RVS_RSN_TXT 			  VARCHAR(40),
  PFT_DRV_BAL_AMT             NUMERIC(15,2),
  PFT_ID_RVRS                 NUMERIC(31),
  PAY_BTCH_ID                 NUMERIC(31),
  PAY_BTCH_DTL_ID             NUMERIC(31),
  PFT_NOTE_IND                CHAR(1),
  PFT_RVRS_IND                CHAR(1) NOT NULL DEFAULT 'y',
  PFT_EFT_PYMT_PRCSR_SENT_DT       TIMESTAMP,
  PFT_SENT_DT                 TIMESTAMP,
  PFT_GNRL_LDGR_SENT_IND      CHAR(1) NOT NULL DEFAULT 'n',
  PFT_ACCT_PYBL_SENT_IND      CHAR(1) NOT NULL DEFAULT 'n',
  CUST_ID                     NUMERIC(31),
  PTCP_ID                     NUMERIC(31),
  AGRE_TYP_CD                 VARCHAR(15),
  PLCY_PRD_ID                 NUMERIC(31),
  PREM_PRD_ID                 NUMERIC(31),
  INVC_ID                     NUMERIC(31),
  CLTRL_DRW_ID				  NUMERIC(31),
  PYRL_RPT_ID                 NUMERIC(31),
  PFT_CMT                     VARCHAR(256),
  PFT_EFT_IND				CHAR(1) NOT NULL DEFAULT 'n',
  JUR_TYP_CD		      VARCHAR(15),
  PAY_TRK_SRC_TYP_CD          VARCHAR(15),
  PFT_DRV_PAY_TRK_SRC_TYP_NM  VARCHAR(80),
  PFT_RVS_REQD_IND 			CHAR(1) NOT NULL DEFAULT 'n',
  PLCY_PRD_GRP_ID			  NUMERIC(31),
  PFT_ID_SRC                  NUMERIC(31),
  LST_INTE_DT				  TIMESTAMP,
  AUDIT_USER_ID_CREA          NUMERIC(31),
  AUDIT_USER_CREA_DTM         TIMESTAMP,
  AUDIT_USER_ID_UPDT          NUMERIC(31),
  AUDIT_USER_UPDT_DTM         TIMESTAMP,
  CONSTRAINT XPK_PFT PRIMARY KEY (PFT_ID)
);


COMMENT ON TABLE POLICY_FINANCIAL_TRANSACTION IS 'This is a data table that captures individual financial transactions for a given policy agreement (policy or quote).';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.FNCL_TRAN_TYP_ID IS 'Foreign key reference specifying the specific type of financial transaction that this row represents.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.AGRE_ID IS 'Foreign key reference specifying the specific agreement for which this financial transaction was created.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_AMT IS 'Original amount of the financial transaction.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_DT IS 'Creation date of the financial transaction.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_DUE_DT IS 'Date by which resolution is due for the financial transaction before it is considered overdue.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_DRV_BAL_AMT IS 'Running or remaining balance amount for the financial transaction.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_ID_RVRS IS 'Foreign key reference to the financial transaction which has reversed this transaction.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PAY_BTCH_ID IS 'Foreign key reference to the cash payment batch which created this transaction.  -  this is the cash_receipt_header.cash_rect_hdr_id';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PAY_BTCH_DTL_ID IS 'Foreign key reference to the specific line item of the cash payment batch which created this transaction. - this is the cash_receipt_line_item_detail.crlid_id';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_NOTE_IND IS 'Indicator identifying whether or not this specific transaction has a note attached to it.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_RVRS_IND IS 'Indicator identifying whether the transaction can still be reversed (value y) or not keep in mind is this means the transaction is reversible if set to y.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_SENT_DT IS 'Date which the transaction was sent to the general ledger or accounts payable systems if applicable.  -  Generally, it is best to do a final AP and GL run to send all data to be converted to GL and AP - that way, this will always have a date';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_GNRL_LDGR_SENT_IND IS 'Indicator identifying whether or not the transaction has been interfaced to the general ledger system.    -  Generally, it is best to do a final AP and GL run to send all data to be converted to GL and AP - that way, this indicator will always be y';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_ACCT_PYBL_SENT_IND IS 'Indicator identifying whether or not the transaction has been interfaced to the accounts payable system.  -  Generally, it is best to do a final AP and GL run to send all data to be converted to GL and AP - that way, this indicator will always be y';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.CUST_ID IS 'Foreign key reference to the customer associated with this financial transaction.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PTCP_ID IS 'Foreign key reference to the participation type in the agreement for the customer associated with this financial transaction.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.AGRE_TYP_CD IS 'Code value used to identify which agreement type this financial transaction was for.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PLCY_PRD_ID IS 'Foreign key reference to the policy period that this financial transaction was created for.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PREM_PRD_ID IS 'Foreign key reference to the premium period that this financial transaction was created for.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.INVC_ID IS 'Foreign key refernce to the invoice that this financial transaction last appeared on.';
comment on column POLICY_FINANCIAL_TRANSACTION.CLTRL_DRW_ID is 'Foreign key to POLICY_ACCOUNTING_TRANSACTION table.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PYRL_RPT_ID IS 'Foreign key reference to the payroll report that this financial transaction was created for.  Try to associate with each individual payroll report ID if possible or Powersuite will not act correctly on active peiods when the next report is entered.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_CMT IS 'Comment entered on the creation of this specific financial transaction.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_ID_SRC IS 'The record Id of the PFT for which the interest was calculated for this specific financial transaction.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.LST_INTE_DT IS 'Last Date and time on which the interest was calculated for this specific financial transaction.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.JUR_TYP_CD IS 'Code value used to identify which JURISDICTION_TYPE this financial transaction was on';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PAY_TRK_SRC_TYP_CD IS 'This is a foreign key from the PAYMENT_TRACK_SOURCE_TYPE table';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_EFT_IND IS 'Indicator to identify PFTs that have been sent to the the EFT Payment Processor';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_EFT_PYMT_PRCSR_SENT_DT IS 'Date this PFT was sent to the EFT Payment processor';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_DRV_PAY_TRK_SRC_TYP_NM is 'Derived payment track source type name';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_RVS_DUE_DT IS 'BRAC Specific: If this date is set, it will be considered as Due Date by which resolution is due for the financial transaction before it is considered overdue.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_RVS_RSN_TXT IS 'BRAC Specific: Revised Due Date Reason if Revised Due Date is set.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PFT_RVS_REQD_IND IS 'BRAC Specific: Indicator to flag a transaction for which revised due date and reason need to be set.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRANSACTION.PLCY_PRD_GRP_ID  IS 'Foreign key to POLICY_PERIOD_GROUP table. Identifies the location group to which the financial transaction is associated.';

CREATE TABLE POLICY_FINANCIAL_TRAN_APP
(
  PFTA_ID                        NUMERIC(31) NOT NULL,
  FTAT_ID                        NUMERIC(31),
  PFTA_AMT                       NUMERIC(15,2) NOT NULL,
  PFTA_DT                        TIMESTAMP NOT NULL,
  PFT_ID_APLD_FR                 NUMERIC(31),
  PFT_ID_APLD_TO                 NUMERIC(31),
  PFT_ID_RVRS                    NUMERIC(31),
  PFT_APLD_FR_AMT				 NUMERIC(15,2) DEFAULT 0 NOT NULL,
  PFT_APLD_TO_AMT				 NUMERIC(15,2) DEFAULT 0 NOT NULL,
  PFTA_SENT_DT                   TIMESTAMP,
  PFTA_GNRL_LDGR_SENT_IND        CHAR(1) NOT NULL DEFAULT 'n',
  PFTA_ACCT_PYBL_SENT_IND        CHAR(1) NOT NULL DEFAULT 'n',
  PFTA_COMS_FR_IND		 CHAR(1) NOT NULL DEFAULT 'n',
  PFTA_COMS_TO_IND		 CHAR(1) NOT NULL DEFAULT 'n',
  PFTA_ORIG_APP_IND CHAR(1)   NOT NULL DEFAULT 'n' ,
  AUDIT_USER_ID_CREA             NUMERIC(31) NOT NULL ,
  AUDIT_USER_CREA_DTM            TIMESTAMP NOT NULL ,
  AUDIT_USER_ID_UPDT             NUMERIC(31),
  AUDIT_USER_UPDT_DTM            TIMESTAMP,
  CONSTRAINT XPK_PFTA PRIMARY KEY (PFTA_ID)
);


COMMENT ON TABLE POLICY_FINANCIAL_TRAN_APP IS 'This is a data table that captures the application information between two specific financial transactions for a policy type agreement (policy or quote).';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFTA_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.FTAT_ID IS 'Foreign key reference specifying the specific type of financial transaction application that this row represents.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFTA_AMT IS 'The amount of the application that has taken place (always a positive number).';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFTA_DT IS 'The date on which this application was created.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFT_ID_APLD_FR IS 'Foreign key refernce to the specific financial transaction that was applied from.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFT_ID_APLD_TO IS 'Foreign key refernce to the specific financial transaction that was applied to.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFT_APLD_FR_AMT IS 'The amount of the From transaction after application has taken place.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFT_APLD_TO_AMT IS 'The amount of the To transaction after application has taken place.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFT_ID_RVRS IS 'Foreign key refernce to the specific financial transaction that was reversed resulting in the creation of this application.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFTA_SENT_DT IS 'Date which the transaction was sent to the general ledger or accounts payable systems if applicable.  -  Generally, it is best to do a final AP and GL run to send all data to be converted to GL and AP - that way, this will always have a date';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFTA_GNRL_LDGR_SENT_IND IS 'Indicator identifying whether or not the transaction has been interfaced to the general ledger system.  -  Generally, it is best to do a final AP and GL run to send all data to be converted to GL and AP - that way, this indicator will always be y';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFTA_ACCT_PYBL_SENT_IND IS 'Indicator identifying whether or not the transaction has been interfaced to the accounts payable system.  -  Generally, it is best to do a final AP and GL run to send all data to be converted to GL and AP - that way, this indicator will always be y';
comment on column POLICY_FINANCIAL_TRAN_APP.PFTA_ORIG_APP_IND  is 'Original Application Indicator. Set value to y on original applications.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFTA_COMS_TO_IND IS 'Should be y if the FTAT_ID is commissionable, the FROM fncl_tran_typ_id is one that is in the class for commissionable, and the commission has been paid  -  else should be n';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_APP.PFTA_COMS_FR_IND IS 'Should be y if the FTAT_ID is commissionable, the FROM fncl_tran_typ_id is one that is in the class for commissionable, and the commission has been paid  -  else would be n';


CREATE TABLE POLICY_FINANCIAL_TRAN_TRN
(
  PLCY_FNCL_TRAN_TRN_ID          NUMERIC(31) NOT NULL,
  PFT_ID_TRN_FR                  NUMERIC(31),
  PFT_ID_TRN_TO                  NUMERIC(31),
  CONSTRAINT XPK_PFTT PRIMARY KEY (PLCY_FNCL_TRAN_TRN_ID)
);


COMMENT ON TABLE POLICY_FINANCIAL_TRAN_TRN IS 'This is a data table that keeps track of transfers between two different policy agreements (policy or quote) by capturing the financial transaction identifiers of both the transferred from and transferred to agreements.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_TRN.PLCY_FNCL_TRAN_TRN_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_TRN.PFT_ID_TRN_FR IS 'Foreign key reference to the specific financial transaction that was created to transfer monies to another agreement.';
COMMENT ON COLUMN POLICY_FINANCIAL_TRAN_TRN.PFT_ID_TRN_TO IS 'Foreign key reference to the specific financial transaction that was created from the transfer of monies from another agreement.';


CREATE TABLE CHECK_REQUEST
(
  CHCK_REQS_ID                    NUMERIC(31) NOT NULL,
  CHCK_REQS_DRV_AMT               NUMERIC(9,2),
  CHCK_REQS_ISS_CHCK_NO           VARCHAR(25),
  PAY_TYP_CD                      VARCHAR(15),
  PAY_MEDA_PREF_TYP_CD            VARCHAR(15),
  CHCK_REQS_CHCK_SET_NO           NUMERIC(31),
  CONSTRAINT XPK_CHCKR PRIMARY KEY (CHCK_REQS_ID)
);


COMMENT ON TABLE CHECK_REQUEST IS 'This data table captures details about the checks being sent (amount, bank, etc.)';
COMMENT ON COLUMN CHECK_REQUEST.CHCK_REQS_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN CHECK_REQUEST.CHCK_REQS_DRV_AMT IS 'The derived amount for the check request';
COMMENT ON COLUMN CHECK_REQUEST.CHCK_REQS_ISS_CHCK_NO IS 'The check number issued for the request';
COMMENT ON COLUMN CHECK_REQUEST.PAY_TYP_CD IS 'This is a foreign key to the PAYMENT_TYPE table identifying the payment type';
COMMENT ON COLUMN CHECK_REQUEST.PAY_MEDA_PREF_TYP_CD IS 'This is a foreign key to the PAYMENT_MEDIA_PREFERENCE_TYPE table identifying the media preference type';
COMMENT ON COLUMN CHECK_REQUEST.CHCK_REQS_CHCK_SET_NO IS 'The check set number to be associated with this payment';


CREATE TABLE CHECKS
(
  CHCK_ID                    NUMERIC(31) NOT NULL,
  PAY_TYP_CD                 VARCHAR(15),
  PAY_MEDA_PREF_TYP_CD       VARCHAR(15),
  CHCK_SET_NO                NUMERIC(31) NOT NULL,
  ACCT_TYP_CD                VARCHAR(15),
  CHCK_STR_CHCK_NO           NUMERIC(31) NOT NULL,
  CHCK_MAX_CHCK_NO           NUMERIC(31) NOT NULL,
  CHCK_LST_USED_CHCK_NO      NUMERIC(31) NOT NULL,
  CHCK_FRMT_LNGTH            NUMERIC(31) NOT NULL,
  VOID_IND                   CHAR(1) NOT NULL DEFAULT 'n',
  CONSTRAINT XPK_CHCK PRIMARY KEY (CHCK_ID)
);


COMMENT ON TABLE CHECKS IS 'This data table captures information of checks created in the CoreSuite system';
COMMENT ON COLUMN CHECKS.CHCK_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN CHECKS.PAY_TYP_CD IS 'This is a foreign key to the PAYMENT_TYPE table identifying the payment type';
COMMENT ON COLUMN CHECKS.PAY_MEDA_PREF_TYP_CD IS 'This is a foreign key to the PAYMENT_MEDIA_PREFERENCE_TYPE table identifying the media preference type';
COMMENT ON COLUMN CHECKS.CHCK_SET_NO IS 'The check set number to be associated with this payment';
COMMENT ON COLUMN CHECKS.ACCT_TYP_CD IS 'This is a foreign key to the ACCOUNT_TYPE table identifying the account type';
COMMENT ON COLUMN CHECKS.CHCK_STR_CHCK_NO IS 'The starting check number for the account';
COMMENT ON COLUMN CHECKS.CHCK_MAX_CHCK_NO IS 'The maximum check number for the account';
COMMENT ON COLUMN CHECKS.CHCK_LST_USED_CHCK_NO IS 'The last used check number on the account';
COMMENT ON COLUMN CHECKS.CHCK_FRMT_LNGTH IS 'The check format length';
COMMENT ON COLUMN CHECKS.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE CHECK_PAYEE
(
  CHCK_PAYE_ID         NUMERIC(31) NOT NULL,
  CHCK_REQS_ID         NUMERIC(31),
  CHCK_PAYE_NO         NUMERIC(31) NOT NULL,
  PAY_TYP_CD           VARCHAR(15),
  CUST_ID              NUMERIC(31),
  CHCK_PAYE_ADDR_IND   CHAR(1),
  CHCK_PAYE_DRV_NM     VARCHAR(400),
  CHCK_PAYE_CNTRY_NM   VARCHAR(80),
  CHCK_PAYE_STT_NM     VARCHAR(40),
  CHCK_PAYE_CNTY_NM    VARCHAR(80),
  CHCK_PAYE_ADDR_STR_1 VARCHAR(40),
  CHCK_PAYE_ADDR_STR_2 VARCHAR(40),
  CHCK_PAYE_CITY_NM    VARCHAR(80),
  CHCK_PAYE_POST_CD    VARCHAR(15),
  CONSTRAINT XPK_CHCKP PRIMARY KEY (CHCK_PAYE_ID)
);


COMMENT ON TABLE CHECK_PAYEE IS 'This data table captures payee information for the checks being sent';
