COMMENT ON COLUMN CHECK_PAYEE.PAY_TYP_CD IS 'This is a foreign key to the PAYMENT_TYPE table identifying the payment type';
COMMENT ON COLUMN CHECK_PAYEE.CUST_ID IS 'This is a foreign key to the CUSTOMER table identifying the customer as payee';
COMMENT ON COLUMN CHECK_PAYEE.CHCK_PAYE_ADDR_IND IS 'This is an indicator for having the payee address';
COMMENT ON COLUMN CHECK_PAYEE.CHCK_PAYE_DRV_NM IS 'The derived name for the payee';
COMMENT ON COLUMN CHECK_PAYEE.CHCK_PAYE_CNTRY_NM IS 'The country name for the payee address';
COMMENT ON COLUMN CHECK_PAYEE.CHCK_PAYE_STT_NM IS 'The state name for the the payee address';
COMMENT ON COLUMN CHECK_PAYEE.CHCK_PAYE_CNTY_NM IS 'The county name for the payee address';
COMMENT ON COLUMN CHECK_PAYEE.CHCK_PAYE_ADDR_STR_1 IS 'The address line 1 of the street address for the payee';
COMMENT ON COLUMN CHECK_PAYEE.CHCK_PAYE_ADDR_STR_2 IS 'The address line 2 of the street address for the payee';
COMMENT ON COLUMN CHECK_PAYEE.CHCK_PAYE_CITY_NM IS 'The city name for the payee address';
COMMENT ON COLUMN CHECK_PAYEE.CHCK_PAYE_POST_CD IS 'The postal code for the payee address';


