		BNFT_RPT_TYP_ID 	 		  NUMERIC(31),
        indm_pay_eff_dt		      	  TIMESTAMP,
        indm_pay_end_dt               TIMESTAMP,
        indm_pay_drv_wek              NUMERIC(31,10),
        indm_pay_drv_dd               NUMERIC(31,10),
        indm_pay_bnft_rt_amt          NUMERIC(31,10),
        indm_pay_calc_bnft_rt_amt     NUMERIC(31,10),
        indm_pay_ovrd_bnft_rt_amt     NUMERIC(31,10),
        indm_pay_ovrd_bnft_rt_cmt     VARCHAR(256),
        indm_pay_drv_tot_amt          NUMERIC(31,10),
        indm_pay_drv_sch_tot_amt      NUMERIC(31,10),
        indm_pay_ovrd_post_injr_amt   NUMERIC(31,10),
        indm_pay_dd_not_pd            NUMERIC(5),
        indm_pay_ovrd_post_injr_comt  VARCHAR(256),
        indm_pay_recalc_ind 	      CHAR(1)   NOT NULL DEFAULT 'n',
        INDM_PAY_NOTE_IND             CHAR(1)       default 'n', 
        indm_rsn_typ_cd 			  VARCHAR(15),
        CALC_RSLT_ID      			  NUMERIC(31),
        clm_avg_wg_id 				  NUMERIC(31),
