        void_ind CHAR(1) NOT NULL DEFAULT 'n',
    CONSTRAINT xpk_ip PRIMARY KEY (indm_pay_id)
 );


COMMENT ON TABLE INDEMNITY_PAYMENT IS 'Table holds the data for the Claim Indemnity payments.';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN INDEMNITY_PAYMENT.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN INDEMNITY_PAYMENT.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN INDEMNITY_PAYMENT.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN INDEMNITY_PAYMENT.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN INDEMNITY_PAYMENT.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN INDEMNITY_PAYMENT.AGRE_ID IS 'The agreement ID of the claim  to which this indemnity payment belongs';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_NOTE_IND IS 'Whether or not a note associated to the indemnity payment exists';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_OVRD_BNFT_RT_AMT IS 'Represents the ability to manually enter a Benefit rate amount to be used in calculating the Benefit Payment ';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_OVRD_BNFT_RT_CMT IS 'A comment associated with the overridden benefit rate  - There must be a Comment if an Override Benefit Rate Amount was used';
COMMENT ON COLUMN INDEMNITY_PAYMENT.CLM_AVG_WG_ID IS 'Foreign key to the claim average wage table for the average wage used in the calculation of the payment';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_CALC_BNFT_RT_AMT IS 'The calculated benefit rate amount';
COMMENT ON COLUMN INDEMNITY_PAYMENT.CALC_RSLT_ID IS 'Foreign key to the calculation result table associated with the calculation of this indemnity payment';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_DD_NOT_PD IS 'The number of days that the claims did not work, but are not being paid on this payment due to jurisdictional statutes';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_OVRD_POST_INJR_AMT IS 'Represents the ability to manually enter a Post-Injury Wage amount to be used in calculating the Benefit Rate ';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_DRV_WEK IS 'Represents the total number of weeks between the Indemnity Start Date and Indemnity End Date';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_END_DT IS 'Represents the last day of indemnity benefits for this Indemnity Payment Range';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_DRV_SCH_TOT_AMT IS 'Represents the total amount that will be paid out to the Claimant on a payment schedule, and has been reduced for any applicable Offsets, Lump Sum Discounts and Waiting Period days.  ';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_OVRD_POST_INJR_COMT IS 'A comment associated with the overridden post injury amount - There must be a Post-Injury Comment if an Override Post-Injury Wage Amount was used';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_RSN_TYP_CD IS 'Foreign key to the indemnity reason type table for a reason associated with the payment';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_BNFT_RT_AMT IS 'The benfit rate amount that was used on the indemnity payment';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_DRV_TOT_AMT IS 'Represents the total amount due for this Benefit Type for the Indemnity Payment range using the Benefit Rate, including Add-Ons, reductions for Apportionment and reductions for Waiting Period rules, but not including any reductions for Offsets';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_DRV_DD IS 'Represents the total number of days between the Indemnity Start Date and Indemnity End Date';
COMMENT ON COLUMN INDEMNITY_PAYMENT.CLM_EARN_CAP_ID IS 'Foreign key to the claim earnings capacity table';
COMMENT ON COLUMN INDEMNITY_PAYMENT.BNFT_TYP_CD IS 'The benefit type that this payment applies to';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_RECALC_IND IS 'Whether or not this indemnity payment recalced';
COMMENT ON COLUMN INDEMNITY_PAYMENT.BNFT_RPT_TYP_ID IS 'Represents the Benefit Type that will be reported to an external agency.  The user must select a Reporting Benefit Type from a list of valid values.  The values will be filtered based on the selected Benefit Type.';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_EFF_DT IS 'Represents the first day of indemnity benefits for this Indemnity Payment Range';
COMMENT ON COLUMN INDEMNITY_PAYMENT.INDM_PAY_DO_NOT_UPDT_IND IS 'Represents if the Indemnity record can be updated or not. If value is set to "Y" then record cannnot be updated';
COMMENT ON COLUMN INDEMNITY_PAYMENT.CNCURNCY_ID IS 'This column needs to be used only for optimistic concurrency support ONLY.';

CREATE TABLE benefit_cycle_type (
	bnft_cyc_typ_cd VARCHAR(15) NOT NULL,
	bnft_cyc_typ_nm VARCHAR(80) NOT NULL,
	void_ind CHAR(1) NOT NULL DEFAULT 'n',
	CONSTRAINT xpk_bct PRIMARY KEY (bnft_cyc_typ_cd)
);

COMMENT ON TABLE BENEFIT_CYCLE_TYPE IS 'Periodic or Fixed, Periodic benefits may be paid on a schedule over any period of time, fixed benefits may only be paid in lump sum.  Periodic benefits may not have overlapping payment periods with any periodic benefit type.';
COMMENT ON COLUMN BENEFIT_CYCLE_TYPE.BNFT_CYC_TYP_CD IS 'Unique identifier for the benefit cycle.';
COMMENT ON COLUMN BENEFIT_CYCLE_TYPE.BNFT_CYC_TYP_NM IS 'Description of the benefit cycle.';
COMMENT ON COLUMN BENEFIT_CYCLE_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE BENEFIT_CATEGORY_TYPE (
	BNFT_CTG_TYP_CD VARCHAR(15) NOT NULL,
	BNFT_CTG_TYP_NM VARCHAR(80) NOT NULL,
	VOID_IND CHAR(1) NOT NULL DEFAULT 'n',
	CONSTRAINT XPK_BCTGT PRIMARY KEY (BNFT_CTG_TYP_CD)
);


COMMENT ON TABLE BENEFIT_CATEGORY_TYPE IS ' Represents a grouping of the benefit types into categories based on whether they are death benefits, paid on a temporary basis or permanently,  or are impairment benefits, or are pension benefits, or other.';
COMMENT ON COLUMN BENEFIT_CATEGORY_TYPE.BNFT_CTG_TYP_CD IS 'The code which uniquely identifies a benefit category ';
COMMENT ON COLUMN BENEFIT_CATEGORY_TYPE.BNFT_CTG_TYP_NM IS 'The text description of the category ';
COMMENT ON COLUMN BENEFIT_CATEGORY_TYPE.VOID_IND IS 'Indicates whether the category has been voided; will be a y or n value; default value is n';


CREATE TABLE BENEFIT_TYPE (
	BNFT_TYP_CD VARCHAR(15) NOT NULL,
	BNFT_TYP_NM VARCHAR(80) NOT NULL,
	bnft_cyc_typ_cd VARCHAR(15),
	lob_typ_cd VARCHAR(15),
