	bnft_typ_end_dt TIMESTAMP,
	bnft_typ_srt_ord NUMERIC(5),
	BNFT_TYP_NCCI_CD  	VARCHAR(15),
	BNFT_CTG_TYP_CD	VARCHAR(15),
	void_ind CHAR(1) NOT NULL DEFAULT 'n',
	CONSTRAINT xpk_bnftt PRIMARY KEY (BNFT_TYP_CD)
);


COMMENT ON TABLE BENEFIT_TYPE IS 'Unique Identifier for the benefit type.';
COMMENT ON COLUMN BENEFIT_TYPE.BNFT_TYP_CD IS 'Unique Identifier for the benefit type.';
COMMENT ON COLUMN BENEFIT_TYPE.BNFT_TYP_NM IS 'Description of the benefit type.';
COMMENT ON COLUMN BENEFIT_TYPE.BNFT_CYC_TYP_CD IS 'Periodic or Fixed -> Periodic benefits may be paid on a schedule over any period of time, fixed benefits may only be paid in lump sum.  Periodic benefits may not have overlapping payment periods with any periodic benefit type.';
COMMENT ON COLUMN BENEFIT_TYPE.LOB_TYP_CD IS 'Line of Business the benefit type applies to.';
COMMENT ON COLUMN BENEFIT_TYPE.BNFT_TYP_EFF_DT IS 'Date on which the benefit type is effective.';
COMMENT ON COLUMN BENEFIT_TYPE.BNFT_TYP_END_DT IS 'Date through which the benefit type is effective.';
COMMENT ON COLUMN BENEFIT_TYPE.BNFT_TYP_SRT_ORD IS 'Identifies the order that the values should appear in a drop-down list.';
COMMENT ON COLUMN BENEFIT_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE INDEMNITY_REASON_TYPE (
	indm_rsn_typ_cd VARCHAR(15) NOT NULL,
	indm_rsn_typ_nm VARCHAR(80) NOT NULL,
	void_ind CHAR(1) NOT NULL DEFAULT 'n',
	CONSTRAINT xpk_indmrt PRIMARY KEY (indm_rsn_typ_cd)
);


COMMENT ON TABLE INDEMNITY_REASON_TYPE IS 'This table is used to identify reasons for updating an indemnity payment. The user will use this table in the Indemnity Payments area.';
COMMENT ON COLUMN INDEMNITY_REASON_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


CREATE TABLE INDEMNITY_SCHEDULE (
