COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.CASH_RECT_NONCUST_SRC_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.CASH_RECT_NONCUST_SRC_NM IS 'Name of the source that submitted the unidentified cash receipt.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.CASH_RECT_NONCUST_SRC_ADDR_1 IS 'First address line of the source that submitted the unidentified cash receipt.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.CASH_RECT_NONCUST_SRC_ADDR_2 IS 'Second address line of the source that submitted the unidentified cash receipt.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.CASH_RECT_NONCUST_SRC_CITY IS 'City from the address of the source that submitted the unidentified cash receipt.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.CASH_RECT_NONCUST_SRC_STT IS 'State from the address of the source that submitted the unidentified cash receipt.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.CASH_RECT_NONCUST_SRC_ZIP IS 'Postal code from the address of the source that submitted the unidentified cash receipt.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.CASH_RECT_NONCUST_SRC_PHN IS 'Phone number of the source that submitted the unidentified cash receipt.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.CASH_RECT_NONCUST_PHN_CNTRY_CD IS 'Phone country code of the source that submitted the unidentified cash receipt.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.CASH_RECT_NONCUST_PHN_DIAL_CD IS 'Phone dial code of the source that submitted the unidentified cash receipt..';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CASH_RECEIPT_NONCUSTOMER_SRC.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';


create table CASH_RECEIPT_HEADER
(
   CASH_RECT_HDR_ID                      NUMERIC(31)   not null,
   CRBH_ID                               NUMERIC(31),
   CASH_RECT_HDR_RECV_DT                 TIMESTAMP,
   CASH_RECT_MEDA_TYP_CD                 VARCHAR(15),
   CASH_RECT_HDR_CASH_RECT_NO            VARCHAR(40),
   CASH_RECT_HDR_CASH_RECT_DT            TIMESTAMP,
   CASH_RECT_HDR_CASH_RECT_AMT           NUMERIC(15,2),
   CUST_ID                               NUMERIC(31),
   CASH_RECT_NONCUST_SRC_ID              NUMERIC(31),
   AUDIT_USER_ID_CREA                    NUMERIC(31),
   CASH_RECT_HDR_EXTRNL_ITEM_NO          VARCHAR(80),
   AUDIT_USER_CREA_DTM                   TIMESTAMP     not null,
   AUDIT_USER_ID_UPDT                    NUMERIC(31),
   AUDIT_USER_UPDT_DTM                   TIMESTAMP,
   constraint XPK_CASHRH primary key (CASH_RECT_HDR_ID)
);


COMMENT ON TABLE CASH_RECEIPT_HEADER IS 'Table containing recorded information for each individual cash receipt in the batch.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.CASH_RECT_HDR_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.CRBH_ID IS 'Foreign key reference to identify which cash receipt batch header identifier was associated with this cash receipt header record.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.CASH_RECT_HDR_RECV_DT IS 'Date the cash receipt was received.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.CASH_RECT_MEDA_TYP_CD IS 'Foreign key reference to identify which cash receipt media type was associated with this cash receipt header record.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.CASH_RECT_HDR_CASH_RECT_NO IS 'Number of the check submitted as the cash receipt or a reference number for any other form of cash receipt media.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.CASH_RECT_HDR_CASH_RECT_DT IS 'Date on the check submitted as the cash receipt or a reference date for any other form of cash receipt media.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.CASH_RECT_HDR_CASH_RECT_AMT IS 'Amount of the cash receipt.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.CUST_ID IS 'Foreign key reference to identify which customer was the source of this cash receipt header record.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.CASH_RECT_NONCUST_SRC_ID IS 'Foreign key reference to identify which non source customer was the source of this cash receipt header record.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.CASH_RECT_HDR_EXTRNL_ITEM_NO IS 'Custom column - external item number reference';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CASH_RECEIPT_HEADER.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';


create table CASH_RECEIPT_LINE_ITEM_DETAIL
(
   CRLID_ID                              NUMERIC(31)   not null,
   CASH_RECT_HDR_ID                      NUMERIC(31),
   CRLID_UNID_IND                        CHAR(1)       not null,
   CASH_RECT_CNTX_TYP_CD                 VARCHAR(15),
   CRLID_CNTX_ID                         NUMERIC(31),
   PLCY_PRD_GRP_ID                       NUMERIC(31),
   PLCY_PRD_ID                           NUMERIC(31),
   CRLID_PLCY_PRD_EFF_DT		 TIMESTAMP,
   CRLID_AMT                             NUMERIC(15,2),
   CASH_RECT_TYP_CD                      VARCHAR(15),
   CRLID_RECT_TYP_ID                     VARCHAR(30),
   CRLID_CMT                             VARCHAR(256),
   PAY_TRK_SRC_TYP_CD                    VARCHAR(15),
   CRLID_DRV_PAY_TRK_SRC_TYP_NM          VARCHAR(80), 
   AUDIT_USER_ID_CREA                    NUMERIC(31),
   AUDIT_USER_CREA_DTM                   TIMESTAMP     not null,
   AUDIT_USER_ID_UPDT                    NUMERIC(31),
   AUDIT_USER_UPDT_DTM                   TIMESTAMP,
   constraint XPK_CRLID primary key (CRLID_ID)
);


COMMENT ON TABLE CASH_RECEIPT_LINE_ITEM_DETAIL IS 'Table containing one-to-many line item details that may exist for a single cash receipt within the batch.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.CRLID_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.CASH_RECT_HDR_ID IS 'This is a foreign key to the cash_receipt_header table identifying which specific cash receipt batch that this line item is associated with.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.CRLID_UNID_IND IS 'Indicator identifying that this portion of the cash receipt cannot be associated and applied to a specific claim, policy, or quote at this time';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.CASH_RECT_CNTX_TYP_CD IS 'Code value indicating context to which the cash receipt line item will be applied (i.e. Quote, Policy, Claim.)';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.CRLID_AMT IS 'Amount of the cash receipt that is to be allocated to the line item detail.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.CASH_RECT_TYP_CD IS 'Code value indicating which type of cash receipt is being entered.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.CRLID_RECT_TYP_ID IS 'Identification or number of the <receipt type> submitted with the cash receipt.  For example, if the receipt type is ?Payroll Report?, this number would represent the payroll report identifier for the context number.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.CRLID_CMT IS 'Optional user entered comment if the unidentified indicator is false, and a mandatory entered comment if the unidentified indicator is true.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.CRLID_CNTX_ID IS 'Agreement to which the cash receipt line item will be applied (i.e. Quote Number, Policy Number, Claim Number)';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.PLCY_PRD_GRP_ID IS 'Foreign key to POLICY_PERIOD_GROUP table. Identifies the location group to which the financial line item is associated.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.PLCY_PRD_ID IS 'Foreign key to POLICY_PERIOD table. Identifies the Policy Period to which the financial line item is associated.';
COMMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.PAY_TRK_SRC_TYP_CD IS 'This is a foreign key from the PAYMENT_TRACK_SOURCE_TYPE table';
comment on column CASH_RECEIPT_LINE_ITEM_DETAIL.CRLID_DRV_PAY_TRK_SRC_TYP_NM is 'Derived payment track source type name';
COOMENT ON COLUMN CASH_RECEIPT_LINE_ITEM_DETAIL.CRLID_PLCY_PRD_EFF_DT IS 'Policy period effective date of the policy the cash receipt line item will be applied to';


CREATE TABLE CASH_RECEIPT_UNID_LN_ITEM_DTL
(
   CRULID_ID                             NUMERIC(31)   NOT NULL,
   CRLID_ID                              NUMERIC(31),
   CRULIST_CD                            VARCHAR(15),
   CRULID_RFND_CHCK_NO                   VARCHAR(40),
   CRULID_RFND_CHCK_DT                   TIMESTAMP,
   CRULID_GL_UNID_SENT_DTM               TIMESTAMP,
   CRULID_GL_ID_SENT_DTM           	 TIMESTAMP, 
   AUDIT_USER_ID_CREA                    NUMERIC(31)   NOT NULL,
   AUDIT_USER_CREA_DTM                   TIMESTAMP     NOT NULL,
   AUDIT_USER_ID_UPDT                    NUMERIC(31),
   AUDIT_USER_UPDT_DTM                   TIMESTAMP,
   CONSTRAINT XPK_CRULID PRIMARY KEY (CRULID_ID)
);


COMMENT ON TABLE CASH_RECEIPT_UNID_LN_ITEM_DTL IS 'Table containg records created upon post for each line item within the batch that has the unidentified indicator set to true.';
COMMENT ON COLUMN CASH_RECEIPT_UNID_LN_ITEM_DTL.CRULID_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN CASH_RECEIPT_UNID_LN_ITEM_DTL.CRLID_ID IS 'Line item detail for which this unidentified record was created.';
COMMENT ON COLUMN CASH_RECEIPT_UNID_LN_ITEM_DTL.CRULIST_CD IS 'Code value identifying the current status of the unidentified record.';
COMMENT ON COLUMN CASH_RECEIPT_UNID_LN_ITEM_DTL.CRULID_RFND_CHCK_NO IS 'Number of the manual check issued to refund the unidentified cash receipt if the status equals ?Refunded?.';
COMMENT ON COLUMN CASH_RECEIPT_UNID_LN_ITEM_DTL.CRULID_RFND_CHCK_DT IS 'Date the refund check was issued if the Status equals ?Refunded?.';
COMMENT ON COLUMN CASH_RECEIPT_UNID_LN_ITEM_DTL.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CASH_RECEIPT_UNID_LN_ITEM_DTL.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CASH_RECEIPT_UNID_LN_ITEM_DTL.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CASH_RECEIPT_UNID_LN_ITEM_DTL.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN CASH_RECEIPT_UNID_LN_ITEM_DTL.CRULID_GL_UNID_SENT_DTM IS 'The date that this unidentified cash reciept was sent to the general ledger';


create table POLICY_PERIOD_COST_OF_SERVICE
(
   PLCY_PRD_CST_OF_SERV_ID          NUMERIC(31) not null, 
   PLCY_PRD_ID          	     	NUMERIC(31) not null,		
   CST_OF_SERV_TYP_CD		 		VARCHAR(15), 		
   PLCY_PRD_CST_OF_SERV_FEE	     	NUMERIC(15,2),		
   PLCY_PRD_CST_OF_SERV_DT			TIMESTAMP,		
   PLCY_PRD_CST_OF_SERV_CMT	     	VARCHAR(500), 		
   AUDIT_USER_ID_CREA   	     	NUMERIC(31) not null,
   AUDIT_USER_CREA_DTM  	     	TIMESTAMP 	not null,
   AUDIT_USER_ID_UPDT   	     	NUMERIC(31),		
   AUDIT_USER_UPDT_DTM  	     	TIMESTAMP,
   VOID_IND 			     		CHAR(1)   not null default 'n', 

   constraint  XPK_PPCOS primary key (PLCY_PRD_CST_OF_SERV_ID)
);


COMMENT ON TABLE POLICY_PERIOD_COST_OF_SERVICE IS 'This data table captures information about the cost of service for a particular policy period.';
COMMENT ON COLUMN POLICY_PERIOD_COST_OF_SERVICE.PLCY_PRD_CST_OF_SERV_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN POLICY_PERIOD_COST_OF_SERVICE.PLCY_PRD_ID IS 'This is a foreign key to the policy_period table specifying which policy/period this record is associated with.';
COMMENT ON COLUMN POLICY_PERIOD_COST_OF_SERVICE.CST_OF_SERV_TYP_CD IS 'This is a foreign key to the cost_of_service_type table defining the applicable type of cost of service.';
COMMENT ON COLUMN POLICY_PERIOD_COST_OF_SERVICE.PLCY_PRD_CST_OF_SERV_FEE IS 'This is the fee amount associated with this particular cost of service for this policy period.';
COMMENT ON COLUMN POLICY_PERIOD_COST_OF_SERVICE.PLCY_PRD_CST_OF_SERV_DT IS 'This is the date that the cost of service occurred.';
COMMENT ON COLUMN POLICY_PERIOD_COST_OF_SERVICE.PLCY_PRD_CST_OF_SERV_CMT IS 'This is a user entered comment about the cost of service.';
COMMENT ON COLUMN POLICY_PERIOD_COST_OF_SERVICE.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN POLICY_PERIOD_COST_OF_SERVICE.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PERIOD_COST_OF_SERVICE.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN POLICY_PERIOD_COST_OF_SERVICE.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN POLICY_PERIOD_COST_OF_SERVICE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table COST_OF_SERVICE_TYPE
(
   CST_OF_SERV_TYP_CD           VARCHAR(15)            not null, 	
   CST_OF_SERV_TYP_NM           VARCHAR(80)           not null, 	
   VOID_IND     CHAR(1)                not null default 'n', 		
   constraint XPK_coST primary key (CST_OF_SERV_TYP_CD)
);


COMMENT ON TABLE COST_OF_SERVICE_TYPE IS 'This code table defines the applicable types of service costs which are tracked at a policy period level.';
COMMENT ON COLUMN COST_OF_SERVICE_TYPE.CST_OF_SERV_TYP_CD IS 'A unique code used to identify the specific service cost.';
COMMENT ON COLUMN COST_OF_SERVICE_TYPE.CST_OF_SERV_TYP_NM IS 'A user friendly name ot describe the service cost.';
COMMENT ON COLUMN COST_OF_SERVICE_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table CANCELLATION_METHOD_TYPE
(
   CANC_METH_TYP_CD			VARCHAR(15) not null,
   CANC_METH_TYP_NM			VARCHAR(80) not null,   
   VOID_IND				CHAR(1) not null default 'n',
   constraint XPK_CMT primary key (CANC_METH_TYP_CD)
);


COMMENT ON TABLE CANCELLATION_METHOD_TYPE IS 'This is a code table which defines the types of cancellation methods for policies in the system.';
COMMENT ON COLUMN CANCELLATION_METHOD_TYPE.CANC_METH_TYP_CD IS 'A unique code value to identify the specific type of cancellation method.';
COMMENT ON COLUMN CANCELLATION_METHOD_TYPE.CANC_METH_TYP_NM IS 'A user friendly name to describe the type of cancellation method.';
COMMENT ON COLUMN CANCELLATION_METHOD_TYPE.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';


create table CANCELLATION_METH_STS_RSN_XREF
(
   CMSRX_ID				NUMERIC(31) not null,
   CANC_METH_TYP_CD			VARCHAR(15) not null,
   PLCY_STS_TYP_CD      		VARCHAR(15) not null,
   PLCY_STS_RSN_TYP_CD  		VARCHAR(15) not null, 
   CTL_ELEM_PLCY_TYP_CD  		VARCHAR(15) not null,
   CMSRX_NON_PAY_DFLT_IND		CHAR(1) ,
   CMSRX_NON_RPT_DFLT_IND		CHAR(1) ,
   CMSRX_NON_PAY_PYRL_IND		CHAR(1) ,
   CMSRX_HI_LOW_SHR_TRM_PENL_IND CHAR(1) ,
   PRIORITY_ORD				NUMERIC(5),
   CMSRX_UNPRD_AUDT_DFLT_IND	CHAR(1) ,
   VOID_IND				CHAR(1) not null default 'n',
   constraint XPK_CMSRX primary key (CMSRX_ID)
);


COMMENT ON TABLE CANCELLATION_METH_STS_RSN_XREF IS 'This is a code table which maps cancellation methods to certain status reasons and what the defualt is for each mapping.';
COMMENT ON COLUMN CANCELLATION_METH_STS_RSN_XREF.CMSRX_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN CANCELLATION_METH_STS_RSN_XREF.CANC_METH_TYP_CD IS 'This is a foreign key to the cancellation_method_type table specifying the cancellation method for this mapping.';
COMMENT ON COLUMN CANCELLATION_METH_STS_RSN_XREF.PLCY_STS_TYP_CD IS 'This is a foreign key to the policy_status_type table specifying the status type for this mapping.';
COMMENT ON COLUMN CANCELLATION_METH_STS_RSN_XREF.PLCY_STS_RSN_TYP_CD IS 'This is a foreign key to the policy_status_reason_type table specifying the reason for this mapping.';
COMMENT ON COLUMN CANCELLATION_METH_STS_RSN_XREF.CTL_ELEM_PLCY_TYP_CD IS 'This is the value of Policy Type control element and is a foreign key to control_element_sub_type table'; 
COMMENT ON COLUMN CANCELLATION_METH_STS_RSN_XREF.CMSRX_NON_PAY_DFLT_IND IS 'An indicator specifying if non payment should be the defualt reason for this mapping.';
COMMENT ON COLUMN CANCELLATION_METH_STS_RSN_XREF.CMSRX_NON_RPT_DFLT_IND IS 'An indicator specifying if non reporting should be the default reason for this mapping.';
COMMENT ON COLUMN CANCELLATION_METH_STS_RSN_XREF.CMSRX_NON_PAY_PYRL_IND IS 'An indicator specifying if non payment of payroll should be the default reason for this mapping.';
COMMENT ON COLUMN CANCELLATION_METH_STS_RSN_XREF.CMSRX_HI_LOW_SHR_TRM_PENL_IND IS 'Determines if a short term penalty will be applied on policies cancelled based on the Cancellation Reason.';
COMMENT ON COLUMN CANCELLATION_METH_STS_RSN_XREF.PRIORITY_ORD IS 'Determines the hierarchy used to identify the primary non-payment cancellation reason when the process determines there is more than one receivable type past due.';
COMMENT ON COLUMN CANCELLATION_METH_STS_RSN_XREF.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
Comment on column CANCELLATION_METH_STS_RSN_XREF.CMSRX_UNPRD_AUDT_DFLT_IND is 'To indicate the cancellation reason the Unproductive Audit batch job will set when cancelling a policy due to an unproductive audit ';

CREATE TABLE CUSTOMER_ADDRESS_HISTORY (
      HIST_ID 				NUMERIC(31) NOT NULL,
      CUST_ADDR_ID 			NUMERIC(31),
      CUST_ID 				NUMERIC(31),
      CUST_ADDR_TYP_CD 		VARCHAR(15),
      STT_ID				NUMERIC(31),
      CNTRY_ID				NUMERIC(31),     
      CUST_ADDR_EFF_DT 			TIMESTAMP,
      CUST_ADDR_END_DT 			TIMESTAMP,
      CUST_ADDR_STR_1 			VARCHAR(40),
      CUST_ADDR_STR_2 			VARCHAR(40),
      CUST_ADDR_COMT 			VARCHAR(256),
      CUST_ADDR_LNK_IND 		CHAR(1),
      CUST_ADDR_POST_CD			VARCHAR(15),
      CUST_ADDR_CITY_NM			VARCHAR(80),
      CUST_ADDR_CITY_UPCS_SRCH_NM	VARCHAR(80),
      CUST_ADDR_CNTY_NM			VARCHAR(80),
      CUST_ADDR_CNTY_UPCS_SRCH_NM	VARCHAR(80),
      CUST_ADDR_POST_BRCD		VARCHAR(16),
      CUST_ADDR_LAT        NUMERIC(31, 10),
      CUST_ADDR_LONG       NUMERIC(31, 10), 
	  CUST_ADDR_EXTRNL_ADDR_NO     VARCHAR(80),
	  CUST_ADDR_UPDT_SRC VARCHAR(20),  
	  CUST_ADDR_OVRRD_IND   CHAR(1)  not null default 'n',
      CUST_ADDR_VLDT_IND   CHAR(1)   not null default 'n',
      CUST_ADDR_VLDT_PRFM_IND   CHAR(1)   not null default 'n',  
	  CUST_ADDR_SRCH_NRMLIZED_STR VARCHAR(80),
	  CUST_LNK_ADDR_ID	NUMERIC(31), 
      AUDIT_USER_ID_CREA 		NUMERIC(31) ,
      AUDIT_USER_CREA_DTM 		TIMESTAMP,
      AUDIT_USER_ID_UPDT 		NUMERIC(31) ,
      AUDIT_USER_UPDT_DTM 		TIMESTAMP,
      HIST_EFF_DTM 			TIMESTAMP NOT NULL,
      HIST_END_DTM 			TIMESTAMP,
	  VOID_IND 				CHAR(1) not null default 'n',
	  CONSTRAINT XPK_CAH PRIMARY KEY (HIST_ID)
);


COMMENT ON TABLE CUSTOMER_ADDRESS_HISTORY IS 'This is a history table which captures historical information about customer addresses.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.HIST_ID IS 'This is the primary surrogate key for the table used to uniquely identify each record.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_ID IS 'This is a foreign key to the customer address table defining the specific customer address record that this history record pertains to.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ID IS 'This is a foreign key to the customer table specifying which customer record this customer address record pertains to.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_TYP_CD IS 'This is a foreign key to the customer_address_type table specifying the address type of the record.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.STT_ID IS 'The state ID of the address.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CNTRY_ID IS 'The country ID of the address.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_EFF_DT IS 'The date that this address record became effective.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_END_DT IS 'The date that this address record is no longer in effect.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_STR_1 IS 'First street address line of the address.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_STR_2 IS 'Second street address line of the address.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_COMT IS 'Comment text field.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_LNK_IND IS 'Indicator that specifies whether the address is linked to a risk location.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_POST_CD IS 'The postal code of the address.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_CITY_NM IS 'The city name of the address.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_CITY_UPCS_SRCH_NM IS 'The uppercase of the customer address city name for search purposes.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_CNTY_NM IS 'The county name of the address.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_CNTY_UPCS_SRCH_NM IS 'The uppercase of the customer address count name - for search purposes.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_POST_BRCD IS 'Validated barcode of the address.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_LAT IS 'Represents the updation of the address latitude.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_LONG IS 'Represents the updation of the address longitude.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_EXTRNL_ADDR_NO is 'Used to link PS address to an address in an external system.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_UPDT_SRC is 'Represents the updation source of the address.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_OVRRD_IND IS 'Represents the updation of customer address override indicator.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_VLDT_IND IS 'Represents the updation of customer address validation indicator.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_VLDT_PRFM_IND IS 'Represents the updation of customer address validation perfomance indicator.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_ADDR_SRCH_NRMLIZED_STR IS 'Represents the updation of customer search nomalized address.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.CUST_LNK_ADDR_ID IS 'The CUST_ADDR_ID of the mail address of the customer - where it is the same as the risk location.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CUSTOMER_ADDRESS_HISTORY.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
