COMMENT ON COLUMN CLAIM_ACCOUNTING_TRANSACTION.ACCT_TRAN_TYP_CD IS 'Select the type from the accounting transaction type table';

CREATE TABLE CLAIM_ALLOCATION_TYPE (
    CLM_ALLOC_TYP_CD            VARCHAR(15) NOT NULL,
