COMMENT ON TABLE CLAIM_ALLOCATION_TYPE IS 'The type of Claim Cost Allocation/Recovery to perform';
comment on column CLAIM_ALLOCATION_TYPE.CLM_ALLOC_TYP_PCT_APP is 'to indicate whether the Percentage is applicable for this type';
comment on column CLAIM_ALLOCATION_TYPE.CLM_ALLOC_TYP_NM is 'to store the allocation type name';

comment on column CLAIM_ALLOCATION_TYPE.CLM_ALLOC_TYP_CD is 'to store the allocation type code';
comment on column CLAIM_ALLOCATION_TYPE.CLM_ALLOC_TYP_MAX_AMT_APP is 'to indicate whether the Maximum amount is applicable for this type';


CREATE TABLE CLAIM_ALLOCATION (
