    AUDIT_USER_ID_UPDT          NUMERIC(31),
    AUDIT_USER_UPDT_DTM         TIMESTAMP,
    VOID_IND                    CHAR(1)          NOT NULL DEFAULT 'n',
    CONSTRAINT XPK_CALLOC PRIMARY KEY (CLM_ALLOC_ID));


COMMENT ON TABLE CLAIM_ALLOCATION IS 'The type of Claim Cost Allocation/Recovery to perform for a given customer';
comment on column CLAIM_ALLOCATION.CUST_ID is 'Customer to calculate the cost allocation for';
comment on column CLAIM_ALLOCATION.CLM_ALLOC_EFF_DT is 'When an allocation is effective for a given customer';
comment on column CLAIM_ALLOCATION.CLM_ALLOC_END_DT is 'When an allocation ends for a given customer';
comment on column CLAIM_ALLOCATION.CLM_ALLOC_COMT is 'Any comment related to this allocation';
COMMENT ON COLUMN CLAIM_ALLOCATION.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN CLAIM_ALLOCATION.CLM_ALLOC_TYP_CD IS 'Select  the proper type code from the claim allocation type table';
COMMENT ON COLUMN CLAIM_ALLOCATION.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';
COMMENT ON COLUMN CLAIM_ALLOCATION.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN CLAIM_ALLOCATION.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CLAIM_ALLOCATION.CLM_ALLOC_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN CLAIM_ALLOCATION.AGRE_ID IS 'The agreement ID of the claim  to which this claim allocation belongs';
COMMENT ON COLUMN CLAIM_ALLOCATION.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';


CREATE TABLE CLAIM_ALLOCATION_DETAIL (
    CLM_ALLOC_DTL_ID            NUMERIC(31) NOT NULL,
    CLM_ALLOC_ID                NUMERIC(31) NOT NULL,
    PAY_TYP_CD                  VARCHAR(15) ,
    BNFT_TYP_CD                 VARCHAR(15),
    CST_CNTR_TYP_CD             VARCHAR(15),
    CLM_ALLOC_DTL_EFF_DT        TIMESTAMP NOT NULL,
    CLM_ALLOC_DTL_END_DT        TIMESTAMP,
    CLM_ALLOC_DTL_PCT           NUMERIC(5,2) NOT NULL,
    CLM_ALLOC_DTL_MAX_AMT       NUMERIC(15,2),
    CLM_ALLOC_DTL_INDM_IND	CHAR(1)	NOT NULL DEFAULT 'n',
    CLM_ALLOC_DTL_MED_IND	CHAR(1)	NOT NULL DEFAULT 'n',
    CLM_ALLOC_DTL_EXP_IND	CHAR(1)	NOT NULL DEFAULT 'n',
    CLM_ALLOC_DTL_PD_AMT	NUMERIC(15,2) NOT NULL,
    AUDIT_USER_ID_CREA          NUMERIC(31) NOT NULL,
    AUDIT_USER_CREA_DTM         TIMESTAMP NOT NULL,
    AUDIT_USER_ID_UPDT          NUMERIC(31),
    AUDIT_USER_UPDT_DTM         TIMESTAMP,
    VOID_IND                    CHAR(1)          NOT NULL DEFAULT 'n',
    CONSTRAINT XPK_CADTL PRIMARY KEY (CLM_ALLOC_DTL_ID));


COMMENT ON TABLE CLAIM_ALLOCATION_DETAIL IS 'Stores the details as to what level and type of financial transactions a customer is responsible for';
comment on column CLAIM_ALLOCATION_DETAIL.PAY_TYP_CD is 'The payment type code (from CFT) the customer is responsible for';
comment on column CLAIM_ALLOCATION_DETAIL.BNFT_TYP_CD is 'The benefit type code (from CFT) the customer is responsible for';
comment on column CLAIM_ALLOCATION_DETAIL.CST_CNTR_TYP_CD is 'The cost center type code (from CFT) the customer is responsible for';
comment on column CLAIM_ALLOCATION_DETAIL.CLM_ALLOC_DTL_EFF_DT is 'The effective date for this individual/payment type, etc allocation';
comment on column CLAIM_ALLOCATION_DETAIL.CLM_ALLOC_DTL_END_DT is 'The end date for this individual/payment type, etc allocation';
comment on column CLAIM_ALLOCATION_DETAIL.CLM_ALLOC_DTL_PCT is 'The percentage the customer is responsible for this payment type, etc ';
comment on column CLAIM_ALLOCATION_DETAIL.CLM_ALLOC_DTL_INDM_IND is 'Is this allocation (Suspended Benefit) applicable for Payment Category = Indemnity';
comment on column CLAIM_ALLOCATION_DETAIL.CLM_ALLOC_DTL_MED_IND is 'Is this allocation (Suspended Benefit) applicable for Payment Category = Medical';
comment on column CLAIM_ALLOCATION_DETAIL.CLM_ALLOC_DTL_EXP_IND is 'Is this allocation (Suspended Benefit) applicable for Payment Category = Expense';
comment on column CLAIM_ALLOCATION_DETAIL.CLM_ALLOC_DTL_PD_AMT is 'The amount that has been paid or allocated';
COMMENT ON COLUMN CLAIM_ALLOCATION_DETAIL.VOID_IND IS 'An indicator identifying whether the record has been logically deleted or not.';
COMMENT ON COLUMN CLAIM_ALLOCATION_DETAIL.AUDIT_USER_ID_UPDT IS 'The user identifier of the user that updated this record.';
COMMENT ON COLUMN CLAIM_ALLOCATION_DETAIL.CLM_ALLOC_ID IS 'Foreign key to the parent claim_allocation table';
COMMENT ON COLUMN CLAIM_ALLOCATION_DETAIL.AUDIT_USER_UPDT_DTM IS 'Date and time record was updated.  Used for audit purposes.';
COMMENT ON COLUMN CLAIM_ALLOCATION_DETAIL.CLM_ALLOC_DTL_ID IS 'Surrogate primary key identifier for this table.';
COMMENT ON COLUMN CLAIM_ALLOCATION_DETAIL.CLM_ALLOC_DTL_MAX_AMT IS 'The maximum amount of claim allocation';
COMMENT ON COLUMN CLAIM_ALLOCATION_DETAIL.AUDIT_USER_CREA_DTM IS 'Date and time record was created.  Used for audit purposes.';
COMMENT ON COLUMN CLAIM_ALLOCATION_DETAIL.AUDIT_USER_ID_CREA IS 'The user identifier of the user that created this record.';

