COMMENT ON COLUMN CUSTOMER_PROFILE.CUST_PRFL_ANSW_TXT IS 'This field contains the answer provided by user for each profile';

create table JURISDICTION_METHOD_CNV_CALC
(
	JUR_METH_CNV_CALC_ID   	   NUMERIC(31) not null,
	JUR_TYP_CD                 VARCHAR(15),
	JUR_METH_SRC_METH          VARCHAR(20),
	JUR_METH_TRGT_METH         VARCHAR(20),
	JUR_METH_CNV_CALC          VARCHAR(200),
	JUR_METH_CNV_CALC_EFF_DT   TIMESTAMP not null,
	JUR_METH_CNV_CALC_END_DT   TIMESTAMP,
  	CONSTRAINT JMCC_PK PRIMARY KEY(JUR_METH_CNV_CALC_ID)
);

COMMENT ON TABLE JURISDICTION_METHOD_CNV_CALC IS 'This table is used to identify a Jurisdiction''s calculations to convert an amount that represents one time period (method) into an amount that represents a different time period than the source.';
COMMENT ON COLUMN JURISDICTION_METHOD_CNV_CALC.JUR_METH_CNV_CALC_ID IS 'Primary Key for the Table';
COMMENT ON COLUMN JURISDICTION_METHOD_CNV_CALC.JUR_TYP_CD IS 'Jurisdiction to which this calculation applies. The calculation for "All" is used unless there is a specific calculation for the Jurisdiction.';
COMMENT ON COLUMN JURISDICTION_METHOD_CNV_CALC.JUR_METH_SRC_METH IS 'Identifies the time period (method) of the SOURCE amount that will be converted.';
COMMENT ON COLUMN JURISDICTION_METHOD_CNV_CALC.JUR_METH_TRGT_METH IS 'Identifies the time period (method) of the TARGET amount that the source amount will be converted to.';
COMMENT ON COLUMN JURISDICTION_METHOD_CNV_CALC.JUR_METH_CNV_CALC IS 'Identifies the specific calculation to be used.';
COMMENT ON COLUMN JURISDICTION_METHOD_CNV_CALC.JUR_METH_CNV_CALC_EFF_DT IS 'Date on which the calculation became effective.';
COMMENT ON COLUMN JURISDICTION_METHOD_CNV_CALC.JUR_METH_CNV_CALC_END_DT IS 'Date on which the calculation ceased to be effective.';

